#define rand(p,i) fract(  sin( dot( ceil(p), vec2(12.9,78.2) ) ) * 4e5  \
                         + .2 + 1.4 * (i + floor(iTime/2.) )            \
                       )             
float H(vec2 U) {                           // choose truchet LOD
    float i=0.;
    for( ; i < 5. ; i++, U*=2. )
        if( U.y > 1. && ( abs(U.y) > 2. || rand(U,i) < .5 ) ) break;

    return i;
}

void mainImage( out vec4 O, vec2 co )
{
    vec2 R = iResolution.xy, F,
         U = co / R.y - vec2( .5*R.x/R.y, - .075);

    float z =  1.8 * exp2(-fract(iTime/2.)),// zooming coordinates
          h = H(U*=z), s = exp2(h), u,v,d;  // truchet LOD h and scale s

    F = fract(U*s);                         // local coords in truchet tile    
    v = rand( U*s, h ) -.5;                 // 2 rands per tile: sign,abs

    if( v < 0.) F = vec2( 1.-F.y, F.x );    // < 0 : rotate 90Â°
    v = abs(v);                             // [0,.5]
    d =  v > .4                             // 20% luck:
           ? min(  abs( length(F)    - .5 ),// round parts at one of the 2 ends
                   abs( length(F-1.) - .5 )
                ) -1./6. 
       : v > .1                             // 60% luck:
           ? min(       abs(F.y-.5)  -1./6.,// segment parts
                  max(  abs(F.x-.5) - 1./6.,// crossing ?
                      -(abs(F.y-.5) - 1./4.) 
                )    )
       :     min( abs( F.y = abs(F.y-.5) ), // 20% luck:
                  length( F-.5 )            // semi-round parts ?
                ) - 1./6.;
   	d /= s;        

    for(float c,i=h; i < 6.; i++, s*=2., d=-d) {  // draw all LOD >= h
        F = round(U*s)/s;                   // local tile id
        vec2 e = vec2(-1,1)/64.;            // ( any small value ? )
        c = max( max( H( F + e.xx),         // seek deepest LOD "around"
                      H( F + e.yx)),
                 max( H( F + e.xy),
                      H( F + e.yy)) );
        if( i < c )                         // if less deep,
            u = length(U - F) - 1./6./s,    // draw disk (?) 
            d = min( d, u );                // ( not sure why we continue )
    }
    int(iTime/2.) % 2 >0 ? d = -d : d;      // for cycling continuity, alternate BW
    O = vec4( .5+.5* d*R.y/z );             // paint with AA
}

// noise background is baced on æææ°VIP:
// https://www.shadertoy.com/view/4sVBDm
const float noiseIntensity = 2.8;
const float noiseDefinition = 0.6;
const vec2 glowPos = vec2(-2., 0.);

float random(vec2 co){
    return fract(sin(dot(co.xy ,vec2(-0.950,-0.910))) * 43758.5453);
}

float noise( in vec2 p ){
    p*=noiseIntensity;
    vec2 i = floor( p );
    vec2 f = fract( p );
	vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( random( i + vec2(0.0,0.0) ), 
                     random( i + vec2(1.0,0.0) ), u.x),
                mix( random( i + vec2(0.0,1.0) ), 
                     random( i + vec2(1.0,1.0) ), u.x), u.y);
}

float fbm( in vec2 uv ){	
	uv *= 5.0;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    float f  = 0.5*noise( uv ); uv = m*uv;
    f += 5.*noise( uv ); uv = m*uv;
    f += 5.*noise( uv ); uv = m*uv;    
	f = 0.1 + .5*f;
    return f;
}

vec3 bg(vec2 uv ){
    float velocity = iTime * 0.5;
    float intensity = sin(uv.x*noise(uv)*7.+velocity*3.)*noise(uv*10.)*1.0+.3;
    uv.y -= 2.;
    vec2 bp = uv+glowPos;
    uv *= noiseDefinition;

    //ripple
    float rb = fbm(vec2(uv.x*.5-velocity*.03, uv.y))*.1;
    uv += rb;

    //coloring
    float rz = fbm(uv*.9+vec2(velocity*.35, 0.0));
    rz *= dot(bp*intensity,bp);

    //bazooca line
    rz *= sin(uv.x*0.1+velocity*0.8);

    //lightning
    rz *= 5.*sin(uv.x*0.1+velocity*sin(iTime*100.));

    vec3 bgColor = vec3(cos(iTime),  sin(iTime*0.2), sin(iTime*0.3));
    vec3 col = bgColor/(.1-rz);
    return sqrt(abs(col));
}

// 2D rotate
mat2 rotate(in float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, s, -s, c);
}

