// Hash without Sine 2 - https://www.shadertoy.com/view/XdGfRR
#define UI0 1597334673U
#define UI1 3812015801U
#define UI2 uvec2(UI0, UI1)
#define UI3 uvec3(UI0, UI1, 2798796415U)
#define UIF (1.0 / float(0xffffffffU))

vec2 hash22(vec2 p)
{
	uvec2 q = uvec2(ivec2(p))*UI2;
	q = (q.x ^ q.y) * UI2;
	return vec2(q) * UIF;
}

vec3 hash33(vec3 p)
{
	uvec3 q = uvec3(ivec3(p)) * UI3;
	q = (q.x ^ q.y ^ q.z)*UI3;
	return vec3(q) * UIF;
}

// -	-	-	-	-	-	-	-

float calcVoro31(vec3 p)
{
    vec3 cellPos = fract(p);
    vec3 cellId = floor(p);

    float len = 2.0;

    for (int z = -1; z < 2; z++)
    for (int y = -1; y < 2; y++)
    for (int x = -1; x < 2; x++)
    {
        vec3 offs = vec3(x, y, z);
        len = min(len, length(cellPos + offs - hash33(cellId - offs)));
    }

    return len;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

    float timeScale = 20.0f;

    vec3 p = vec3(uv, iTime / timeScale);

    float res;
    float voro = calcVoro31(p * 3.0);

    res += voro;

    //res = cla(res, 0.3);
    res *= 2.0;

    res = abs(res - 0.4);   

    res = smoothstep(0.02, 0.025, res);

    col.rgb = mix(vec3(0.9, 0.9, 0.9), vec3(0.2, 0.4, 0.6),res);

    // Output to screen
    fragColor = vec4(col,1.0);
}

