#define PI acos(-1.)
#define TAU 6.2831853071
#define time(offset) fract((iTime+offset)*0.1)
#define AAstep(thre, val) smoothstep(-.7,.7,(val-thre)/min(.05,fwidth(val-thre)))
#define circle(s,pix) AAstep(s,length(pix))
#define square(s,pix) AAstep(s,max(abs(pix.x),abs(pix.y)))


// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float xor (float a, float b)
{return (1.-b)*a + (1.-a)*b;}

vec3 palette (float t, vec3 c)
{return vec3(.5)+vec3(.5)*cos(TAU*(c*t+vec3(.0,.37,.63)));}

vec3 background (vec2 uv)
{
    uv *= rot(sin(length(uv*1.5)+time(0.)*TAU));
    uv += texNoise(uv+smoothstep(0.1,0.25,abs(sin(length(uv)-iTime)-1.))).r*0.5-0.1;
    float mask = circle(sqrt(sin((time(5.))*TAU)*2.),length(uv));
    float spread = atan(uv.y,uv.x);
    return palette(spread, vec3(TAU/8.))*(1.-mask);
}

vec3 dancing_shapes (vec2 uv)
{
    float size = 0.1;
    float spread = pow(clamp(sin(time(0.)*TAU)*0.2,0.,1.),0.35);
    vec2 puv = uv;
    uv *= rot(time(0.)*TAU*1.2);

    vec2 uc = vec2(uv.x - spread,uv.y);
    vec2 us = vec2(uv.x + spread, uv.y);
    us *= rot(time(0.)*PI);

    float cir = circle(size,uc);
    float squ = square(size,us);
    float mask = xor(cir,1.-squ);

    vec3 color = (spread<=0.0001) ? background(puv)*squ+(1.-mask) : vec3(mask); 
    return color;
}


void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);
    vec3 col = vec3(dancing_shapes(uv));
    fragColor = vec4(sqrt(col),1.);
}

#define time iTime

// #define SPEED 15.
#define SPEED 15.
#define FLOOR 0
#define SPH 1
#define CAP 2
#define EXTR 3
#define INF 4

int hit = FLOOR;

mat2 rot2d(float a) {
    float c = cos(a), s = sin(a);

    return mat2(c, s, -s, c);
}

vec3 kifs(vec3 p) {
    float r = .7;
    float s = 2.;

    for (float i = 0.; i < 4.; i++) {
        p.xy *= rot2d(time * .7);
        p.yz *= rot2d(time * .5 - i);
        p = abs(p);
        p -= s;
        s *= r;
    }

    return p;
}

float sph(vec3 p, float r) {
    return length(p) - r;
}

float tube(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b - a;
	vec3 ap = p - a;
	float t = dot(ab, ap) / dot(ab, ab);
	vec3 c = a + ab * t;

	return length(p - c) - r;
}

float cap(vec3 p, vec3 a, vec3 b, float r) {
	vec3 ab = b - a;
	vec3 ap = p - a;
	float t = dot(ab, ap) / dot(ab, ab);
	t = clamp(t, 0., 1.);
	vec3 c = a + ab * t;

	return length(p - c) - r;
}


vec3 rep(vec3 p, vec3 r) {
    return mod(p, r) - .5 * r;
}

float map(vec3 p) {
    p.xy *= rot2d(p.z / 70.);
//    p = rep(p, vec3(0., 0, 100.));
    p = rep(p, vec3(0., 0, 40.));
    vec3 pkifs = kifs(p);
    float d = 5000.;

    hit = FLOOR;
  	float dsph = sph(pkifs - vec3(2, 0, 0), 1.);
    d = min(d, dsph);
    hit = (d == dsph) ? SPH : hit;
    float dtube = tube(pkifs, vec3(0, 1, 0), vec3(2, 1, 0), .5);
    d = min(d, dtube);
    hit = (d == dtube) ? CAP : hit;
    float dextr = -tube(p, vec3(0, 0, -1), vec3(0, 0, 0), 2.);
    d = max(d, dextr);
    hit = (d == dextr) ? EXTR : hit;

    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    vec3 col = vec3(0);

    vec3 ro = vec3(0, 0, -30. + time * SPEED);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    vec3 p = ro;
    float d = 0.;
    for (int i = 0; i < 100; i++) {
		float ds = map(p);

        if (ds < .01 || ds > 100.) {
            break;
        }
        if (ds > 100.) {
            hit = INF;
        }
        p += .7 * ds * rd;
    }

    col = vec3(length(p - ro) * .1);
    vec2 e = vec2(0.01, 0);

    vec3 n = normalize(map(p) - vec3(
        map(p - e.xyy),
        map(p - e.yxy),
        map(p - e.yyx)
	));

    vec3 lp = vec3(0, 0, -30. + time * SPEED);
    float dif = dot(n, normalize(lp - p));

//    map(p);
    col = vec3(0, 0, 1);
    if (hit == SPH) {
        col = vec3(0.306, 0.21 + .3 * (.5 * sin(3. * time) + 0.5), .43);
    }
    if (hit == CAP) {
//        col = vec3(201. /255., 122. / 255., 30. / 255.);
        col = vec3(1);
    }
    if (hit == EXTR) {
//        255, 182, 71
		col = vec3(255. / 255., 182. / 255., 71. / 255.);
//        col = vec3(1., 0., 0.);
    }
    if (hit == INF) {
        col = vec3(1);
    }
    col *= dif;

    fragColor = vec4(col,1.0);
}

#define MAXSTEPS 100
#define MAXDIST 100.
#define SURFDIST .01

float GetDist(vec3 p){

	vec4 s1 = vec4(0, 4, 19, 4);
    vec4 s2 = vec4(10, 4.2, 10, 4.2);
    vec4 s3 = vec4(-8, 5, 6, 5);

    float sphereDist1 = length(p - s1.xyz) - s1.w;
    float sphereDist2 = length(p - s2.xyz) - s2.w;
    float sphereDist3 = length(p - s3.xyz) - s3.w;
    float planeDist = p.y;

    float d = min(min(min(sphereDist1, planeDist), sphereDist2), sphereDist3);
    return d;

}


float RayMarch(vec3 ro, vec3 rd){

	float dO = 0.;

        for(int i = 0; i < MAXSTEPS; i++){

        	vec3 p = ro + rd * dO;
            float dS = GetDist(p);
            dO += dS;
            if(dO > MAXDIST || dS < SURFDIST){



            }

        }

	return dO;

}


vec3 GetNormal(vec3 p){

	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
    	GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);

}

float GetLight(vec3 p){

	vec3 lightPos = vec3(0, 1, 6);

    lightPos.xz += vec2(sin(iTime), cos(iTime)) * 2.;

    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);

    float d = RayMarch(p+n*SURFDIST * 2., l);
    if(d<length(lightPos - p )) dif *= .2;

    return dif;

}


