                                                 -s*A.y, s*A.x,  0   );
}                                           // GLSL bug: can't factor s !

float distrib( vec3 p, inout vec3 C, inout vec3 id) { // return dist; C=pos, id=detail 3D id
    float d = 1e15;
    vec3 ip = floor(p);
    for (int k=0; k<27; k++) {                    // --- visit 3x3x3 neighborhood
                vec3  p0 = ip + vec3(k%3,k/3%3,k/9) - 1., // cell id
                       c = hash33(p0)+p0-p;       // vector-dist to cell sample
                float d0 = dot(c,c);              // distÂ²
                if  (d0<d) d=d0, C=c+p, id = p0;  // keep closest sample info
            }
    return sqrt(d);
}

