vec3 calcNormal(vec3 p) { // taken here https://www.shadertoy.com/view/3sBBWy
  vec3 n = vec3(0), id;
  for(int i = 0; i < 4; i++){
      vec3 e = .001*( vec3(9>>i&1, i>>1&1, i&1) *2.-1. );
      n += e * dist2scene(p+e, id);
  }
  return normalize(n);
}

#define NORM 2    // 0 : full DF normal 1: centered directional 2: even cheaper
float shadedNormal( vec3 p, float v ) {
    float epsL = .005, dx; vec3 id;               // use smaller eps for contrast
#if NORM == 0
    dx =  dot(calcNormal(p),lightDir);            // true Lambert with full DF normal
#elif NORM == 1                                   // centered directional derivative
// Cheap computation of normals+Lambert using directional derivative (see https://www.shadertoy.com/view/Xl23Wy )
// still, we need an estimate of slope amplitude to avoid artifacts (see grad+scale).
    dx = ( dist2scene(p+epsL*lightDir, id)
          -dist2scene(p-epsL*lightDir, id)) / (2.*epsL);
#else                                             // cheap directional derivative
    dx = (dist2scene(p+epsL*lightDir, id)-v)/epsL;
#endif
    return clamp(dx, 0.,1.);                      // approx Lambert shading    
}

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         q = ( 2.*u - R ) / R.y,
         M = iMouse.xy / R;
    if ( M==vec2(0)) M=vec2(.5);
    geoMode = !keyToggle(32);

    // --- set camera
    float theta = -( M.x - .5 ) *6.28,
            phi = -( M.y - .5 ) *3.14;
#if 1 // camera shake 
    float t=10.*iTime,B=.02;   theta += B*cos(t); phi += B*sin(t);
#endif
    vec3 cameraPos = vec3(sin(theta)*cos(phi),sin(phi),cos(theta)*cos(phi)),   
      cameraTarget = vec3(0),
         W = normalize( cameraPos - cameraTarget ),
         U = normalize(cross( vec3(0,1,0), W )),
         V = cross(W,U),
    rayDir = normalize( q.x*U + q.y*V -1.5*W );

    // --- ray-trace volume
    float l = .5, d,
        transp = 1.;
    vec3 col = vec3(0),
           p = cameraPos+l*rayDir, p_=p, 
          id = vec3(-2);                      // default = sky

    float i=0.;
    for (; i<200.; i++) {                     // ray-march
        d = dist2scene(p, id); 
        if (id==vec3(-2)) break;              // early exit if sky
        if (d < .003)     break;              // "hit" on an object
        p += .3*d*rayDir;
    }
 // O = i/vec4(200,50,12,1); return;          // test: hitmap of iterations

    // --- shading
    if ( d < .01 && id != vec3(-2) ) {        // hit
#if FOG 
	    float fog = pow(skyTrsp,length(p_-p)); p_=p;
        col += transp*skyColor*(1.-fog);
    	transp *= fog; 
#endif            
        vec3 c = id != vec3(-1) 
                  ? .2*hue( hash33(id).x )    // detail color
                  : ambient;                  // other surface: ambiant
#if SHADED          
        c += diffuse* shadedNormal(p,d);      // shading
#else
        c += diffuse* (1.-i/40.);             // pseudo-shading
#endif
 	    col += transp*c; transp = 0.;
      }

   O = vec4(col+ transp*skyColor, 1.);
}

