// transformation
mat4 inverseTransform(vec3 s, vec3 rDeg, vec3 t) {

    // convert from degrees to radians
    vec3 rotate = vec3(radians(rDeg.x), radians(rDeg.y), radians(rDeg.z));

    mat4 r_x;
    r_x[0] = vec4(1., 0., 0., 0.);
    r_x[1] = vec4(0., cos(rotate.x), sin(rotate.x), 0.);
    r_x[2] = vec4(0., -sin(rotate.x), cos(rotate.x), 0.);
    r_x[3] = vec4(0., 0., 0., 1.);


    mat4 r_y;
    r_y[0] = vec4(cos(rotate.y), 0., -sin(rotate.y), 0.);
    r_y[1] = vec4(0., 1, 0., 0.);
    r_y[2] = vec4(sin(rotate.y), 0., cos(rotate.y), 0.);
    r_y[3] = vec4(0., 0., 0., 1.);

    mat4 r_z;
    r_z[0] = vec4(cos(rotate.z), sin(rotate.z), 0., 0.);
    r_z[1] = vec4(-sin(rotate.z), cos(rotate.z), 0., 0.);
    r_z[2] = vec4(0., 0., 1., 0.);
    r_z[3] = vec4(0., 0., 0., 1.);

    mat4 rMat = r_x * r_y * r_z;

    // creating worldTransform matrix
    mat4 translateMat;
    translateMat[0] = vec4(1., 0., 0., 0.);
    translateMat[1] = vec4(0., 1., 0., 0.);
    translateMat[2] = vec4(0., 0., 1., 0.);
    translateMat[3] = vec4(t.x, t.y, t.z, 1.);

    mat4 scaleMat;
    scaleMat[0] = vec4(s.x, 0., 0., 0.);
    scaleMat[1] = vec4(0., s.y, 0., 0.);
    scaleMat[2] = vec4(0., 0., s.z, 0.);
    scaleMat[3] = vec4(0., 0., 0., 1.);

    mat4 worldTrans = translateMat * rMat * scaleMat;

    return inverse(worldTrans);
}

// SDF of box
float roundedBox(vec3 p, vec3 s, vec3 r, vec3 t, float radius) {
    mat4 inverseTrans = inverseTransform(s, r, t);
    vec4 tempP = inverseTrans * vec4(p, 1.f);
    p = vec3(tempP.x, tempP.y, tempP.z);

    vec3 q = abs(p) - vec3(0.5, 0.5, 0.5);
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - radius;
}


float sdCappedCylinder(vec3 p, vec3 a, vec3 b, float r)
{

  	vec3  ba = b - a;
    vec3  pa = p - a;
    float baba = dot(ba,ba);
    float paba = dot(pa,ba);
    float x = length(pa*baba-ba*paba) - r*baba;
    float y = abs(paba-baba*0.5)-baba*0.5;
    float x2 = x*x;
    float y2 = y*y*baba;
    float d = (max(x,y)<0.0)?-min(x2,y2):(((x>0.0)?x2:0.0)+((y>0.0)?y2:0.0));
    return sign(d)*sqrt(abs(d))/baba;
}

float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{

  	p.x = abs(p.x);
  	float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  	return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

float smoothBlendSDF(vec3 pos, float a, float b, float k) {

    float h = clamp((0.5f + 0.5f * (b - a) / k), 0.f, 1.f);
    return mix(b, a, h) - k * h * (1.f - h);
}

void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos) {
    t = FLOOR_SDF;
    obj = FLOOR_ID;

    float t2;
    if ((t2 = HANDLE_SDF) < t) {
        t = t2;
        obj = HANDLE_ID;
    }
    if ((t2 = SUN_SDF) <t) {
        t = t2;
        obj = SUN_ID;
    }
    if ((t2 = MJOLNIR_SDF) < t) {
        t = t2;
        obj = MJOLNIR_ID;
    }
    if ((t2 = STRING_SDF) < t) {
        t = t2;
        obj = STRING_ID;
    }
    if ((t2 = CAP_SDF) < t) {
        t = t2;
        obj = CAP_ID;
    }
}

float sceneMap3D(vec3 pos, vec3 lightPos) {
    float t = FLOOR_SDF;

    float t2;
    if ((t2 = HANDLE_SDF) < t) {
        t = t2;
    }
    if ((t2 = SUN_SDF) < t) {
        t = t2;
    }
    if ((t2 = MJOLNIR_SDF) < t) {
        t = t2;
    }
    if ((t2 = CAP_SDF) < t) {
        t = t2;
    }
    return t;
}

float shadowMap3D(vec3 pos, vec3 lightPos) {
    float t = FLOOR_SDF;

    float t2;
    if ((t2 = HANDLE_SDF) < t) {
        t = t2;
    }
    if ((t2 = MJOLNIR_SDF) < t) {
        t = t2;
    }
    return t;
}

float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thin) {
	vec3 scatterDir = lightDir + normal * DISTORTION; // Last term is tunable
	float lightReachingEye = pow(clamp(dot(viewVec, -scatterDir),
							     0.0, 1.0), GLOW) * SCALE;
	float attenuation = max(0.0, dot(normal, lightDir)
                             + dot(viewVec, -lightDir));
    //attenutation = 1.f;
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thin;
	return totalLight;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float max_t, float k) {
    float res = 1.0;
    for(float t = min_t; t < max_t; /*No update in loop header*/) {
        float m = shadowMap3D(origin + t * dir, vec3(0., 0., 0.));
        if(m < 0.0001) {
            return 0.0; // In shadow
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

vec3 rotateY(vec3 p, float a) {
    return vec3(sin(a) * p.x + cos(a) * p.z, p.y, -cos(a) * p.x + sin(a) * p.z);
}

vec2 sphereUVMapSun(vec3 pos, vec3 lightPos) {
    vec3 n = normalize(pos - lightPos); // sphere point - sphere center: for the sun
    float phi = atan(n.z, n.x);
    if (phi < 0.) {
        phi += 2. * 3.1415926;
    }
    float theta = acos(n.y);
    float u = 1. - phi / (2. * 3.1415926);
    float v = 1. - theta / 3.1415926;
    return vec2(u, v);
}

vec2 planeUVMap(vec3 pos) {
    vec3 n = vec3(0., 1., 0.);
    vec3 u = normalize(vec3(1., 0., 0.));
    vec3 v = cross(n, u);

    float u_coord = dot(u, pos);
	float v_coord = dot(v,pos);
    return vec2(u_coord, v_coord);
}

vec3 computeMaterial(int hitObj, vec3 pos, vec3 n, vec3 lightVec, vec3 view, float thickness, vec3 lightPos) {
    float lambert = max(0., dot(lightVec, n)) * softShadow(lightVec, pos, 0.1, 50., 6.0);

    switch(hitObj) {
        case FLOOR_ID:
        	if (pos.z > 70.) {
				return vec3(0.);
            }
        	vec3 warpedColor = vec3(1., 1., 1.) * lambert * colorPalette(cos(iTime * SPEED)* WorleyNoise2D(planeUVMap(2. * pos / 4.)));
    		vec3 warpedConstant = vec3(1., 1., 1.) * lambert * colorPalette(cos(2.6)* WorleyNoise2D(planeUVMap(pos)));
        	//warpedConstant /= 1.5;
        	warpedColor *= 1.3;
        	vec3 floorColor = warpedColor + warpedConstant;
            if (pos.z > 60.) {
				floorColor *= 0.7;
            }
        	return floorColor;
        	break;
        case MJOLNIR_ID:

        	vec3 diffuseColor = vec3(0.65, 0.65, 0.65) * fbm2(vec2(pos.x, pos.y));
        	// Calculate Bling Phong terms
            float diffuseTerm = max(0., dot(normalize(n), normalize(lightVec)));
            vec3 average = normalize((normalize(lightVec) + normalize(view)) / 2.f);
            float specularIntensity = max(pow(dot(average, normalize(n)), 3.5), 0.);
            float lightIntensity = diffuseTerm + 0.2f;

            // Compute final shaded color
        	vec3 blingColor = vec3(diffuseColor * lightIntensity) + vec3(specularIntensity);
        	vec3 mjolnirColor = blingColor + subsurface(lightVec, n, view, thickness) * vec3(0.1, 0.3, 1.);

        	return mjolnirColor;
            break;
		case HANDLE_ID:

        	// metal stripes
            if ((pos.y <= 2. && pos.y > 1.85) || (pos.y <= 3. && pos.y > 2.85) ||
               (pos.y <= 1. && pos.y > 0.85) || (pos.y <= 4. && pos.y > 3.85) || 
               (pos.y <= 5. && pos.y > 4.85)) {

                vec3 diffuseHandle = vec3(180., 180., 180.) / 255.;
                float diffuseTerm2 = max(0., dot(normalize(n), normalize(lightVec)));
            	vec3 average2 = normalize((normalize(lightVec) + normalize(view)) / 2.f);
            	float specularIntensity2 = max(pow(dot(average2, normalize(n)), 50.f), 0.);
            	float lightIntensity2 = diffuseTerm2 + 0.2f;
                vec3 shinyColor = vec3(diffuseHandle * lightIntensity2) + vec3(specularIntensity2);
				return shinyColor;
            }

        	// wood
        	vec3 color = vec3(0.55, 0.26, 0.04) * lambert * fbm(pos.xy);
        	return color + subsurface(lightVec, n, view, thickness) * vec3(0.2, 0.5, 1.);
            break;
        case SUN_ID:
        	return vec3(1.0, 0.88, 0.7) * colorPalette2(fbm(sphereUVMapSun(pos, vec3(10., 0., 100.))) * mix(0.95, 1.15, cos(iTime / 4.)));
        	break;
        case STRING_ID:
        	vec3 lightning = vec3(0.3, 0.8, 1.) * colorPalette(mix(1.2, 1.5, cos(iTime * 3.))* WorleyNoise2D(pos.xy * 64.));

        	return lightning;
        	break;
        case CAP_ID:
        	vec3 diffuseColor3 = vec3(0.45, 0.45, 0.45) * fbm2(vec2(pos.x * 32., pos.y * 32.));
        	// Calculate Bling Phong terms
            float diffuseTerm3 = max(0., dot(normalize(n), normalize(lightVec)));
            vec3 average3 = normalize((normalize(lightVec) + normalize(view)) / 2.f);
            float specularIntensity3 = max(pow(dot(average3, normalize(n)), 20.f), 0.);
            float lightIntensity3 = diffuseTerm3 + 0.2f;

            // Compute final shaded color
        	vec3 blingColor3 = vec3(diffuseColor3 * lightIntensity3) + vec3(specularIntensity3);
        	vec3 mjolnirColor3 = blingColor3 + subsurface(lightVec, n, view, thickness) * vec3(0.1, 0.3, 1.);

        	return mjolnirColor3;
        	break;
        case -1:
            return vec3(0., 0., 0.);
        	break;
    }
    return vec3(0., 0., 0.);
}


vec3 computeNormal(vec3 pos, vec3 lightPos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos), 
                    	  sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                          sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}



void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos) {
    t = 0.001;
    for (int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if (m < 0.01) {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}


float AO5Step(vec3 p, vec3 n) {
    float sum = 0.;
    for (float i = 1.; i <= 5.; i = i + 1.) {
        float distSDF = sceneMap3D(p + n * i * OCCDist, vec3(0.));
        sum += (1. / pow(2., i)) * (i * OCCDist - distSDF);
    }
    float ao = 1.0 - KSTEP * sum;
    return ao;
}


Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos) {
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;

    vec3 nor = computeNormal(isect, lightPos);

    vec3 lightDir = normalize(lightPos - isect);
    float thickness = AO5Step(isect, -nor);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thickness, lightPos);

    return Intersection(t, surfaceColor, isect, hitObj, nor);
}

// returns direction of ray
vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc) {
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0, 1, 0)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}

