void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = uv * 2.0 - vec2(1.0, 1.0);

    vec3 eye = vec3(0, 5.5, -35);
    vec3 ref = vec3(0, 2.5, 0);

    vec3 rayDir = rayCast(eye, ref, uv);
    vec3 lightPos = vec3(9., 8.4, 5.);
    //vec3 lightPos = vec3(9.,8., 7.);
    //vec3 lightPos = rotateY(vec3(0., 7.4, 10.), sin(iTime * 0.5));

    Intersection isect = sdf3D(rayDir, eye, lightPos);

    // ambient occlusion
    float k = 2.5;
    float occDist = 0.12;
    float sum = 0.;
    for (float i = 1.; i <= 5.; i = i + 1.) {
        float distSDF = sceneMap3D(isect.p + isect.n * i * occDist, lightPos);
        sum += (1. / pow(2., i)) * (i * occDist - distSDF);
    }
    float ao = 1.0 - k * sum;


    // Output to screen
    fragColor = vec4(isect.color, 1.) * ao;

    /**** Lightning *****/
    vec2 p = 2.*fragCoord.xy/iResolution.yy-1.;
    p.x -= 0.82;
    p.y += 0.1;
    vec2 p2 = vec2(p.x + 0.5, p.y);

    p *= 3.;
    p2 *= 4.;

    vec2 tgt = vec2(1., -8.);
    float c = 0.;
    vec3 col;

    vec2 tgt2 = vec2(1., -10.);
    float c2 = 0.;
    vec3 col2;

    float t = hash(floor(2.*iTime));
    tgt += 8.*hash2b(tgt+t);
    float t2 = hash(floor(2.*iTime));
    tgt2 += 8.*hash2b(tgt+t);

    if(hash(t+2.3)>.8)
	{
		c= thunderbolt(p*10.+2.*fbm2Light(3.*p),tgt);	
		col+=clamp(1.7*vec3(0.3,.6, 1.2)*c,0.,1.);	
	}

    if(hash(t2+4.3)>.8)
	{
		c2= thunderbolt(p*6.+5.*fbm2Light(3.*p),tgt2);	
		col2+=clamp(1.7*vec3(0.3,.6, 1.2)*c2,0.,1.);	
	}

    if (length(col) > 0.8) {
        fragColor = vec4(col, 0.0);
    }

    if (length(col2) > 0.8) {
        if (p.y > -0.05 || p.y < -1.5) {
            return;
        }
        fragColor = vec4(col2, 0.0);
    }
}

vec3 rgbToFragCol(vec3 rgb) {
    return rgb/255.0;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float ux = uv[0];
    float uy = uv[1];

    vec3 col = rgbToFragCol(vec3(39,60,117));
    fragColor = vec4(col,1.0)*0.0;

    for (float i=1.0; i >= 0.0; i-=0.1) {
        float inv = 1.0-i;
        vec3 col = vec3(1.0-i)*col;
        float pos;
        if (mod(inv, 0.2) > 0.01) {
            pos = sin(ux*100.0/(7.5+i*5.0)-(iTime*inv*5.0))/80.0;
        } else {
            pos = sin(ux*100.0/(7.5+i*5.0)+(iTime*inv*5.0))/80.0;
        }
        if (uy-0.05-i+sin(iTime)*0.01 < pos) {
        	fragColor = vec4(col, 1.0);
    	}

    }
}

