#define PI 3.141592
#define TAU (2.*PI)

float hash21 (vec2 x)
{return fract(sin(dot(x,vec2(12.4,23.4)))*145.4);}

mat2 rot(float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float box (vec3 p, vec3 c)
{return length(max(abs(p)-c,0.));}

vec3 new_p;
float ruban, sphere, g1=0.;
float SDF (vec3 p)
{
    p.x = abs(p.x)-2.;
    p.xz *= rot(PI/4.);

    sphere = length(p-vec3(9.,0.5,0.8))-3.;
    g1 += 0.1/(0.1+sphere*sphere);
    p.x -= p.y*p.y*0.11;

    p.z += sin(p.y*2.+iTime*5.)*0.2;

    p.xz *= rot(p.y*0.35);
    new_p = p;
    ruban = box(p,vec3(2.,40.,0.1));
    return min(sphere,ruban);
}

vec3 palette (float t, vec3 c)
{return vec3(0.5)+vec3(0.5)*cos(TAU*(c*t+vec3(0.,0.36,0.74)));}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.1,0.);
    return normalize(SDF(p)-vec3(SDF(p-eps.xyy),SDF(p-eps.yxy),SDF(p-eps.yyx)));
}

float spec (vec3 rd, vec3 n, vec3 l)
{
    vec3 h = normalize(l-rd);
    return pow(max(dot(n,h),0.), 15.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float dither = hash21(uv);

    vec3 ro = vec3(3.,0.,-5.8),
        p = ro,
        rd = normalize(vec3(uv,1.)),
        l = vec3(-2.,1., -3.),
        col = vec3(0.01,0.,0.03);

    float shad, d = 0.;
    bool hit = false;

    for (float i=0.; i<64.;i++)
    {
        d = SDF(p);
        if (d<0.001)
        {
            hit = true;
            shad = i/64.;
            break;
        }
        d *= 0.7 + dither*0.1;
        p += d*rd;
    }

    if(hit)
    {
        if (d == ruban) col = palette(texture(iChannel0,new_p.xx*0.02).r, vec3(1.));
        if (d == sphere) col = vec3(0.5,0.2,0.);

        vec3 n = getnorm(p);        
        col += spec(rd,n,l);
    }

    col += g1*length(uv)*0.1;

    // Output to screen
    fragColor = vec4(sqrt(col),1.0);
}

const float MAX_RAYMARCH_DISTANCE = 100.;
const int MAX_RAYMARCH_STEPS = 100;
const float EPSILON = 0.0001;
const float VERTICAL_FOV = 45.0;

//Phong Parameters
const float ambientReflectionConstant = 1.;
const float ambientLightingLevel = 1.3;
const float diffuseReflectionConstant = .8;
const float diffuseLightingLevel = .9;
const float specularReflectionConstant = .5;
const float specularLightingLevel = .7;
const float shininess = 16.;

vec3 rand3(vec3 st)
{
    st = vec3( dot(st, vec3(12.9898,78.233, 37.135)),
               dot(st, vec3(325.800, 228.540, 561.121)),
               dot(st, vec3(285.760, 189.060, 126.580)) );
    return -1.0 + 2.0 * fract(sin(st) * 43758.5453123);
}

// returns from 0 to 1
vec3 smootherstep(vec3 x){
	return x*x*x*(x*(x*6.-15.)+10.);
}

float noise(vec3 st)
{
    vec3 i = floor(st);
    vec3 fractional = fract(st);

    // Eight samples from cube corners
    float a = dot( rand3(i), fractional );
    float b = dot( rand3(i + vec3(1.0, 0.0, 0.0)), fractional - vec3(1.0, 0.0, 0.0) );
    float c = dot( rand3(i + vec3(0.0, 1.0, 0.0)), fractional - vec3(0.0, 1.0, 0.0) );
    float d = dot( rand3(i + vec3(1.0, 1.0, 0.0)), fractional - vec3(1.0, 1.0, 0.0) );
    float e = dot( rand3(i + vec3(0.0, 0.0, 1.0)), fractional - vec3(0.0, 0.0, 1.0) );
    float f = dot( rand3(i + vec3(1.0, 0.0, 1.0)), fractional - vec3(1.0, 0.0, 1.0) );
    float g = dot( rand3(i + vec3(0.0, 1.0, 1.0)), fractional - vec3(0.0, 1.0, 1.0) );
    float h = dot( rand3(i + vec3(1.0, 1.0, 1.0)), fractional - vec3(1.0, 1.0, 1.0) );

    vec3 u = smootherstep(fractional);

    float ab = mix(a, b, u.x);
    float cd = mix(c, d, u.x);
    float ef = mix(e, f, u.x);
    float gh = mix(g, h, u.x);
    float c1 = mix(ab, cd, u.y);
    float c2 = mix(ef, gh, u.y);
    return mix(c1, c2, u.z);
}

float sphere(vec3 uv, vec3 position, float radius){
    return (distance(position, uv) - radius);
}

float displacedSphereSDF(vec3 samplePoint){
	float actualDistance = sphere(samplePoint, vec3(0.,0.,0.), .5);
    float modifiedDistance = actualDistance;
    modifiedDistance -= 0.15+0.01*sin(23.98+120.*samplePoint.y*samplePoint.x*samplePoint.z);
    modifiedDistance -= 0.15+0.03*sin(2.*iTime+33.*samplePoint.y);
    modifiedDistance -= 0.15+0.01*sin(123.3+14.*samplePoint.x);
    modifiedDistance -= .2*noise(5.*samplePoint+324.4);

	return modifiedDistance;
}

// Defines the surfaces 
// returns the nearest distance from the provided position to the scene's surfaces
float sceneSDF(vec3 samplePoint){
    float largeSphere = sphere(samplePoint, vec3(0.,0.,0.), .5);

    return displacedSphereSDF(samplePoint);
}



float rayMarch(vec3 cameraPoint, vec3 cameraRay){
	vec3 samplePoint = cameraPoint;
    float depth = 0.;

    for (int i = 0; i < MAX_RAYMARCH_STEPS; i++){
        float closestDistance = sceneSDF(samplePoint);
        // If we are close enough
        if (closestDistance < EPSILON){
        	break;
        }
        depth += closestDistance;
        // If we have exhausted the raymarching
        if (depth > MAX_RAYMARCH_DISTANCE) {
            depth = 0.;
            break;
        }
        // March the Sample Point forward by the closestDistance
        samplePoint += (closestDistance)*cameraRay;  
    }

    // 0. cameraPoint INSIDE the SDF
    // +f distance to the surface
    return depth; // pointOnShape
}

vec3 getNormal(vec3 position){
	vec3 normal = vec3(0.);

    vec3 x1Sample = position+vec3(EPSILON, 0.,0.);
    vec3 x2Sample = position-vec3(EPSILON, 0.,0.);
    float x1SampleDistance = sceneSDF(x1Sample);
    float x2SampleDistance = sceneSDF(x2Sample);
    normal.x = x1SampleDistance-x2SampleDistance;

    vec3 y1Sample = position+vec3(0.,EPSILON,0.);
    vec3 y2Sample = position-vec3(0.,EPSILON,0.);
    float y1SampleDistance = sceneSDF(y1Sample);
    float y2SampleDistance = sceneSDF(y2Sample);
    normal.y = y1SampleDistance-y2SampleDistance;

    vec3 z1Sample = position+vec3(0.,0.,EPSILON);
    vec3 z2Sample = position-vec3(0.,0.,EPSILON);
    float z1SampleDistance = sceneSDF(z1Sample);
    float z2SampleDistance = sceneSDF(z2Sample);
    normal.z = z1SampleDistance-z2SampleDistance;

    return normalize(normal);
}

vec3 cameraRayFromFrag(float vFoVDegrees, vec2 fragCoord){

    // Remap coordinate (0,0) to center of screen
    fragCoord = fragCoord-iResolution.xy/2.;

    float z = iResolution.y / tan(radians(vFoVDegrees) / 2.);

    vec3 cameraRay = vec3(fragCoord.x, fragCoord.y, -z);
    return normalize(cameraRay);
}

float phong(vec3 pointOnSurface, vec3 normal, vec3 cameraPoint, vec3 lightPoint){

    float ambient = ambientReflectionConstant*ambientLightingLevel;
    vec3 L = normalize(lightPoint-pointOnSurface);
    float diffuse = diffuseReflectionConstant*dot(L,normal)*diffuseLightingLevel;

    vec3 R = normalize(reflect(-L, normal));
    vec3 view = normalize(cameraPoint-pointOnSurface);
    float reflectionFactor = dot(R, view);
    float specular = 0.;
    if (reflectionFactor > 0.){
    	specular = specularReflectionConstant*pow(dot(R, view), shininess)*specularLightingLevel;
    }

    return ambient+diffuse+specular;
}

vec3 asteroidShader(vec3 pointOnSurface, vec3 normal){

    vec3 outputColor = vec3(0.); 
    //float threshold = step(1.1, length(pointOnSurface));

    float elevation = clamp((length(pointOnSurface)-.87)*3.03, 0., 1.);
    float slope = length(cross(pointOnSurface, normal));
    float slopeElevbyElev = pow(mix(elevation, 1.-slope, elevation),2.);
    outputColor = mix(vec3(0.02,.01,0.),
                      vec3(.8,.6,.3), 
                      slopeElevbyElev);



    //.87 to 1.1

    return outputColor;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv.x *= iResolution.x/iResolution.y;

    // Grey Background
    vec3 col = vec3(0.2);


    vec3 cameraPoint = vec3(0.,0.,5.);
    vec3 cameraRay = cameraRayFromFrag(VERTICAL_FOV, fragCoord);
    float rayMarchDistance = rayMarch(cameraPoint, cameraRay);
    vec3 pointOnSurface = cameraPoint+cameraRay*rayMarchDistance;
    vec3 normal = getNormal(pointOnSurface);
    vec3 lightPoint = vec3(cos(1.3*iTime),2.,2.+sin(1.1*iTime));

    float phongLighting = phong(pointOnSurface,normal,cameraPoint,lightPoint);
    vec3 asteroidShaderColor = asteroidShader(pointOnSurface, normal);

    col = mix(col, phongLighting*asteroidShaderColor, clamp(rayMarchDistance, 0., 1.));

    // Output to screen
    fragColor = vec4(col, 1.0);
}

