#define AA  1   // optional antialiasing

float PrBox2Df (vec2 p, vec2 b);
float PrSphDf (vec3 p, float r);
float PrRoundCylDf (vec3 p, float r, float rt, float h);
float PrTorusDf (vec3 p, float ri, float rc);
float Maxv3 (vec3 p);
mat3 StdVuMat (float el, float az);
vec2 Rot2D (vec2 q, float a);
float Fbm2 (vec2 p);

vec3 ltDir;
float tCur, dstFar, tRadB, tRadS;
int idObj;
const float pi = 3.14159;

#define VAR_ZERO min (iFrame, 0)

#define DMIN(id) if (d < dMin) { dMin = d;  idObj = id; }

float RobDf (vec3 p, float dMin)
{
  vec3 q;
  float szFac, rAngH, rAngA, d, s;
  s = sin (pi * tCur);
  rAngH = -0.7 * s;
  rAngA = 1.1 * s;
  szFac = 0.15;
  dMin /= szFac;
  p /= szFac;
  p.y -= 0.25;
  q = p; 
  q.y -= 2.3;
  d = max (PrSphDf (q, 0.85), - q.y - 0.2);
  q = p; 
  q.y -= 1.5;
  d = min (d, PrRoundCylDf (q.xzy, 0.9, 0.28, 0.75));
  q = p;  
  q.x = abs (q.x); 
  q.xy -= vec2 (1.05, 2.);
  q.yz = Rot2D (q.yz, rAngA * sign (p.x));
  q.y -= -0.5;
  d = min (d, PrRoundCylDf (q.xzy, 0.2, 0.15, 0.6));
  q = p;
  q.xz = Rot2D (q.xz, rAngH);
  q.x = abs (q.x);
  q.xy -= vec2 (0.3, 3.1);
  q.xy = Rot2D (q.xy, 0.2 * pi);
  q.y -= 0.25;
  d = min (d, PrRoundCylDf (q.xzy, 0.06, 0.04, 0.3));
  q = p;
  q.xy -= vec2 (0.4, 0.3);
  d = min (d, PrRoundCylDf (q.xzy, 0.25, 0.15, 0.65));
  q = p;
  q.x = - q.x;  
  q.xy -= vec2 (0.4, 0.3);
  d = min (d, PrRoundCylDf (q.xzy, 0.25, 0.15, 0.65));
  DMIN (2);
  q = p;
  q.xz = Rot2D (q.xz, rAngH);
  q.x = abs (q.x); 
  q -= vec3 (0.4, 2.7, 0.7);  
  d = PrSphDf (q, 0.15);
  DMIN (3);
  return szFac * dMin;
}

float ObjDf (vec3 p)
{
  vec3 q;
  vec2 b;
  float dMin, d, a, t, ns;
  dMin = dstFar;
  p.yz = Rot2D (p.yz, 0.1 * pi);
  q = p;
  ns = 16.;
  t = 0.05 * tCur;
  d = length (vec2 (Rot2D (vec2 (length (q.xz) - tRadB, q.y),
     0.5 * atan (q.z, q.x)))) - tRadS;
  q.xz = Rot2D (q.xz, t);
  a = 2. * pi * (floor (ns * atan (q.z, - q.x) / (2. * pi)) + 0.5) / ns;
  q.xz = Rot2D (q.xz, a);
  q.x += tRadB;
  q.xy = Rot2D (q.xy, 0.5 * (a + t));
  b = vec2 (0.95 * tRadS, 0.65 * pi * tRadB / ns);
  d = max (d, - max (PrBox2Df (vec2 (abs (q.x) - tRadS, q.z), b),
     PrBox2Df (vec2 (abs (q.y) - tRadS, q.z), b)));
  DMIN (1);
  q.xy = Rot2D (q.xy, 2. * pi * (floor (4. * atan (q.y, - q.x) / (2. * pi)) / 4.)) -
     vec2 (-0.35, 0.06);
  q.xz = vec2 (q.z, - q.x);
  dMin = RobDf (q, dMin);
  return 0.7 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.001 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float TrObjDf (vec3 p)
{
  p.yz = Rot2D (p.yz, 0.1 * pi);
  return PrTorusDf (p.xzy, tRadS - 0.02, tRadB);
}

float TrObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 120; j ++) {
    d = TrObjDf (ro + dHit * rd);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 TrObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.005, -0.005);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = TrObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec3 ErCol (vec3 rd)
{
  vec3 erDir, col, vn;
  float erRad, bs, ts;
  erDir = normalize (vec3 (0.02, -0.04, 1.));
  erRad = 0.04;
  col = vec3 (0.);
  bs = dot (rd, erDir);
  ts = bs * bs - 1. + erRad * erRad;
  if (ts > 0.) {
    ts = bs - sqrt (ts);
    if (ts > 0.) {
      vn = normalize ((ts * rd - erDir) / erRad);
      col = mix (vec3 (0.3, 0.4, 0.8), vec3 (1., 1., 0.95),
         smoothstep (0.2, 0.8, Fbm2 (6. * vn.xy + 7.1))) * (0.5 + 0.5 * max (- dot (vn, rd), 0.)) *
         (0.2 + 0.8 * max (dot (vn, ltDir), 0.));
    }
  }
  return col;
}

vec3 StarPat (vec3 rd, float scl)
{
  vec3 tm, qn, u;
  vec2 q;
  float f;
  tm = -1. / max (abs (rd), 0.0001);
  qn = - sign (rd) * step (tm.zxy, tm) * step (tm.yzx, tm);
  u = Maxv3 (tm) * rd;
  q = atan (vec2 (dot (u.zxy, qn), dot (u.yzx, qn)), vec2 (1.)) / pi;
  f = 0.57 * (Fbm2 (11. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 131.13 * scl * q) +
      Fbm2 (13. * dot (0.5 * (qn + 1.), vec3 (1., 2., 4.)) + 171.13 * scl * q.yx));
  return 8. * vec3 (1., 1., 0.8) * pow (f, 16.);
}

vec3 BgCol (vec3 rd)
{
  vec3 col;
  col = ErCol (rd);
  if (length (col) < 0.03) col += StarPat (rd, 16.);
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 col, colR, vn, roo;
  float dstObj, dstObjT;
  tRadB = 3.;
  tRadS = 0.8;
  roo = ro;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 1) col4 = vec4 (0.5, 0.5, 0.55, 0.2);
    else if (idObj == 2) col4 = vec4 (0., 1., 1., 0.2);
    else if (idObj == 3) col4 = vec4 (1., 0., 0., 0.2);
    col = col4.rgb * (0.2 + 0.2 * max (- dot (vn, ltDir), 0.) +
       0.8 * max (dot (vn, ltDir), 0.)) +
       col4.a * pow (max (dot (normalize (ltDir - rd), vn), 0.), 32.);
  } else {
    col = BgCol (rd);
  }
  ro = roo;
  dstObjT = TrObjRay (ro, rd);
  if (dstObjT < min (dstObj, dstFar)) {
    ro += dstObjT * rd;
    vn = TrObjNf (ro);
    col = mix (0.1 + 1.5 * BgCol (reflect (rd, vn)), col, 0.1 +
       0.9 * smoothstep (0.1, 0.7, - dot (rd, vn)));
  }
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv;
  float el, az, zmFac, sr;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = -0.07 * pi;
  el = -0.05 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += pi * mPtr.y;
  }
  vuMat = StdVuMat (el, az);
  ro = vuMat * vec3 (0., 0., -20.);
  zmFac = 6.;
  dstFar = 50.;
  ltDir = normalize (vec3 (1., 0.7, -1.));
#if ! AA
  const float naa = 1.;
#else
  const float naa = 3.;
#endif  
  col = vec3 (0.);
  sr = 2. * mod (dot (mod (floor (0.5 * (uv + 1.) * canvas), 2.), vec2 (1.)), 2.) - 1.;
  for (float a = float (VAR_ZERO); a < naa; a ++) {
    rd = vuMat * normalize (vec3 (uv + step (1.5, naa) * Rot2D (vec2 (0.5 / canvas.y, 0.),
       sr * (0.667 * a + 0.5) * pi), zmFac));
    col += (1. / naa) * ShowScene (ro, rd);
  }
  fragColor = vec4 (pow (col, vec3 (0.8)), 1.);
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrRoundCylDf (vec3 p, float r, float rt, float h)
{
  float dxy, dz;
  dxy = length (p.xy) - r;
  dz = abs (p.z) - h;
  return min (min (max (dxy + rt, dz), max (dxy, dz + rt)), length (vec2 (dxy, dz) + rt) - rt);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float Maxv3 (vec3 p)
{
  return max (p.x, max (p.y, p.z));
}

mat3 StdVuMat (float el, float az)
{
  vec2 ori, ca, sa;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  return mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
         mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
}

vec2 Rot2D (vec2 q, float a)
{
  vec2 cs;
  cs = sin (a + vec2 (0.5 * pi, 0.));
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

const float cHashM = 43758.54;

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (dot (p, cHashVA2) + vec2 (0., cHashVA2.x)) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 200
#define MAX_DIST 80.
#define SURF_DIST .002
#define EDGE_WIDTH 0.015
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)

float edge;

float smin( float a, float b, float k ) {
    float h = clamp( 0.5+0.5*(b-a)/k, 0., 1. );
    return mix( b, a, h ) - k*h*(1.0-h);
}

vec4 combine(vec4 val1, vec4 val2 ){
    return (val1.w < val2.w)?val1:val2;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

vec3 emitColor(vec3 p, vec3 emitPos, vec3 emitColor, float radius, float softness, float brightness) {
    float c = smoothstep(-softness,softness,-(length(p-emitPos)-radius));
    emitColor = (emitColor*c)*brightness;
    return emitColor;
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}


// ventilator pipe based on the sdLink distance function
// Notice that the following function is not reusable code.
float sdVentilator( vec3 p, float le, float r1, float r2 )
{
    p.x*=0.9;
    vec3 prevP = p;
    vec3 q = vec3( p.x, p.y, max(abs(p.z)-le,0.0) );
    vec2 p2 = vec2(length(q.xz)-r1,q.y);

    float d = length(p2) - r2;
	float d2 = length(p2) - (r2*0.8);

    float space = 0.05;
    p.z = mod(p.z,0.38)-0.19;
    float b = sdBox(p,vec3(1.5,0.3,space));
    p = prevP;
	float b2 = sdBox(p,vec3(1.5,0.3,0.9));
    b = max(b2,b);

    p*= matRotateY(radians(52.0));
    float b3 = sdBox(p,vec3(1.5,0.3,space));
    p = prevP;
    b = min(b,b3);

    p*= matRotateY(radians(70.0));
	float b4 = sdBox(p,vec3(1.5,0.3,space));
    p = prevP;
    b = min(b,b4);

    p*= matRotateY(radians(-52.0));
    float b5 = sdBox(p,vec3(1.5,0.3,space));
    p = prevP;
    b = min(b,b5);

    p*= matRotateY(radians(-70.0));
	float b6 = sdBox(p,vec3(1.5,0.3,space));
    p = prevP;
    b = min(b,b6);


    vec3 p3 = p-vec3(0.0,0.02,1.15);
    p3*= matRotateZ(radians(90.0));
    float d3 = sdCappedCylinder(p3,0.2,0.33);

    return min(min(d2,d3),max(-b,d))*0.6;
}

float headBack(vec3 p) {
    float size = 1.5;
    float thickness = 0.35;
    vec4 a1 = vec4(0.0,size,1.6,0.0); // btm
    float p1 = dot(p,a1.xyz) + a1.w;

    vec4 a2 = vec4(0.0,-size,0.0,0.85); // top
    float p2 = dot(p,a2.xyz) + a2.w;

    vec4 a3 = vec4(size,0.0,0.0,0.15); // right
    float p3 = dot(p,a3.xyz) + a3.w;

    vec4 a4 = vec4(-size,0.0,0.0,0.15); // left
    float p4 = dot(p,a4.xyz) + a4.w;

    vec4 a5 = vec4(0.0,0.0,-size,thickness); // foward
    float p5 = dot(p,a5.xyz) + a5.w;

    vec4 a6 = vec4(0.0,0.0,size,thickness); // back
    float p6 = dot(p,a6.xyz) + a6.w;

    float d = max(-p1,max(-p2,max(-p3,max(-p4,max(-p5,-p6)))));
    return d;
}

float sdHead(vec3 p) {
    vec3 prevP = p;
    p.x*=1.15;
    float d =  length(p)-1.0;
    d = max(-(p.y+0.3),d);
    float d2 = sdBox(p-vec3(0.0,0.3,1.0),vec3(1.5,0.2,0.5));
    d = max(-d2,d);

    p = prevP;
    p*=matRotateX(radians(-110.0));
    p.y-=1.25;
    p.z+=1.35;
    float sc = mix(0.7,3.0,smoothstep(-1.0,1.0,p.y));
    vec3 p2 = p-vec3(0.0,-0.25,1.2);
    p2.x*=sc;

    d2 = sdBox(p2,vec3(0.6,0.46,.15));
    d = smin(d,d2,0.3);

    p = prevP;
    p.x = abs(p.x);
    p.x-=0.85;
    d2 = sdBox(p-vec3(0.0,0.34,0.15),vec3(0.15,0.15,0.15));
    d = max(-d2,d);
    p = prevP;
    p*=matRotateX(radians(-18.0));
    d2 = sdBox(p-vec3(0.0,0.1,1.5),vec3(0.2,0.1,0.1));
    d = max(-d2,d);
    p = prevP;

    p.x = abs(p.x);
    p.x-=0.15;

    d2 = headBack(p-vec3(0.0,-0.04,-1.08));
    d = smin(d,d2,0.1);

    return d*0.6;
}

vec4 GetDist(vec3 p) {

    vec3 prevP = p;
    float _floor = p.y;

    float y = 0.0;

    p*=matRotateX(radians(-10.0));
    float v = sdVentilator(p-vec3(0.0,1.2+y,-0.2),0.3,0.9,0.15);
    p = prevP;
	float h = sdHead(p-vec3(0.0,1.1+y,0.0));

    float d = min(v,h);
    vec4 res = vec4(vec3(0.4,0.5,0.4),d);

    vec3 eyeEmitPos = vec3(sin(iTime*2.0)*0.2,1.43+y,0.56);
    vec3 eyePos = p-eyeEmitPos;
    eyePos *= matRotateX(radians(90.0));
    vec3 ecol = emitColor(p,eyeEmitPos, vec3(1.0,0.5,0.8),0.0001,0.9,3.0);
    d = sdCappedCylinder(eyePos,0.15,0.02);
    vec4 eye = vec4(vec3(0.7,0.0,0.0)+ecol,d);

    vec4 model = combine(res,eye);
    return model;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 r = vec4(0.0,0.0,0.0,1.0);

    float lastDistEval = 1e10; 
    float dist;
    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*r.w;
        vec4 dS = GetDist(p);
        dist =  dS.w;
        r.w += dS.w;
        r.rgb = dS.xyz;

		if (lastDistEval < EDGE_WIDTH && dist > lastDistEval + 0.001) {
            edge = 1.0;
		}
        if (dist < lastDistEval) lastDistEval = dist;

        if(r.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return r;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

float shadowMap(vec3 ro, vec3 rd){
    float h = 0.0;
    float c = 0.001;
    float r = 1.0;
    float shadow = 0.5;
    for(float t = 0.0; t < 30.0; t++){
        h = GetDist(ro + rd * c).w;
        if(h < 0.001){
            return shadow;
        }
        r = min(r, h * 16.0 / c);
        c += h;
    }
    return 1.0 - shadow + r * shadow;
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(2,8,3);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float lambert = max(.0, dot( n, l))*0.1;

    float shadow = shadowMap(p + n * 0.001, l);

    return vec2((lambert+dif),max(0.9, shadow)) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

vec3 zionLogo(vec2 p, vec3 col, vec3 bcol) {
    vec2 prevP = p;
    p.y*=0.95;
    float d = length(p)-0.2;
    p.x*=1.2;
    float d2 = length(p)-0.18;
    d = max(-d2,d);

    p.x = abs(p.x);
    p.x -= 0.15;
    d2 = sdTriangle(p, vec2(0.22,0.05), vec2(0.03,-0.02), vec2(0.0,-0.155));
    d = min(d,d2);
    p = prevP;

    p.x = abs(p.x);
    d2 = sdTriangle(p-vec2(0.0,0.35), vec2(0.03,-0.2), vec2(0.0,0.0), vec2(0.0,-0.3));
    d = min(d,d2);
    p = prevP;

    d2 = sdBox(p-vec2(0.0,-0.15),vec2(0.012,0.25));
    d = min(d,d2);

    p.x = abs(p.x);
    d2 = sdTriangle(p-vec2(0.0,-0.35), vec2(0.05,0.00), vec2(0.00,-0.03), vec2(0.0,-0.14));
    d = min(d,d2);
    p = prevP;

	p.x = abs(p.x);
    p.x -= 0.01;
    d2 = sdTriangle(p-vec2(0.0,-0.1), vec2(0.1,0.05), vec2(0.0,0.0), vec2(0.0,-0.05));
    d = min(d,d2);
    p = prevP;

    p.x = abs(p.x);
    d2 = sdTriangle(p-vec2(0.0,-0.27), vec2(0.45,0.2), vec2(0.0,0.0), vec2(0.15,-0.03));
    d = min(d,d2);
    p = prevP;

	p.x = abs(p.x);
    p.x -= 0.01;
    d2 = sdTriangle(p-vec2(0.0,0.02), vec2(0.3,0.2), vec2(0.0,0.0), vec2(0.0,-0.06));
    d = min(d,d2);
    p = prevP;

    col = mix(col,bcol,S(d,-0.002));
    return col;
}

vec3 serialNumber(vec2 p, vec3 col, vec3 bcol) {
    vec2 prevP = p;

    //ãï¼
    vec2 p0 = p-vec2(-0.1,0.0);
    float d = sdBox(p0,vec2(0.1,0.15));
    float d2 = sdBox(p0,vec2(0.05,0.1));
    d = max(-d2,d);
    d2 = sdBox(p0,vec2(0.01,0.2));
    d = max(-d2,d);

    // 6
    vec2 p1 = p-vec2(0.1,0.0);
    d2 = sdBox(p1-vec2(-0.05,0.0),vec2(0.025,0.15));
    d = min(d,d2);
    d2 = sdBox(p1-vec2(0.025,-0.06),vec2(0.075,0.09));
    d = min(d,d2);
    d2 = sdBox(p1-vec2(0.015,-0.06),vec2(0.04,0.04));
    d = max(-d2,d);
    d2 = sdBox(p1-vec2(0.035,0.125),vec2(0.065,0.025));
    d = min(d,d2);
    d2 = sdBox(p1-vec2(-0.02,0.0),vec2(0.01,0.2));
    d = max(-d2,d);

    col = mix(col,bcol,S(d,0.0));
    return col;
}

vec3 animationLine(vec2 p, vec3 col, float dir) {
    p.x+=iTime*0.1*dir;
    p.x = mod(p.x,0.02)-0.01;
    p*=Rot(radians(45.0*dir));
    float d = sdBox(p,vec2(0.001,0.05));
	col = mix(col,vec3(1.0),S(d,0.0));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec2 m = iMouse.xy/iResolution.xy;
    float t = mod(iTime,8000.0);
    vec3 col = vec3(0);

    vec3 ro = vec3(0, 3.5, -1.0);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-45.0));
    ro.xz *= Rot(radians((sin(t*0.2)*80.0)+180.0));
    #endif

    vec3 rd = R(uv, ro, vec3(0,1.1,0), 1.);
	vec4 r = RayMarch(ro, rd);

    if(r.w<MAX_DIST) {
        vec3 p = ro + rd * r.w;

        vec2 dif = GetLight(p);
        col = vec3(dif.x)*r.rgb;
        col *= dif.y;

    } else {
        // background
        col =.84*max(mix(vec3(1.1)+(.1-length(uv.xy)/3.),vec3(1),.1),0.);

        uv*=2.0;
        col = zionLogo(uv-vec2(1.05,0.5),col,vec3(0.7,0.1,0.1));
        uv = prevUV;
        uv*=1.5;
        col = serialNumber(uv-vec2(-0.9,-0.45),col,vec3(0.95));
    }

    col = mix(col,vec3(0.1,0.1,0.1),edge);

    // overlay contents
    uv = prevUV;
    col = animationLine(uv-vec2(0.0,-0.49),col,1.0);
    col = animationLine(uv-vec2(0.0,0.49),col,-1.0);

    fragColor = vec4(col,1.0);
}

