void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float thickness = 0.1;
    float radius = 0.3;
    vec2 origin = vec2(0.5);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    vec3 col = vec3(0.5,0.5,0.36);

    // Time varying pixel color
    if (abs(length(uv-origin) - radius) < abs(sin(iTime)/3.)) {
    	col = 0.5 + 0.5*cos(iTime+length(uv-radius)+col);
    	col.r += sin(iTime)+cos(iTime);
    }

    else {
    	col = 0.5 + 0.5*sin(iTime+length(uv-radius)+col);
        col.r += sin(iTime)+cos(iTime);
    }

    col.g /= col.r;

    col.g *= 0.05;

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - .5*iResolution.xy)/(min(iResolution.x, iResolution.y));

    // cycles between the options

    float opt = floor(mod(iTime*.3, 4.0));
    float shadow = 0.8; 

    //angle
    float a = atan(uv.y, uv.x);

    if(opt == 3.0){ a += 0.3/length(uv); }

    float r = length(uv);

    if(opt == 2.0){ r *= sin(a*3.)*.5 + 4.; }
    if(opt == 3.0){ r *= sin(a*10.)*.5 + 4.; }

    if(opt == 0.0){ uv.y = r; }
    else { uv.y = 1./r; }

    uv.x = a;



    // animate
    uv.y -= 0.1*iTime;

    // repeating grid
    float w = 0.02;
    uv *= 5.0;
    uv = mod(uv + .5, 1.0) - .5;
    vec3 col = vec3(uv.x, uv.y, 0.);

    // draw white lines
    if(uv.x > -w && uv.x < w){ col += vec3(1.0); }
    if(uv.y > -w && uv.y < w){ col += vec3(1.0); }

    // shading in the middle
    col *= smoothstep(0., shadow, r);

    // Output to screen
    fragColor = vec4(col,1.0);
}

