	vec3 ref_ev = reflect( ev, n );

	// light 0
	{

		vec3 vl = normalize( plight_pos1 - v );

		float diffuse  = max( 0.0, dot( vl, n ) );
		float specular = max( 0.0, dot( vl, ref_ev ) );
