//--- program parameters ---//

const float	DEPTH		= 48.0;
const float	ITERATIONS	= 64.0;
const float SKIN_SIZE   =  6.0;


//--- fractal formula (julia 3rd degree polynomial) ---//

float julia(vec2 p, vec2 parameter)
{
	for (float i = 1.0; i <= ITERATIONS; i++)
    {
		vec2 o = p;

		p = vec2(p.x*p.x - p.y*p.y, 2.0*p.x*p.y);
		p = vec2(p.x*o.x - p.y*o.y + parameter.x, o.x*p.y + p.x*o.y + parameter.y);

        if (dot(p, p) > 4.0) return i;
	}

	return 0.0;	
}


//--- coloring function ---//

vec4 color(float n, float i)
{
    return SKIN_SIZE < n?

        vec4(vec3(1.0), 1.0)

			:

		vec4(0.6 + 0.4*sin(0.874*iTime + (1.22 - 0.4*sin(0.88*iTime))*i),
             0.6 + 0.4*sin(0.675*iTime + 1.26*i), 
             0.6 + 0.4*cos(0.543*iTime + 0.84*i),
             1.0);
}


//--- main program ---//

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 pixel_coord = 1.0*(fragCoord - 0.5*iResolution.xy) / iResolution.y;

    pixel_coord *= 0.9 - 0.3*sin(0.21*iTime);


	for (float i = 0.0; i < DEPTH; i++)
    {
        float f = 0.12*i;

		vec2 parameter = vec2(0.35*cos(iTime-f) + 0.1*sin(0.234*iTime+0.68*f),
                              0.25*sin(iTime+1.4*f) + 0.11*sin(0.431*iTime-f));

        float n = julia(pixel_coord, parameter);

        if (0.0 < n)
        {
            fragColor = color(n, i);
            return;
        }

        pixel_coord *= 1.05;
    }


    fragColor = vec4(0.0, 0.0, 0.0, 1.0);
}

