// udTriangle/sdBox/sdCapsule - https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float dot2(vec3 v) { return dot(v,v); }
float udTriangle(vec3 p, vec3 a, vec3 b, vec3 c)
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

	return //sqrt(
       (sign(dot(cross(ba,nor),pa)) +
		sign(dot(cross(cb,nor),pb)) +
		sign(dot(cross(ac,nor),pc))<2.0)
		?
            min( min(
            dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
            dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
            dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
		:
			dot(nor,pa)*dot(nor,pa)/dot2(nor);// );
}
float sdBox(vec3 p, vec3 b)
{
    vec3 q = abs(p) - b;
    return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}
float sdSphere(vec3 p, float r)
{
	return length(p) - r;
}
float sdCapsule(vec3 p, vec3 a, vec3 b, float r)
{
	vec3 pa = p - a, ba = b - a;
	float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
	return length( pa - ba*h ) - r;
}

struct Bound3
{
    vec3 mMin;
    vec3 mMax;
};


// AABB of controls
vec3 ComponentMin(in vec3 a, in vec3 b)
{
    return vec3(min(a.x, b.x), min(a.y, b.y), min(a.z, b.z));
}
vec3 ComponentMax(in vec3 a, in vec3 b)
{
    return vec3(max(a.x, b.x), max(a.y, b.y), max(a.z, b.z));
}
Bound3 BezierControlAABB(in vec3 p0, in vec3 p1, in vec3 p2, in vec3 p3)
{
    return Bound3(ComponentMin(p0, ComponentMin(p1, ComponentMin(p2, p3))),
                  ComponentMax(p0, ComponentMax(p1, ComponentMax(p2, p3)))
                 );
}


vec3 SampleBezier(in vec3 a, in vec3 b, in vec3 c, in vec3 d, in float t)
{
    // Factorized the shit outta it
	float t2 = t*t;
	float t3 = t2*t;
    return t3*(3.0*(b - c) + d - a) + 3.0*(t2*(a + c - 2.0*b) + t*(b - a)) + a;

    /*
    // First degree
    vec3 p11 = mix(a, b, t);
    vec3 p12 = mix(b, c, t);
    vec3 p13 = mix(c, d, t);

    // Second degree
    vec3 p21 = mix(p11, p12, t);
    vec3 p22 = mix(p12, p13, t);

    // Third degree
    return mix(p21, p22, t);
	*/
}


