Hit GetDist(vec3 p)
{
    Hit closest; // TODO try combining vec4(color, dist) and see if mix speeds up intersections
    closest.Dist = 999999.;
    closest.Color = vec3(1);

    // Plane
    {
        float d = p.y+0.6;
        if (d < closest.Dist)
        {
            closest.Dist = d;
            closest.Color = vec3(.5,.5,.5);
        }
    }

    // Verts
    /*{
		float r = 0.02;
        float d = 9999999.;
        d = min(sdSphere(p-_leftA.Vertex, r), d);
        d = min(sdSphere(p-_leftB.Vertex, r), d);
        d = min(sdSphere(p-_rightA.Vertex, r), d);
        d = min(sdSphere(p-_rightB.Vertex, r), d);

        if (d < closest.Dist)
        {
            closest.Dist = d;
            closest.Color = vec3(0.2,0.2,1);
        }
    }*/



    // Mesh bounding box test
    {
        // Dist to AABB
        Bound3 bL = BezierControlAABB(_leftA.Vertex, _leftA.Handle, _leftB.Vertex, _leftB.Handle);
        Bound3 bR = BezierControlAABB(_rightA.Vertex, _rightA.Handle, _rightB.Vertex, _rightB.Handle);
        vec3 bMin = ComponentMin(bL.mMin, bR.mMin);
        vec3 bMax = ComponentMax(bL.mMax, bR.mMax);
        vec3 bCen = 0.5*(bMin+bMax);
    	vec3 bDim = 0.5*(bMax-bMin);
        float dBox = sdBox(p-bCen, bDim);


		// Draw bounds
		/*
		if (dBox < closest.Dist)
        {
            closest.Dist = dBox;
            closest.Color = vec3(1,1,0);
        }        
		dBox = min(sdSphere(p-bMin, 0.1), dBox);
        if (dBox < closest.Dist)
        {
            closest.Dist = dBox;
            closest.Color = vec3(1,0,1);
        }

        dBox = min(sdSphere(p-bMax, 0.1), dBox);
        if (dBox < closest.Dist)
        {
            closest.Dist = dBox;
            closest.Color = vec3(0,1,1);
        }
		*/


        if (dBox < closest.Dist)
        {
            // Handles
            {
                float rHandle = 0.03;
                float rRod = 0.01;
                float d = 9999999.;
                d = min(sdSphere(p-_leftA.Handle, rHandle), d);
                d = min(sdSphere(p-_leftB.Handle, rHandle), d);
                d = min(sdSphere(p-_rightA.Handle, rHandle), d);
                d = min(sdSphere(p-_rightB.Handle, rHandle), d);

                d = min(sdCapsule(p, _leftA.Vertex, _leftA.Handle, rRod), d);
                d = min(sdCapsule(p, _leftB.Vertex, _leftB.Handle, rRod), d);
                d = min(sdCapsule(p, _rightA.Vertex, _rightA.Handle, rRod), d);
                d = min(sdCapsule(p, _rightB.Vertex, _rightB.Handle, rRod), d);

                if (d < closest.Dist)
                {
                    closest.Dist = d;
                    closest.Color = vec3(1);
                }
            }

            // Mesh
            {
                const vec3 color = vec3(1);

                // Generate Tris

                /* Sample bezier points for quad

                  lt=a  rt=c
                ^ |   / |
                | |  /  |
                | | /   |
                V lb=b  rb=d
                  U --->          */             

                int index = 0;
                float dTri=9999999.;

                vec3 leftBot  = SampleBezier(_leftA.Vertex,  _leftA.Handle,  _leftB.Handle,  _leftB.Vertex,  0.);
                vec3 rightBot = SampleBezier(_rightA.Vertex, _rightA.Handle, _rightB.Handle, _rightB.Vertex, 0.);

                float rowStep = 1./float(DIMZ_SEGS);
                float colmStep = 1./float(DIMX_SEGS);
                for (int row = 1; row <= DIMZ_SEGS; row++)
                {
                    // Sample the bezier curve on the far left and right of this entire row. V coord.
                    float top = float(row)*rowStep;
                    vec3 leftTop  = SampleBezier(_leftA.Vertex,  _leftA.Handle,  _leftB.Handle,  _leftB.Vertex,  top);
                    vec3 rightTop = SampleBezier(_rightA.Vertex, _rightA.Handle, _rightB.Handle, _rightB.Vertex, top);

                    for (int colm = 1; colm <= DIMX_SEGS; colm++)
                    {
                        // Interpolate the row's bezier samples for the quad we're in. U Coord.
                        float left = float(colm-1)*colmStep;
                        float right = float(colm)*colmStep;

                        // TODO EZ-OPTIMISE - Cache the left values outside of the loop to reduce computation
                        vec3 a = mix(leftTop, rightTop, left);  // top left
                        vec3 b = mix(leftBot, rightBot, left);  // bot left
                        vec3 c = mix(leftTop, rightTop, right); // top right
                        vec3 d = mix(leftBot, rightBot, right); // bot right

                        // Depth test triangles
                        dTri = min(udTriangle(p, a, b, c), dTri);
                        dTri = min(udTriangle(p, c, b, d), dTri);
                    }

                    // Store this top sample for use as bottom in the next row
                    leftBot = leftTop;
                    rightBot = rightTop;
                }

                // I removed teh sqrt from udTriangle, so i must do it here!
                dTri = sqrt(dTri) - 0.01;
                if (dTri < closest.Dist)
                {
                    closest.Dist = dTri;
                    closest.Color = color;
                }
            }
        }
    }

    return closest;
}


