vec3 GetNormal(vec3 p)
{
    // Normal technique from iq: https://www.shadertoy.com/view/Xds3zN - I think this speeds up compile time by avoiding inlining of GetDist 4 times.
    vec3 n = vec3(0.0);
    for( int i=ZERO; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*GetDist(p + 0.0005*e).Dist;
    }
    return normalize(n);

    /*float d = GetDist(p).Dist;
    float offset = 0.01;
    vec3 n = d - vec3(
        GetDist(p - vec3(offset,0,0)).Dist,
        GetDist(p - vec3(0,offset,0)).Dist,
        GetDist(p - vec3(0,0,offset)).Dist
        );

    return normalize(n);*/
}

Hit RayMarch(Ray r)
{
    Hit lastHit;
    float totalDist = 0.0;

    for (int i=0; i<MAX_STEPS; i++)
    {
        vec3 p = r.Origin + totalDist*r.Direction;
        lastHit = GetDist(p);
        totalDist += lastHit.Dist;

        if (totalDist < SURFACE_DIST || totalDist > MAX_DIST) 
            break;
    }

    lastHit.Dist = totalDist;

    return lastHit;
}

vec3 GetLight(vec3 p, Light light)
{
    vec3 PL = light.Pos-p;
    float PLLen = length(PL);

    vec3 lightNormal = PL/PLLen;
    vec3 surfNormal = GetNormal(p);

    // Light
    float strength = clamp(dot(surfNormal, lightNormal), 0., 1.);
    float attenuation = 1. / max(PLLen*PLLen, 0.001);

    // Shadow
    Ray shadow;
    shadow.Origin = p + surfNormal*SURFACE_DIST*2.;
    shadow.Direction = lightNormal;
    if(RayMarch(shadow).Dist < PLLen) strength = 0.0;

    return light.Intensity * light.Color * strength * attenuation;
}

mat3 RotateXZ(float rad)
{
    float cosr = cos(rad);
    float sinr = sin(rad);
    return mat3(
        cosr,  0,  sinr,
        0,     1,  0,
        -sinr, 0,  cosr);
}


// MAIN ////////////////////////////////////////////////////////////////////////////////////////////////////
void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.yy; // map to center:(0, 0), topright:(aspect/2, 0.5)

    // Define control points
    {
        float py = 0.35;
        float px = -1.;

        _leftA = BezierControl(vec3(px, py,  1), vec3(px, py+.1, 0.5) + vec3(0, 0.2*cos(iTime*1.3 + 0.5), 0)); 
        _leftB = BezierControl(vec3(px, py, -1), vec3(px, py+.1,-0.5) + vec3(0, 0.2*sin(iTime*1.3 + 0.5), 0));

        px = 1.;
        _rightA = BezierControl(vec3(px, py,  1), vec3(px,-.3,-.2) + vec3(0, 0.2*sin(iTime), 0)); 
        _rightB = BezierControl(vec3(px, py, -1), vec3(px, .4,-.2) + vec3(0, 0.2*cos(iTime), 0));
    }


    // Camera
    Ray camera;
    mat3 R = RotateXZ(iTime*0.2 + 0.7);
    camera.Origin = R * vec3(0, 1.3, -3.1);
    camera.Direction = R * normalize(vec3(uv.x, uv.y-0.7, 1.5));


    // Hit scene
    Hit hit = RayMarch(camera);
    vec3 p = camera.Origin + camera.Direction*hit.Dist; // intersection point


    // Direct Light
    vec3 directLight = vec3(0);
    {
        Light l1;
        l1.Pos = vec3(0, 2, 0) + 5.0;
        l1.Color = vec3(1.0,0.6,0.5);
        l1.Intensity = 50.0;
        directLight += hit.Color * GetLight(p, l1);
    }


    // Ambient Light
    vec3 ambient = hit.Color * 0.8*vec3(0.02,0.02,0.08);


    // Compose colour
    vec3 col = ambient + vec3(directLight);


    // Post
    //col *= 1.0; // exposure bias
  	col = ACESFitted(col); // tonemap 
   	col = pow(col, vec3(.454)); // gamma


	fragColor = vec4(col,1.0);
}

