#define time iTime
#define TAU 6.2831853071
#define dt mod(time,TAU)

vec2 hash22 (vec2 x)
{return fract(sin(vec2(dot(x,vec2(12.4,16.4)),dot(x,vec2(23.4,28.37))))*12.4);}

vec3 voronoise (vec2 uv)
{
    vec2 uv_id = floor(uv);
    vec2 uv_cell = fract(uv);
    vec2 bpoint, bneigh, bdiff;
    float bdist = 10.;
    for (int i=-1; i<=1; i++)
    {
        for (int j=-1; j<=1; j++)
        {
            vec2 neigh = vec2(float(i),float(j));
            vec2 point = hash22(uv_id+neigh);
            vec2 diff = neigh + point - uv_cell;
            float dist = length(diff);
            if (dist < bdist)
            {
                bneigh = neigh;
                bpoint = point;
                bdiff = diff;
                bdist = dist;
            }
        }
    }

    bdist = 10.;
    for (int i=-2; i<=2; i++)
    {
        for (int j=-2; j<=2; j++)
        {
            if (i==0 && j==0) continue;
            vec2 neigh = bneigh + vec2(float(i), float (j));
            vec2 point = hash22(uv_id+neigh);
            vec2 diff = neigh + point - uv_cell;
            float dist = dot(0.5*(bdiff+diff),normalize(diff-bdiff));
            bpoint = point;
            bdist = min(dist, bdist);
        }
    }
    return vec3(bpoint,bdist);
}

vec3 voro;
vec2 ground (vec3 p)
{
    voro = voronoise(p.xz);
    float d = abs(p.y)
        - smoothstep(0.01,0.05,voro.z)*0.1; 
    return vec2(d, 1.);
}

vec2 water (vec3 p)
{
    p.y += 0.01;
    float d = abs(p.y)-smoothstep(0.08,0.01-(sin(length(voro.xy*.5)+dt)*0.5+0.5), voro.z)*0.2;
    return vec2 (d,2.);
}

vec2 mat_min (vec2 a, vec2 b)
{
    if (a.x < b.x) return a;
    else return b;
}

vec2 SDF (vec3 p)
{return mat_min(ground(p),water(p));}

vec3 getnorm(vec3 p)
{
    vec2 eps = vec2(0.05,0.);
    return normalize(SDF(p).x-vec3(SDF(p-eps.xyy).x,SDF(p-eps.yxy).x,SDF(p-eps.yyx).x));
}

vec3 getcam (vec3 ro, vec3 tar, vec2 uv)
{
    vec3 f = normalize(tar-ro);
    vec3 l = normalize(cross(vec3(0.,1.,0.),f));
    vec3 u = normalize(cross(f,l));
    return normalize(f + l*uv.x + u*uv.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    vec3 ro = vec3(0.,4.,-3.),
        rd = getcam(ro,vec3(0.),uv),
        p = ro,
        l = normalize(vec3(1.,2., -2.)),
        col = vec3(0.);

    float shad;
    vec2 d;  

    for (float i=0.; i<20.; i++)
    {
        d = SDF(p);
        if (d.x<0.01) break;
        p += d.x*rd*0.3;
    }

    float t = length(ro-p);
    vec3 n = getnorm(p);
    float light = max(dot(n,l),0.);

    if (d.y == 1.) col = mix(vec3(0.2,0.02,0.01),vec3(0.7,0.5,0.3),light);
    else if (d.y == 2.) col = mix(vec3(0.0,0.0,0.08),vec3(0.1,0.7,0.9),light);

        col = mix(col, vec3(0.9,0.8,0.5),1.-exp(-0.008*t*t));

    fragColor = vec4(sqrt(col),1.);
}

