#define R iResolution.xy
#define ss(a, b, t) smoothstep(a, b, t)

float line( in vec2 p, in vec2 a, in vec2 b ){
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}
float box( in vec2 p, in vec2 b ){
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

float dist(vec2 uv){
    float d = 999.;
    uv.y += .07;

    vec2 p0 = vec2(uv.x*.9+sin(uv.y*1.)*.2, .88*uv.y-pow(uv.x, 2.)); // hair
    vec2 p1 = vec2((uv.x-.004)*.813+cos(uv.y*15.)*.01,uv.y+sin(uv.x*15.)*.01)-vec2(0.,-.15); // head
    vec2 p3 = vec2(abs(uv.x)-.16, uv.y+cos(uv.x*32. + 2.)*.008); // eyes
    vec2 p4 = uv-vec2(0., -.48+exp(-uv.x*uv.x)*.3); // mouth

    float hair = abs(length(p0)- (.45-abs(cos(atan(uv.y, uv.x)*7.))*.16))-.02;
    hair = max(hair, -(length(vec2(uv.x*.4, uv.y-.04)-vec2(0., -.5))-.51));

    d = min(abs(length(p1)-.29)-.0086, hair); // head
    d = min(d, min(abs(length(p3)-.03)-.0002,abs(length(p3)-.001)-.001)); // eyes
    d = min(d, abs(box(p4, vec2(.26, .12))) - 0.001); // mouth

    float teeth = 999.;
    for(float i = 0.; i < 10.;i++){
     	teeth = min(teeth, line(uv, vec2(-.25+i*.05+sin(i*2.)*.01, -.5), 
                                vec2(-.25+i*.05+cos(i*5.)*.016, .5)));}
    teeth = max(teeth, box(p4, vec2(.26, .12)));

    return min(d, teeth);
}

void mainImage( out vec4 f, in vec2 u ){
    vec2 uv = 1.3*vec2(u.xy - .5*R.xy)/R.y;
    uv*=1.-pow(cos(length(uv*8.) + iTime)*0.2, 2.);

    vec3 col = vec3(0);

    float d = dist(uv);
    float thk = .008;

    col += .6+.32*cos(vec3(.5, .2, .4)*d*80.-iTime*2.+vec3(4.7, 0., 2.));
    col *= ss(thk, thk+.006, d);

    col *= ss(1.55, .1, length(uv));
    f = vec4(col, 1.);
}

#define E 0.001
#define INF 10000.0
#define MAX_DIS 430.0


vec2 rot( vec2 p, float l ){
	return vec2(
		p.x*cos(l) - p.y*sin(l),
		p.x*sin(l) + p.y*cos(l)
	);
}


float dfCube( vec3 p, vec3 size ){
	p= abs(p) - size;
	// return max( max( p.x, p.y), p.z );
	return length( vec3(
		max( 0.0, p.x),
		max( 0.0, p.y),
		max( 0.0, p.z)
	));
}



int matId= 0;
vec3 lightDis= vec3(INF);


float map(vec3 p){
	float rc= p.z;
	float r= rc;
	float h;
	matId= 0;
	lightDis= vec3(INF);
	vec3 ph, ph2;

	/// lamps
	ph= p;
	ph.x= abs(ph.x) - 6.2;
	ph.y = mod( ph.y, 4.0 ) - 2.0;
	rc= length(ph.xy)-0.1;
	ph.z -= 3.0;
	rc= max( rc, ph.z );
	rc= min( rc, length(ph)-0.2 );
	lightDis= min( lightDis, vec3(length(ph)-0.1));
	if( rc < r ){
		r= rc;
		matId= 1;
	}

	/// pavement
	ph= p;
	ph.x= abs(ph.x) - 6.0;
	ph.z -= 0.2;
	rc= length( vec2(
		max( 0.0, -ph.x),
		max( 0.0, ph.z)
	));
	if( rc < r ){
		r= rc;
		matId= 1;
	}

	/// building
	ph= p;
	float seed= floor( p.y / 30.0 ) * sign(p.x);
	ph.x= abs(ph.x) - 20.0;
	ph.y= mod( ph.y, 30.0 ) - 15.0;
	rc= dfCube( ph, vec3(12.0) );
	float buildingHeight= 6.0 + 3.0 * floor( 3.0*( 0.5 + 0.5 * sin(10.*seed) ) );
	rc= max( rc, p.z - buildingHeight );
	rc= min( rc,  max( 1.0, max( ph.x-12.0, ph.y-12.0 ) ) );
	if( rc < r ){
		r= rc;
		matId= 10 + int( floor( mod( seed, 6.999 ) ) );
	}
	/// door
	ph2= ph;
	ph2.x += 12.0;
	ph2.y += 11.0*sin( 7.0*seed );
	rc= dfCube( ph2, vec3(0.1, 0.7, 2.5) );
	if( mod(seed,5.0) < 3.0 ){
		float doory= ph2.y;
		/// window
		ph2= ph;
		ph2.x += 12.0;
		ph2.y += mod( 11.0*sin( 7.0*seed ) + 11.0 + 11.0, 22.0 ) - 11.0;
		ph2.z -= 2.0;
		rc= min( rc, dfCube( ph2, vec3(0.1, 0.7, 0.8) ) );
		if( mod(17.0*seed,23.0) < 15.0 )
		{
			ph2.z = mod( p.z, 3.0 )-1.5;
			ph2.z = max( ph2.z, p.z + 1.5 - buildingHeight );
			rc= min( rc, dfCube( ph2, vec3(0.1, 0.7, 0.8) ) );
			ph2.y = doory;
			rc= min( rc, dfCube( ph2, vec3(0.1, 0.7, 0.8) ) );
		}
	}
	if( rc < r ){
		r= rc;
		matId= 3;
	}

	/// extra stone elements
	rc= INF;
	/// corner
	h= mod(27.0*seed,29.0);
	if( h < 10.0 ){
		ph2= ph;
		ph2.x= abs(ph2.x)-12.0;
		ph2.y= abs(ph2.y)-12.0;
		ph2.z = mod( ph2.z, 0.8 ) - 0.4;
		rc= min( rc, dfCube( ph2+vec3(.2,.2,.0), vec3(.3,.3,.25) ) );
	}else if( h < 20.0 ){

		ph2= ph;
		ph2.x= abs(ph2.x)-12.0;
		ph2.y= abs(ph2.y)-12.0;

		// ph2.z -= 4.0;
		ph2.z = mod( ph2.z, 3.0 ) - 1.0;

		ph2.x += 0.1;
		ph2.y += 0.1;
		rc= min( rc, dfCube( ph2, vec3(.15,.15,.12) ) - 0.03 );

		ph2.x += 0.05;
		ph2.y += 0.05;
		ph2.z -= 0.5;
		rc= min( rc, dfCube( ph2, vec3(.19,.19,.14) ) - 0.03 );

		ph2.x += 0.7;
		ph2.y += 0.15;
		ph2.z += 0.1;
		rc= min( rc, dfCube( ph2-vec3(.15,.15,.0), vec3(.19,.19,.12) ) - 0.03 );

		ph2.x -= 0.7;
		ph2.y += 0.5;
		ph2.z += 0.1;
		rc= min( rc, dfCube( ph2, vec3(.19,.19,.17) ) - 0.03 );

	}
	/// roof
	if( mod(13.0*seed,17.0) < 12.0 ){
		ph2= ph;
		ph2.z -= buildingHeight;
		if( mod(13.0*seed,17.0) < 5.0 ){
			ph2.z = mod( ph2.z+1.5, 3.0 )-1.5;
		}
		rc= min( rc, dfCube( ph2, vec3( 12.1, 12.1, 0.1 ) ) );
	}
	/// cut above building height
	rc= max( rc, p.z - buildingHeight );
	if( rc < r ){
		r= rc;
		matId= 2;
	}

	return r;
}


vec3 render( vec3 sp, vec3 dir ){
	vec3 p= sp;
	float dis= map(p);
	float t= 0.0;
	bool isHit= false;
	vec3 lightDisFromRay= vec3(INF);
	for( int a=0;a<0x200;a++){
		t += dis;
		p= sp + t*dir;
		dis= map(p);
		lightDisFromRay= min( lightDisFromRay, lightDis );
		if( dis < E || MAX_DIS < t ){
			isHit= true;
			break;
		}
	}

	int hitMatId= matId;
	vec3 light= lightDis;

	vec3 r;
	if( t < MAX_DIS ){

		//vec3 nor= normalize(vec3(
		//	map(vec3(p.x+E,p.y,p.z))-map(vec3(p.x-E,p.y,p.z)),
		//	map(vec3(p.x,p.y+E,p.z))-map(vec3(p.x,p.y-E,p.z)),
		//	map(vec3(p.x,p.y,p.z+E))-map(vec3(p.x,p.y,p.z-E))
		//));
  		vec3 nor= vec3( 0 );
		for( int i=min( iFrame, 0 ); i<4; i++){
			vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
			nor += e * map( p + e*E );
		}
        nor= normalize( nor );


		float ao= 1.0;
		for( float a=0.1; a>0.01; a*=0.5 ){
			ao= min( ao, map(p+nor*a) / a );
		}
		ao= clamp( ao, 0.0, 1.0 );

		vec3 baseColor;
		switch(hitMatId){
			/// street
			case 0: {
				baseColor= vec3(.5,.5,.7); 
				if( abs( p.x ) < 0.05 && mod(p.y,4.0) < 2.0 ){
					baseColor= vec3(.9); 
				}
			} break;
			/// pavement
			case 1: baseColor= vec3(.5); break;
			/// stone (roof etc)
			case 2: baseColor= vec3(.65); break;
			/// doors
			case 3: baseColor= vec3(.55,.6,.1); break;
			/// buildings
			case 10: baseColor= vec3(.9,.9,.6); break;
			case 11: baseColor= vec3(.6,.6,.6); break;
			case 12: baseColor= vec3(.7,.9,.6); break;
			case 13: baseColor= vec3(.8,.7,.6); break;
			case 14: baseColor= vec3(.2,.3,.4); break;
			case 15: baseColor= vec3(.6,.6,.1); break;
			case 16: baseColor= vec3(.3,.3,.1); break;
			case 17: baseColor= vec3(.1,.1,.8); break;
			/// other
			default: baseColor= vec3(1,0,0); break;
		}

		r= baseColor *
			( 
				(1.0 / (1.0 + 1.0 * light) )
				+ 1.0
				* ( 0.5 + 0.5*dot(nor,vec3(0,0,1)) )
				* ( 0.5 + 0.5*dot(nor,-dir) )
				* ( 0.5 + 0.5*ao )
				* ( 1.0 / (1.0 + 0.0001*t*t) )
			)
		;

	}else{
		/// sky
		r= vec3(0);
	}

	/// add light
	r += vec3(1) / (1.0+2.0*lightDisFromRay);
	return r;
}

