#define maxstep 100
#define maxl 100.
#define minsr 0.01

float makeface(vec2 uv){
    vec2 eye = vec2(.15,.02);
	float deye = smoothstep(.04,.034,length(eye-vec2(abs(uv.x*1.5),uv.y*.8)));
    float dmt =	smoothstep(.01,.01,(abs(7.*uv.x)-.5)*abs(7.*uv.x)-4.*uv.y-.13),
        dmb = smoothstep(.05,.01,(abs(7.*uv.x)-.5)*abs(7.*uv.x)-4.*uv.y-.15),
        dmouse = min(dmt,dmb);
        dmouse = min(dmouse,step(abs(uv.x),.06));


    return clamp(dmouse+deye,0.,1.);
}
float sm(float a,float b,float k)
{
    float h = clamp(.5+.5*(b-a)/k,0.,1.);
    return h*a+(1.-h)*b-h*(1.-h)*k;
}
float GetDist(vec3 p){


 vec2 offs= vec2(0);
 float ft =maxl
     ,s=1.;

    float angle = 6.29/20.;
	  for(int i = 0;i<20;i++){
        offs = vec2(cos(s*angle+iTime*1.5),sin(s*angle+iTime*1.5))*1.9;
    ft = min(length(p+vec3(0,.3,0)+vec3(offs.x,0,offs.y))-.35,ft);

       s+=1.;

   }
	float sf = length(vec3(p.x,p.y*2.,p.z)+vec3(0.,-1.,0.))/2.-1.0;
 float d = sm(ft,sf,.2);
    return d;

}

float RayMarch(vec3 ro ,vec3 rd){
	float ds,d0;
    vec3 p;
    for(int i = 0;i<maxstep;i++){
    	p = ro+rd*d0;
        ds = GetDist(p);
        if(d0>maxl || ds<minsr ) break;
        d0 += ds;
    }
	return d0;
}


vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.01, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p,vec3 lightPos ) {


    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);

    float d = RayMarch(p+n*minsr*2., l);
    if(d<length(lightPos-p)) dif *= .1;

    return dif;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;


    vec3 col = vec3(0.1 + 0.1*uv.y);

    float zoom = 1.0;
    vec3 ro = vec3(0.,3.,6.)
        +vec3(20.*(iMouse.x-.5*iResolution.x)/iResolution.x,0,-.5*(iMouse.y-.5*iResolution.y)/iResolution.y*10.),

        lookat = vec3(0.,0.,0.),
        f = normalize(lookat - ro),
        r = cross(vec3(0.,1.,0.),f),
        u = cross(f,r),
        c = ro+f*zoom,
        i = c+uv.x*r+uv.y*u,
        rd = normalize(i-ro);

    float d = RayMarch(ro,rd);
    vec3 p = ro+rd*d;
    vec3 l = vec3(2., 5., 4.);
    float dif = GetLight(p,l);
    float toon = smoothstep(.01,.2,dif);
	float  tex = smoothstep(.8,.79,p.y+.27);
    vec3 n = GetNormal(p);
    float rim = smoothstep(.6,.1,-dot(n,f));
    float gx = atan(p.x*.4,p.z);
    float gy = (p.y-.6)*.3;
    float face = makeface(vec2(gx,gy));

    col += clamp(
        		(vec3(.5,.5,.8)*(1.-tex)
                 +vec3(.5,.1,.3)*tex
                 +dif*.5
                 )*vec3(clamp(toon,.7,1.),clamp(toon,.5,1.),clamp(toon,.8,1.))
                 -step(50.,length(p-ro))*10.
                 +rim
                 -face*1.
                 ,0.,1.);


    fragColor = vec4(col,1.);
}

