//hash functions from https://www.shadertoy.com/view/4djSRW
vec2 hash22(vec2 p)
{
    p *= 733.424;
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec3 hash33(vec3 p3)
{
    p3 *= 733.424;
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float hash13(vec3 p3)
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}


vec3 voronoi2d(vec2 p, float s) {
    vec2 gv = fract(p)-.5;
	vec2 iv = floor(p);
    vec2 id;

    vec2 o;
    float res = 8.;

    for(int y=-1; y<=1; y++)
	for(int x=-1; x<=1; x++)
    {
        o = vec2(x, y);

        vec2 n = hash22(iv+o);
        vec2 p = o+.5*sin(t*n);
        float d = dot(gv-p, gv-p)/s;

        if(hash12(n)>.5 ? d<1. : 1.<res) {
            res = d;
            id = iv+p;
        }
    }
    res = sqrt(res);
    return vec3(res, id*float(res<1.) );
}

vec4 voronoi3d(vec3 p, float s) {
    vec3 gv = fract(p)-.5;
	vec3 iv = floor(p);
    vec3 id;

    vec3 o;
    float res = 8.;

    for(int z=-1; z<=1; z++)
    for(int y=-1; y<=1; y++)
	for(int x=-1; x<=1; x++)
    {
        vec3 o = vec3(x, y, z);

        vec3 n = hash33(iv+o);
        vec3 p = o+.5*sin(t*n);
        float d = dot(gv-p, gv-p)/s;

        if(hash13(n)>.5 ? d<1. : 1.<res) {
            res = d;
            id = iv+p;
        }
    }
	res = sqrt(res);
    return vec4(res, id*float(res<1.) );
}

float getCol(vec2 coord) {
	vec2 R = iResolution.xy;
    vec2 uv = (coord-.5*R.xy)/R.y;
    float x = coord.x/R.x;
	uv *= 8.;

    float size = 1.;

    float v = voronoi2d(uv, size).x;

    if(x<.5)
    	v = step(.9, v)*float(v<1.);
    else
    	v = voronoi3d(vec3(uv, 0), size).w*.5+.5;

    return screen(v, s(.0025, .2, x ) );
}

