    float x = uv.x;
	float y = uv.y;
    float cx = cv.x;
	float cy = cv.y;
    // create concentric circles from the centre of the screen
    sv = sin(sqrt(100.0 * ((x * x) + (y * y))) + 1.0 + t);
    // use dynamic vector to create interference pattern by moving centre
    dv = sin(sqrt(100.0 * ((cx * cx) + (cy * cy))) + 1.0 + t);
    return vec2(sv, dv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 10.; // zoom out
    vec2 ov = uv; // create copy of vector
    uv.x += 5.*sin((iTime/1.)/5.0);                    // dynamic x movement
    uv.y += 7. * cos((iTime/1.)/3.0);                    // dynamic y movement
	uv *= .5 * (2.+(1.*sin(2.*pi*.04*iTime)/10.));  // dynamic zoom
    uv *= rot(22.5+(180.*sin(2.*pi*.01*iTime)));    // dynamic rotation
    float t = iTime;
    vec2 v = calc(uv,ov,t);
	vec3 hole = colormap(v);

    fragColor = vec4(hole,1.0);

}

struct Vertex
{
    vec4 Pos;
};


// Test a position against a triangle and return
// the non-perspective-correct barycentric coordinates in the triangle
// Vertex z and w values are ignored.
vec2 BaryTri2D ( vec2 pos, Vertex v1, Vertex v2, Vertex v3 )
{
    vec2 posv1 = pos - vec2(v1.Pos);

    vec2 v21 = vec2(v2.Pos) - vec2(v1.Pos);
    vec2 v31 = vec2(v3.Pos) - vec2(v1.Pos);

    float scale = v21.x * v31.y - v21.y * v31.x;
    float rscale = 1.0 / scale;
    float baryi = ( posv1.x * v31.y - posv1.y * v31.x ) * rscale;   
    float baryj = ( posv1.x * v21.y - posv1.y * v21.x ) * -rscale;

    return vec2 ( baryi, baryj );
}


