void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    Vertex v1 = Vertex (vec4(0.5, 0.5, 0.0, 0.5));
    Vertex v2 = Vertex (vec4(0.9, 0.6, 0.0, 0.8));
    Vertex v3 = Vertex (vec4(0.4, 0.8, 0.0, 0.9));

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    col.xy = uv.xy;
    col.z = 0.0;

    int rep = 0;
    // Uncomment this line for "benchmarking"
    //for ( rep = 0; rep < 5000; rep++ )
    {
        float angle = iTime + 0.1 * float(rep);
        v1.Pos.xy = 0.5 + 0.2 * vec2 ( cos ( angle ), sin ( angle ) );
        v2.Pos.xy = 0.5 + 0.2 * vec2 ( sin ( angle ), -cos ( angle ) );

        vec2 bary = BaryTri3D ( uv, v1, v2, v3 );
        if ( ( bary.x >= 0.0 ) &&
             ( bary.y >= 0.0 ) &&
             ( bary.x + bary.y < 1.0 ) )
        {
            col.xy = bary;
            col.z = 1.0;
            if ( fract ( bary.x * 4.0 ) < 0.05 ) { col.z = 0.0; }
            if ( fract ( bary.y * 4.0 ) < 0.05 ) { col.z = 0.0; }
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

