// A simple generaliztion of sylvain69780's shader "Moss's EGG":
// https://www.shadertoy.com/view/wsBBR3
//
// A more powerful generalization is possible where the height of the egg
// is also user specified, and the tangent/contact points are computed
// instead of hardcoded.
//
// This is also a special case of the Vesica primitive, in particular
// this egg is just half a vesica with a circle attached at its end
// https://www.shadertoy.com/view/XtVfRW

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and http://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm



float sdEgg( in vec2 p, in float ra, in float rb )
{
    const float k = sqrt(3.0);

    p.x = abs(p.x);

    float r = ra - rb;

    return ((p.y<0.0)       ? length(vec2(p.x,  p.y    )) - r :
            (k*(p.x+r)<p.y) ? length(vec2(p.x,  p.y-k*r)) :
                              length(vec2(p.x+r,p.y    )) - 2.0*r) - rb;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    // animation
    float ra = 0.6;
    float rb = ra*(0.55+0.45*cos(2.0*iTime));

    // distance
    float d = sdEgg(p+vec2(0.,0.2), ra, rb);

    // coloring
    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-4.0*abs(d));
	col *= 0.8 + 0.2*cos(128.0*abs(d));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = sdEgg(m+vec2(0.,0.2), ra, rb);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(0.5,0.5);

	vec4 col = vec4(0);
    float thickness = 5.0/iResolution.y;

    for(float k=0.0;k<1.0;k+=0.05){
    	if(abs(uv.y - (k-0.5)*sin(uv.x*10.0 + 10.0*(k-0.5)*iTime))<thickness*(k+0.1))
        	col=vec4(1.0,cos(3.14*(1.0-k)/2.0),cos(k*3.14/2.0),1.0);
    }

    // Output to screen
    fragColor = col;
}

#define MAX_STEPS 100
#define MAX_DIST 500.
#define SURF_DIST 0.05

const float rep = 0.5; //change to increase the number of threads on the sphere

float sdCylinderYZ( vec3 p, vec3 c )
{
    return length(p.yz-c.xy)-c.z;
}

float sdCylinderXY( vec3 p, vec3 c)
{
    return length(p.xy-c.xy)-c.z;
}

float twistX(vec3 p, float offsetZ, float offsetY) {
    //twisting coordinates
    const float k = 1.0; 
    vec3 q = vec3(p.x, p.y-offsetY, p.z-offsetZ);
    float c = cos(k*q.x);
    float s = sin(k*q.x);
    mat2 m = mat2(c,-s,s,c);
    q = vec3(q.x, m*q.yz);

    //adding ridges
    float dist = 0.1 * (0.3*sin(16.0*atan( q.z, q.y )));
    return dist;
}

float twistZ(vec3 p, float offsetX, float offsetY) {
    //twisting coordinates
    const float k = 1.0; 
    vec3 q = vec3(p.x-offsetX, p.y-offsetY, p.z);
    float c = cos(k*q.z);
    float s = sin(k*q.z);
    mat2 m = mat2(c,-s,s,c);
    q = vec3(m*q.xy, q.z);

    //adding ridges
    float dist = 0.1 * (0.3*sin(16.0*atan( q.x, q.y )));
    return dist;
}

vec3 wrapSphere(vec3 p, vec3 s, float r) {
    vec3 n = normalize(p-s);
    float nx = r * acos( dot(normalize(vec3(n.x, 0, n.z)), vec3(0,0,1)) )*rep;
    float nz = r * acos( dot(n, vec3(0,1,0)) )*rep;
    vec3 newP = vec3(nx, (length(p-s)-r)*rep, nz);
    return newP;
}

mat2 rotate(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

float getDist(vec3 p) {

    //Defining objects and distances to them

    //rotation
    p.z -= 60.;
    p.xz *= rotate(iTime*0.1);
    p.z += 60.;

    vec3 sDist = wrapSphere(p, vec3(0, 0, 60), 30.);
    vec3 repDist = vec3(mod(sDist.x, 3.14), sDist.y, mod(sDist.z, 5.));

    float dst = sin(0.1*(iTime*25.+p.y)) *rep;

    float rad1 = twistX(repDist, 2.0, (0.15 - sin(sDist.x + 1.7) + dst));
    float rad2 = twistX(repDist, 4.0, (0.15 - sin(sDist.x + 4.84) + dst));
    float rad3 = twistZ(repDist, 0.0, 0.0 + dst);
    float rad4 = twistZ(repDist, 3.14, 0.0 + dst);

    float thread1 = sdCylinderYZ(repDist, vec3(0.15 - sin(sDist.x + 1.7)+dst, 2., 0.5 - rad1));
    float thread2 = sdCylinderYZ(repDist, vec3(0.15 - sin(sDist.x + 4.84)+dst, 4., 0.5 - rad2));
    float thread3 = sdCylinderXY(repDist, vec3(0, 0.15+dst, 0.5-rad3));
    float thread4 = sdCylinderXY(repDist, vec3(3.14, 0.15+dst, 0.5-rad4));

    float threads = min(thread1, min(thread2, min(thread3, thread4)));

    //float plane = p.y+2.;

    float d = threads/rep;
    return d;
}

float rayMarch(vec3 ro, vec3 rd) {
    float dO = 0.;

    for(int i = 0; i < MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = getDist(p);
        dO += dS;
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }

    return dO;
}

vec3 getNormal(vec3 p) {
    float d = getDist(p);
    vec2 e = vec2(0.1, 0);

    vec3 n = d - vec3(
        getDist(p-e.xyy),
        getDist(p-e.yxy),
        getDist(p-e.yyx)
    );

    return normalize(n);
}

vec3 skyColor( in vec3 ro, in vec3 rd ) {
    vec3 col = vec3(0.3,0.4,0.5)*0.9 - 0.275*rd.y;

    return col;
}

float ambientOcclusion(vec3 p, vec3 n) {
    float step = 0.1;
    float ao = 0.0;
    int iter = 3;
    float intensity = 0.3;
    float dist;
    for(int i = 1; i <= iter; i++) {
        dist = step * float(i);
        ao += max(0.0, (dist - getDist(p + n * dist)) / dist);
    }
    return 1.0 - ao * intensity;
}

float getLight(vec3 p, vec3 lightPos) {

    vec3 l = normalize(lightPos - p);
    vec3 n = getNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);

    //shadow
    float d = rayMarch(p+n * SURF_DIST * 2. , l);
    if(d<length(lightPos-p)) dif *= .1;

    //ao
    dif *= ambientOcclusion(p, n);

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((gl_FragCoord.xy-.5*iResolution.xy)/iResolution.y) * 2.0;
    vec3 col = vec3(0);

    //Camera
    vec3 rOrigin = vec3(0., 0., 10.);
    vec3 rDirection = normalize(vec3(uv.x, uv.y, 1));

    float dist = rayMarch(rOrigin, rDirection);
    vec3 p = rOrigin + rDirection * dist;
    float dif = getLight(p, vec3(75, 20, 0));
    float amb = getLight(p, vec3(-75, 20, 0));

    col = skyColor(rOrigin, rDirection);
    if(dist < 100.) {
        col = vec3(1.,0.95,0.85)*dif + vec3(0.3,0.4,0.5)*0.5*amb;
    }
    fragColor = vec4(col, 1.0);
}

const float FOVY = 3.14159 * 0.25;
const int RAY_STEPS = 256;
const float PI = 3.14159;
const float TWO_PI = 3.14159 * 2.0;
const vec3 matteWhite = vec3(0.85, 0.81, 0.78);
const vec3 matteRed = vec3(0.63, 0.065, 0.05);
const vec3 matteGreen = vec3(0.14, 0.45, 0.091);
const float SHADOW_HARDNESS = 6.0;

// Adjust these to alter where the subsurface glow shines through and how brightly
const float K_VAL = 2.0;
const float OCC_DIST = 0.085;
// The larger the DISTORTION, the smaller the glow
const float DISTORTION = 0.2;
// The higher GLOW is, the smaller the glow of the subsurface scattering
const float GLOW = 6.0;
// The higher the BSSRDF_SCALE, the brighter the scattered light
const float BSSRDF_SCALE = 3.0;
// Boost the shadowed areas in the subsurface glow with this
const float AMBIENT = 0.0;

//Intersection
struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object; // unique ID for every object
};    

// Box with side lengths b
float cube(vec3 p, vec3 b)
{
  return length(max(abs(p) - b, 0.0));
}

// SquarePlane SDF
float plane(vec3 p, vec4 n)
{
    n = normalize(n);
    return dot(p, n.xyz) + n.w;
}

// Sphere with radius r at center c
float sphere(vec3 p, float r, vec3 c)
{
    return distance(p, c) - r;
}

#define BACK_WALL 0
#define LEFT_WALL 1
#define RIGHT_WALL 2
#define FLOOR 3
#define SPHERE1 4
#define SHORT_CUBE 5
#define BIG_CUBE 6
#define LIGHT 7
#define BACK_WALL_SDF plane(pos, vec4(0.0, 0.0, -1.0, 15.0))
#define LEFT_WALL_SDF plane(pos, vec4(1.0, 0.0, 0.0, 5.0))
#define RIGHT_WALL_SDF plane(pos, vec4(-1.0, 0.0, 0.0, 5.0))
#define CEILING_SDF plane(pos, vec4(0.0, -1.0, 0.0, 7.5))
#define FLOOR_SDF plane(pos, vec4(0.0, 1.0, 0.0, 4.0))
#define SPHERE1_SDF sphere(rotateY(pos, 15.0 * 3.14159 / 180.0), 4.5, vec3(-7.0, 3.0, -1.0))
#define SHORT_CUBE_SDF cube(rotateY(pos + vec3(-2, 1.5, -0.75), -17.5 * 3.14159 / 180.0), vec3(2.5, 8.0, 2.5))

vec3 rotateY(vec3 p, float a)
{
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);   
}

float shadowMap3D(vec3 pos)
{
    float t = FLOOR_SDF;

    float t2;
    if ((t2 = SPHERE1_SDF) < t) {
        t = t2;
    }
    if ((t2 = SHORT_CUBE_SDF) < t) {
        t = t2;
    }
    return t;
}

// function to create whole scene
void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos)
{
    t = FLOOR_SDF;
    obj = FLOOR;

    float t2;
    if ((t2 = SPHERE1_SDF) < t) {
        t = t2;
        obj = SPHERE1;
    }
    if ((t2 = SHORT_CUBE_SDF) < t) {
        t = t2;
        obj = SHORT_CUBE;
    }
    if((t2 = sphere(pos, 2.0, lightPos)) < t) {
        t = t2;
        obj = LIGHT;
    }
}

float sceneMap3D(vec3 pos, vec3 lightPos)
{
    float t = sphere(pos, 2.0, lightPos);

    float t2;
    if ((t2 = FLOOR_SDF) < t) {
        t = t2;
    }
    if ((t2 = SPHERE1_SDF) < t) {
        t = t2;
    }
    if ((t2 = SHORT_CUBE_SDF) < t) {
        t = t2;
    }
    return t;
}

float fiveTapAO(vec3 p, vec3 n, float k) {
    float aoSum = 0.0;
    for(float i = 0.0; i < 5.0; ++i) {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * OCC_DIST - shadowMap3D(p + n * i * OCC_DIST));
    }
    return 1.0 - k * aoSum;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = shadowMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thickness) {
    vec3 scatteredLightDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, -scatteredLightDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
    float attenuation = 1.0;
    attenuation = max(0.0, dot(normal, lightDir) + dot(viewVec, -lightDir));
    float totalLight = attenuation * (lightReachingEye + AMBIENT) * thickness;
    return totalLight;
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos)
{
    t = 0.001;
    for (int i = 0; i < RAY_STEPS; ++i) {
        vec3 pos = origin + t * dir;
        float m;
        sceneMap3D(pos, m, hitObj, lightPos);
        if (m < 0.001) {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view, float thick)
{
    float t;
    switch(hitObj) {
        case FLOOR:
        return matteWhite * max(0.0, dot(n, lightVec)) * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        break;
        case SPHERE1:
        vec3 sColor = vec3(0.85, 0.41, 0.2) * max(0.0, dot(n, lightVec)) * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        return sColor + vec3(0.85, 0.41, 0.2) * subsurface(lightVec, n, view, thick);
        break;
        case SHORT_CUBE:
        vec3 cColor = vec3(0.1, 0.31, 0.6) * max(0.0, dot(n, lightVec)) * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        return cColor + vec3(0.1, 0.31, 0.6) * subsurface(lightVec, n, view, thick);
        break;
        case LIGHT:
        return vec3(1.0, 0.88, 0.7);
        case -1:
        return vec3(0, 0, 0);
        break;
    }
    return vec3(0, 0, 0);
}

vec3 computeNormal(vec3 pos, vec3 lightPos)
{
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize(vec3(sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                         sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                         sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}

Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + (t * dir);
    vec3 nor = computeNormal(isect, lightPos);

    vec3 lightDir = normalize(lightPos - isect);
    float thickness = fiveTapAO(isect, -nor, K_VAL);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect), thickness);

    return Intersection(t, surfaceColor, isect, hitObj);
}

// Returns direction of ray
vec3 rayCast(vec3 eye, vec3 ref, vec2 ndc)
{
    vec3 F = ref - eye;
    vec3 R = normalize(cross(vec3(0, 1, 0), F));
    vec3 U = normalize(cross(R, -F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V;

    return normalize(p - eye);
}

