//
// Ray tracing a cone, showing effect of rounding errors on quadratic solution.
// See https://www.shadertoy.com/view/WdXfR2 for similar problems with sphere.
//
// Errors are particularly pronounced at cone apex, even at smaller distances,
// can be significantly reduced by moving the projection point closer to the
// surface (compare LHS of image with RHS, zoom in with 'z').
//
// Despite shader name, it also does hyperboloids and spheres (illustrating
// another, possibly GPU dependent, source of numeric error).
//
// Matthew Arcus, mla, 2020.
//
// <mouse>: rotate cone
// b: background
// c: clip cone
// h: hyperboloid
// q: quality
// x: sphere
// z: extra zoom in
//
////////////////////////////////////////////////////////////////////////////////

const float PI = 3.1415927;

int quality = 0;

// NB: This solves Ax^2 + 2Bx + C = 0 (with factor of 2 on B)
bool quadratic(float A, float B, float C, out vec2 t) {
  // Sometimes should also check for A = 0 or C = 0
  float D = B*B-A*C;
  if (D < 0.0) return false;
  float x,y;
  if (true) {
    // The wrong way, but doesn't seem to cause problems
    // with this shader.
    x = (-B-sqrt(D))/A;
    y = (-B+sqrt(D))/A;
  } else {
    // The right way
    x = B <= 0.0 ? -B-sqrt(D) : -B+sqrt(D);
    y = C/x;
    x /= A;
  }
  // Return solutions in order
  t = x < y ? vec2(x,y) : vec2(y,x);
  return true;
}

bool dohyperboloid = false;
bool doclip = true;
bool dosphere = true;

int checkcone(vec3 p) {
  float height = dohyperboloid ? 2.0 : 1.0;//exp(iTime);
  if (doclip && abs(p.y) > height) return 0;
  float N = 16.0;
  float i = floor(N*(atan(p.z,p.x))/PI);
  float j = floor(N*p.y/height);
  return int(mod(i+j,2.0));
}

int intersectcone(vec3 p, vec3 r, out vec3 n) {
  float k = 0.0;
  if (quality == 1) {
    // Move projection point to be orthogonal to cone apex.
    k = -dot(p,r); p += k*r;
  }
  // Quadric equation is: (p*X).p = 0
  // So solve: ((p+tr)*X).(p+tr) = 0
  vec3 X = dosphere ? vec3(1) : vec3(1,-1,1);
  // Since r is normalized, can take a shortcut for a sphere.
  float A = dosphere ? 1.0 : dot(r*X,r);
  float B = dot(p*X,r);
  float C = dot(p*X,p);
  if (dosphere) C -= 1.0;
  else if (dohyperboloid) C += sin(0.2*iTime);// Hyperboloid
  vec2 t;
  if (!quadratic(A,B,C,t)) return 0;
  int type;
  vec3 q;
#if 0
  // Do people prefer assignments in conditionals,
  if (!(t.x+k >= 0.0 && (type = checkcone(q = p+t.x*r)) != 0) &&
      !(t.y+k >= 0.0 && (type = checkcone(q = p+t.y*r)) != 0)) {
    return 0;
  }
#elif 0
  // or using a one-iteration loop and break/return?
  for(;;) {
    if (t.x+k >= 0.0) {
      q = p+t.x*r;
      type = checkcone(q);
      if (type != 0) break;
    }
    if (t.y+k >= 0.0) {
      q = p+t.y*r;
      type = checkcone(q);
      if (type != 0) break;
    }
    return 0;
#else
  // Or just this?
  type = 0;
  if (t.x+k >= 0.0) {
     q = p+t.x*r;
     type = checkcone(q);
  }
  if (type == 0 && t.y+k >= 0.0) {
     q = p+t.y*r;
     type = checkcone(q);
  }
  if (type == 0) return 0;
#endif

  n = normalize(q*X);
  if (dot(n,r) > 0.0) n = -n;
  return type;
}

int intersectscene(vec3 p, vec3 r, out vec3 n) {
  return intersectcone(p,r,n);
}

vec3 getcolor(vec3 p) {
  return abs(p);
}

vec3 raycolor(vec3 p, vec3 r, vec3 light) {
  vec3 n;
  int type = intersectscene(p,r,n);
  if (type == 0) {
    return vec3(0);
  } else {
    float ambient = 0.3;
    float diffuse = 0.3;
    vec3 basecolor = getcolor(n);
    if (type == 2) basecolor *= 0.8;
    vec3 color = basecolor*(ambient+diffuse*max(0.0,dot(light,n)));
    float specular = pow(max(0.0,dot(reflect(light,n),r)),5.0);
    //vec3 speccol = vec3(1);
    vec3 speccol = basecolor;
    color += 0.5*specular*speccol;
    return color;
  }
}

