float shadow(vec3 r0, vec3 rd, float maxDist)
{
 float d = .1;
 float shadow = 1.0;
 while(d < maxDist)
 {
  float t = GetDist(r0 + d * rd).x;
  if(t < 0.001) return 0.0;
  d += t;
  shadow = min(shadow,5.0 * (t / d));
 }
 return shadow;
}

