    float isWithinTrapezoid = 0.0;
    float mask = 0.0;
    float mask1 = 0.0;
    float mask2 = 0.0;
    float m = 0.0;
    float dropOffset = 0.5;

    // black bottom
    modP = vec2(p.x, p.y);
