float noise(vec2 p) {
    float a = sin(p.x * 1234.456) * cos(p.y * 5678.1234) * 999.0;
    float b = cos(p.x * 2355.234) * sin(p.y * 3534.3465) * 456.0;
    return fract(a + b);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float scale = 0.5;
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.y;
    float time = mod(iTime, 6.35);
    vec2 mouse = (iMouse.xy - 0.5 * iResolution.xy)/iResolution.y;
    vec2 center = vec2(0);
    vec2 follow = mix(center, vec2(mouse.x, 0.0), clamp(iMouse.w, 0., 1.));

    uv *= scale;
    follow *= scale;
    vec3 col = vec3(0);

    float points = mix(noise(uv), noise(uv + iTime * 0.000001), 0.01);
    float focus = clamp(length(uv.xx - follow + vec2(sin(time), cos(time)) * 0.01), 0.0, 1.0);
    float waves = pow((1.0 - focus) * sin(pow(focus, 0.5) * 200.0 - time) * 0.5 + 0.5, 1.5);
    float mask = smoothstep(0.0, 0.0001, smoothstep(0.2, 0.0, pow(focus + waves * 0.05, 0.8) * points + focus * 0.15));

    col = mix(vec3(0.2, 0.0, 0.1), vec3(0.9, 0.9, 0.8), mask);
    fragColor = vec4(col,1.0);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}


vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 2.*(2.*fragCoord-iResolution.xy)/iResolution.y;
    float pi = 3.14159265359;
    float p = 1.+sin(iTime)*.05;
    u *= p;
    vec2 v = u;
    float e = -4.;
    for(float i = 0.; i < 3.; ++i)
    {
        //u = sin(atan(u.y,u.x)*e+vec2(0.,pi*.5))*pow(length(u),-1.+4./(e+1.));
        float ttt = (1.+0.1*sin(iTime))*pi;
        vec2 ooo = vec2(pi*.5*sin(ttt),pi*.5*cos(ttt));
        u = sin(atan(u.y,u.x)*e+ooo)*pow(length(u),-1.+4./(e+1.));
        //u = sin(atan(u.y,u.x)*e+vec2(pi*.5*sin(iTime),pi*.5*cos(iTime)))*pow(length(u),-1.+4./(e+1.));
        u+= v/p;
    }

//  vec3 hsl = vec3(1.+.5*sin(iTime+length(u)), 1.+.5*cos(iTime+length(u)), exp(dot(u,u)*-.1));
    vec3 hsl = vec3(.5+.5*sin(iTime+length(u)), .5+.5*cos(iTime+length(u)), exp(dot(u,u)*-.1));
    vec3 rgb = hsv2rgb(hsl);
    fragColor = vec4(rgb, 1.0);

}

#define SIZE 8.0
vec2 hash22(vec2 p)
{
    p = vec2 (
        dot(p, vec2(127.1, 311.7)),
        dot(p, vec2(269.5, 183.3))
    );
    return -1.0 + 2.0 * fract(sin(p) * 43758.5453123);
}

float wnoise(vec2 p, float time)
{
    vec2 pi = floor(p);
    vec2 pf = p - pi;
    float minDistance = 5.0;

    //ä¹å®«æ ¼ä¸­åæå°è·ç¦»
    for(int i = -1; i <= 1; i++)
    {
    	for(int j = -1; j <=1; j++)
        {
            vec2 offset = vec2(i, j);
            vec2 rand = hash22(pi + offset);
           	vec2 move = 0.5 + 0.5 * sin(rand * time);
            move = pi + offset + move - p;
            float distance = dot(move, move) * 1.0;
            if(distance < minDistance)
            {
                minDistance = distance;
            }
        }
    }
    return minDistance;
}

float noise_sum(vec2 p)
{
    float f = 0.0;
    p = p / 2.0;
    f += 1.0000 * wnoise(p, iTime); p = 2.0 * p;
    //f += 0.5000 * wnoise(p, iTime); 
    p = 2.0 * p;
    f += 0.2500 * wnoise(p, iTime); p = 2.0 * p;
    //f += 0.1250 * wnoise(p, iTime); 
    p = 2.0 * p;
    f += 0.0625 * wnoise(p, iTime); p = 2.0 * p;

   return f;
}

