vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax,p)*ax,p,cos(ro))+sin(ro)*cross(ax,p);
}

float linedist(vec3 p, vec3 a, vec3 b) {
  float k = dot(p-a,b-a)/dot(b-a,b-a);
  return distance(p, mix(a,b,clamp(k,0.,1.)));
}

float linedist(vec2 p, vec2 a, vec2 b) {
  float k = dot(p-a,b-a)/dot(b-a,b-a);
  return distance(p, mix(a,b,clamp(k,0.,1.)));
}

float smin(float a, float b, float k) {
  float h = max(0.,k-abs(b-a))/k;
  return min(a,b)-h*h*h*k/6.;
}

float box(vec4 p, vec4 d) {
  vec4 q = abs(p)-d;
  return length(max(q,0.)) + min(0., max(max(q.x,q.y),max(q.z,q.w)));
}
float box(vec3 p, vec3 d) {
  vec3 q = abs(p)-d;
  return length(max(q,0.)) + min(0., max(q.x,max(q.z,q.y)));
}

vec4 wrot(vec4 p) {
  return vec4(dot(p,vec4(1.)), p.yzw + p.zwy - p.wyz - p.xxx)/2.;
}

