float plane;
float obj;
float hiddenball;
float clouds(vec3 p) {

  p = erot(p,normalize(vec3(2,3,1)),2.123);
  float d1 = length(abs(asin(sin(p))*.9))-1.;
  p = erot(p,normalize(vec3(1,2,3)),2.453);
  float d2 = length(abs(asin(sin(p))*.9))-1.;
  p = erot(p,normalize(vec3(3,2,1)),0.7821);
  float d3 = length(abs(asin(sin(p))*.9))-1.;
  return (d1+d2+d3)/3.;
}

float airplane(vec3 p) {
  float pln = linedist(p, vec3(.4,0,0), vec3(-.6,0,0))-.1-cos(p.x*2.)*.1;
  float wingskel = linedist(p.xy, vec2(0,.6), vec2(0,-.6))-.15;
  float wing = linedist(vec2(wingskel, p.z), vec2(-.04,0), vec2(-1,0))-.04;
  float tail = linedist(p, vec3(-.65,0,0), vec3(-.85,0,.2))-.13;
  float ruddskel = linedist(p.xy, vec2(-.9,.3), vec2(-.9,-.3))-.1;
  float rudd = linedist(vec2(ruddskel, p.z), vec2(-.04,.28), vec2(-1,.28))-.03;
  return smin(min(rudd,wing), smin(tail,pln,0.2),.1);
}

float blimp(vec3 p) {
  float blmp = linedist(p, vec3(.6,0,0), vec3(-.7,0,0))-.4-cos(p.x*2.)*.1;
  float carriage = linedist(p, vec3(.3,0,-.5), vec3(-.3,0,-.5))-.15;
  p.yz = abs(p.yz);
  p.yz = vec2(max(p.y,p.z),min(p.y,p.z));
  float ruddskel = linedist(p.xy, vec2(-1.,.3), vec2(-.8,-.3))-.2;
  float rudd = linedist(vec2(ruddskel, p.z), vec2(-.04,.0), vec2(-1,.0))-.03;
  return min(min(carriage,blmp),rudd);
}

float hotair(vec3 p) {
  p.z -= .4;
  float ht = length(p)-.7;
  float dimpl = linedist(p, vec3(0), vec3(0,0,-.8))-.2;
  float hollow = linedist(p, vec3(0), vec3(0,0,-1.3))-.18;
  float carriage = box(p-vec3(0,0,-1.3), vec3(.12))-.05;
  carriage = -min(-carriage, box(p-vec3(0,0,-1.), vec3(.12, .12, .3)));
  p.xy = abs(p.xy);
  float lines = linedist(p, vec3(0.1,0.1,-.5), vec3(.1,.1,-1.3))-.01;
  return min(-smin(-smin(ht, dimpl, 0.4),hollow,.01), min(lines,carriage));
}

float helicopter(vec3 p) {
  float body = linedist(p, vec3(.2,0,0), vec3(-.2,0,0))-.35;
  body = smin(body, linedist(p, vec3(.2,0,-.2), vec3(-1.,0,-.2))-.2, .2);
  float tail = linedist(p, vec3(-1.1,0,-.2), vec3(-1.3,0,0))-.13;
  body = smin(body,tail,.1);
  vec3 p2 = p;
  p2.x -= -1.3;
  p2 = erot(p2, vec3(0,1,0), iTime);
  p2.y -=0.15;
  p2.y=abs(p2.y);
  p2.xz = abs(p2.xz);
  p2.zx = vec2(max(p2.x,p2.z),min(p2.x,p2.z));
  p = erot(p, vec3(0,0,1), iTime);
  p.z -=0.44;
  p.yx = abs(p.yx);
  p.yx = vec2(max(p.y,p.x),min(p.y,p.x));

  float wingskel = linedist(p.xy, vec2(0,.6), vec2(0,-.6))-.1;
  float wing = linedist(vec2(wingskel, p.z), vec2(-.04,0), vec2(-1,0))-.04;
  float wingskel2 = linedist(p2.xz, vec2(0,.2), vec2(0,-.23))-.06;
  float wing2 = linedist(vec2(wingskel2, p2.y), vec2(-.04,0), vec2(-1,0))-.02;
  return min(body,min(wing,wing2));//smin(min(rudd,wing), smin(tail,pln,0.2),.1);

}

float scene(vec3 p) {
  float fl = p.z + 5.;

  vec3 pp = p;
  pp.y = asin(sin(pp.y/sep))*sep;
  float roll = pow(sin(fract(t*.03)*3.14/2.),50.) + floor(t*.03);
  pp = erot(pp, vec3(1,0,0),roll*acos(-1.)*2.);
  pp = erot(pp, vec3(0,1,0),sin(t)*.4);
  pp = erot(pp, vec3(0,0,1),cos(t*.5)*.2);
  pp.z += cos(t)*.3;
  pp.y += -sin(t*.5)*.2;
  float air = airplane(pp);
  float blmp = blimp(pp);
  float hot = hotair(pp);
  float heli = helicopter(pp);
  float mixval_a = smoothstep(-.25,.25,sin((iTime+1.)/2.));
  float mixval_b = smoothstep(-.15,.15,sin((iTime+1.)/4.));
  plane = mix(mix(hot, heli, mixval_a), mix(blmp, air, mixval_a), mixval_b);
  //plane = 


  p.x += mod(t,1000.);
  vec3 p2 = p;
  p2 = erot(p2, vec3(0,0,1),.31233);
  p2.xy = asin(sin(p2.xy/3.)*.97)*3.+3.;
  p2.z += 4.;
  p2 = erot(p2, vec3(0,0,1),t*.3);
  p2 = erot(p2, vec3(0,1,0),t*.2);
  p2 = erot(p2, vec3(1,0,0),t*.1);
  obj = length(min(1.-abs(p2),0.6))-.7;


  p = erot(p,normalize(vec3(2,3,1)),2.123);
  float d1 = dot(abs(asin(sin(p))*.9),vec3(1))/sqrt(3.)-1.;
  p = erot(p,normalize(vec3(1,2,3)),2.453);
  float d2 = dot(abs(asin(sin(p))*.9),vec3(1))/sqrt(3.)-1.;
  p = erot(p,normalize(vec3(3,2,1)),0.7821);
  float d3 = dot(abs(asin(sin(p))*.9),vec3(1))/sqrt(3.)-1.;



  float ground = mix((d1+d2+d3)/2.5, fl,0.2);
  vec4 reproj = vec4(ground, asin(sin(p*5.))/5.);
  float mx = cos(iTime*.3-fl*.3)*.5+.5;
  mx = smoothstep(0.4,.6,mx);
  mx = smoothstep(0.,1.,mx);
  reproj = mix(wrot(reproj), reproj,mx);
  ground = box(reproj,vec4(.02,.27,.27,.27))-0.04;
  hiddenball = length(reproj+vec4(.2,0,0,0))-.2;
  ground = min(ground, hiddenball);
  return min(min(obj,ground), plane);
}

vec3 norm(vec3 p) {
  mat3 k = mat3(p,p,p)-mat3(0.03);
  return normalize(scene(p)-vec3(scene(k[0]), scene(k[1]), scene(k[2])));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = vec2(fragCoord - iResolution.xy*.5)/iResolution.y;

  float sc = smoothstep(.5,.6,cos(iTime/10.+3.));
  t = pow(sin(fract(iTime*.4)*3.14/2.),70.)*2. + floor(iTime*.4)*2. + iTime*.9 + 2.;
  sep = 2.+cos(t*.4);
  vec3 cam = normalize(vec3(1, uv));
  vec3 init = vec3(-5.+sin(t), 0, 0) + cam;
  cam = erot(cam,vec3(0,1,0),.7-sc*.3);
  init = erot(init,vec3(0,1,0),.7-sc*.3);
  float zrot = t*.3;
  cam = erot(cam,vec3(0,0,1),zrot);
  init = erot(init,vec3(0,0,1),zrot); 
  init.z -= sc*4.;


  vec3 p = init;
  bool hit = false;
  float dist;
  bool triggered = false;
  bool outline = false;
  float glow = 0.;
  for (int i = 0; i < 150 && !hit; i++) { 
    dist = scene(p);
    glow += 1./(hiddenball*500.+1.);
    if (!triggered) triggered = dist < 0.03;
    if (triggered) {
      float ol = 0.05-dist;
      outline = ol < dist;
      dist = min(ol,dist);
    }
    hit = dist*dist < 1e-6;
    p+=cam*dist;
  }
  vec3 p2 = init;
  for (int i = 0; i < 30; i++) {
    float dd = abs(clouds(p2))+.1;
    p2+=cam*dd;
  }
  float lcoudstr = distance(p2, init);
  float fog = smoothstep(40.,10.,distance(p,init)+ lcoudstr*.5);
  bool pl = plane == dist;
  bool oj = obj == dist;
  vec3 n = norm(p);
  vec3 r = reflect(cam,n);
  float ao = smoothstep(-.1,.1,scene(p+n*.1));
  float diff = ao*length(sin(n*vec3(3,2,2))*.5+.5)/sqrt(3.);
  diff = floor(diff*6.)/6.+.1;
  float spec = length(sin(r*vec3(3,2,2))*.5+.5)/sqrt(3.);
  float fres = 1.-abs(dot(cam,n))*.9;
  vec3 matcol = mix(vec3(0.3,0.7,0.2), vec3(0.7,0.5,0.2), smoothstep(-5.5,-4.5,p.z));
  matcol = mix(matcol, vec3(0.7,0.75,0.9), smoothstep(-4.5,-3.5,p.z));
  if (pl) {
    matcol = vec3(0.9,0.8,0.3);
    float id = floor(p.y/acos(-1.)/sep + 1.5);
    id += floor(iTime);
    matcol = abs(erot(matcol, vec3(1,0,0), id))*.6+.4;
    matcol = abs(erot(matcol, vec3(0,1,0), id*2.3423))*.6+.4;
  }
  vec3 col = matcol*diff + pow(spec,10.)*fres*1.5;
  if (oj) {

    float spec2 = length(sin(r*vec3(3,1,1))*.5+.5)/sqrt(3.);
    float spec3 = length(sin(-r*vec3(3,1,1))*.5+.5)/sqrt(3.);
    col = vec3(0.8,0.3,0.2)*floor(spec2*6.)/6.+pow(spec2,10.);
    col += (vec3(0.1,0.3,0.8)*floor(spec3*6.)/6.+pow(spec3,10.))*.7;
    col *=ao;
  }
  vec3 bgcol = vec3(0.4,0.7,0.9);
  if (!pl) {
    col *= 0.7;
  } else {
    col = col*.7+.3;
  }
  col = erot(col,normalize(sin(p)),.02);
  fragColor.xyz = (hit ? mix(bgcol, col,fog) : bgcol) + pow(smoothstep(40.,5.,lcoudstr),10.)*(1.-fog);
  if (outline) fragColor.xyz = mix(bgcol, fragColor.xyz*.2, fog);
  fragColor.xyz = smoothstep(vec3(-.2),vec3(1.2),fragColor.xyz);
  fragColor.xyz += (glow*vec3(0.7,0.3,0.)+glow*.9)*.4;
}

const float pi = 3.141592;
const float tau = 2.0*pi;

//#define TIME (3.0*tau/4.0)
#define TIME iTime/2.0

const float sphereRadius = 15.0;
const float camRadius = 2.0*sphereRadius;

struct hit {
    bool didHit;
    vec3 col;
};

hit getVoxel(ivec3 p) {
    if (length(vec3(p)) < sphereRadius)
        return hit(true, vec3(p) / (sphereRadius * 2.0) + 0.6);
    else
        return hit(false, vec3(0,0,0));

}

vec3 lighting(vec3 norm, vec3 pos, vec3 rd, vec3 col) {
    vec3 lightDir = normalize(vec3(-1.0, 3.0, -1.0));
    float diffuseAttn = max(dot(norm, lightDir), 0.0);
    vec3 light = vec3(1.0,0.9,0.9);

    vec3 ambient = vec3(0.2, 0.2, 0.3);

    vec3 reflected = reflect(rd, norm);
    float specularAttn = max(dot(reflected, lightDir), 0.0);

    return col*(diffuseAttn*light*1.0 + specularAttn*light*0.6 + ambient);
}

// Voxel ray casting algorithm from "A Fast Voxel Traversal Algorithm for Ray Tracing" 
// by John Amanatides and Andrew Woo
// http://www.cse.yorku.ca/~amana/research/grid.pdf
hit intersect(vec3 ro, vec3 rd) {
    //Todo: find out why this is so slow
    vec3 pos = floor(ro);

    vec3 step = sign(rd);
    vec3 tDelta = step / rd;


    float tMaxX, tMaxY, tMaxZ;

    vec3 fr = fract(ro);

    tMaxX = tDelta.x * ((rd.x>0.0) ? (1.0 - fr.x) : fr.x);
    tMaxY = tDelta.y * ((rd.y>0.0) ? (1.0 - fr.y) : fr.y);
    tMaxZ = tDelta.z * ((rd.z>0.0) ? (1.0 - fr.z) : fr.z);

    vec3 norm;
    const int maxTrace = 100;

    for (int i = 0; i < maxTrace; i++) {
        hit h = getVoxel(ivec3(pos));
        if (h.didHit) {
            return hit(true, lighting(norm, pos, rd, h.col));
        }

        if (tMaxX < tMaxY) {
            if (tMaxZ < tMaxX) {
                tMaxZ += tDelta.z;
                pos.z += step.z;
                norm = vec3(0, 0,-step.z);
            } else {
                tMaxX += tDelta.x;
            	pos.x += step.x;
                norm = vec3(-step.x, 0, 0);
            }
        } else {
            if (tMaxZ < tMaxY) {
                tMaxZ += tDelta.z;
                pos.z += step.z;
                norm = vec3(0, 0, -step.z);
            } else {
            	tMaxY += tDelta.y;
            	pos.y += step.y;
                norm = vec3(0, -step.y, 0);
            }
        }
    }

 	return hit(false, vec3(0,0,0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.xy - 0.5;
    vec3 worldUp = vec3(0,1,0);
    vec3 camPos = vec3(camRadius*sin(TIME), 10, 1.0*camRadius*cos(TIME));
    vec3 lookAt = vec3(0,0,0);
    vec3 camDir = normalize(lookAt - camPos);
    vec3 camRight = normalize(cross(camDir, worldUp));
    vec3 camUp = cross(camRight, camDir);

    vec3 filmCentre = camPos + camDir*0.3;
    vec2 filmSize = vec2(1,iResolution.y / iResolution.x);

    vec3 filmPos = filmCentre + uv.x*filmSize.x*camRight + uv.y*filmSize.y*camUp;
    vec3 ro = camPos;
    vec3 rd = normalize(filmPos - camPos);

    hit h = intersect(ro, rd);    
    if(h.didHit) {
        fragColor = vec4(h.col,0);
    } else{
        fragColor = vec4(0,0,0,0);
    }
}

////////////////////////////////////////////////////////////////////////////////
//
// Loopless Conic Envelopes
// Matthew Arcus, mla, 2020
//
// A demo of drawing envelopes of conic curves looplessly
//
////////////////////////////////////////////////////////////////////////////////


const float PI = 3.1415927;
int AA = 2;

// With homogeneous coordinates, (x,y,z) corresponds to the Euclidean
// point (x/z,y/z), so (kx,ky,kz) represents the same point for any
// k. z can be regarded as a "scaling" parameter, so (x,y,0)
// represents a point infinitely far away (like a vanishing point in a
// perspective drawing it can be regarded as a pure direction), in
// fact, a point on the "line at infinity", equation z = 0.
//
// Lines also are represented as l = vec3(a,b,c) with point p being on line l
// just when dot(l,p) = 0, again, multiples of (a,b,c) represent the
// same line, so (a,b,c) represents the Euclidean line ax+by+c = 0.
//
// If l1 and l2 are lines, join(l1,l2) is their intersection, and if p1,p2 are
// points, then join(p1,p2) is the line between them. join is just
// cross product:

vec3 join(vec3 p, vec3 q) {
  return cross(p,q);
}

// Actually drawing a point or a line or a conic involves Euclidean notions of
// scale and distance, so we need to convert to Euclidean points (or do something
// equivalent), so to draw a point, we need the distance between the pixel point
// and the point being drawn (we can look on this as mapping both points to the
// z=1 plane and measuring distances there):

float point(vec3 p, vec3 q) {
  return distance(p.xy/p.z,q.xy/q.z);
}

// We can short circuit the calculations a little:
// If p = (x,y,z) and l = (a,b,c) then (x/z,y/z) is the Euclidean point
// and if |(a,b)| = 1, then the distance to the line is ax+by+c:
float line(vec3 p, vec3 l) {
  return abs(dot(p,l)/(p.z*length(l.xy)));
}

// For a line given by two points, just join the points into a line:
float line(vec3 p, vec3 q, vec3 r) {
  return line(p,join(q,r));
}

// A conic curve is represented by a 3x3 symmetric matrix A and p is on the
// conic just when dot(p,A*p) = dot(p*A,p) = 0. Lines tangent to the conic then
// satisfy dot(l,inverse(A)*l) = dot(l*inverse(A),l) = 0 (points and lines
// are dual).
//
// For simple conics where the matrix is diagonal, we can just use a
// a vector to represent the conic, the code is much the same:
//
// To turn the conic equation into a distance function, we need to
// divide by the magnitude of the gradient, and the gradient at p is
// just 2.0*A*p (analogous to dx^2/dx = 2x):
float conic(vec3 p, mat3 A) {
  float s = dot(p,A*p);
  vec3 ds = 2.0*A*p; // Gradient
  return abs(s/(p.z*length(ds.xy))); // Normalize for Euclidean distance
}

// Note, this is solving at^2 + 2bt + c = 0, so the discriminant is
// b^2 - ac, not b^2 - 4ac.
bool quadratic(float a, float b, float c, out float t1, out float t2) {
  // Assumes we have checked for a == 0 or c == 0
  float disc = b*b-a*c;
  if (disc < 0.0) return false;
  if (b >= 0.0) {
    // Try to avoid rounding error.
    t1 = -b-sqrt(disc);
    t2 = c/t1; t1 /= a;
  } else {
    t2 = -b+sqrt(disc);
    t1 = c/t2; t2 /= a;
  }
  return true;
}

// Find the intersections of the line through p0,p1 with the conic
// defined by A by solving a quadratic equation.
// (or dually, find the tangent lines to A through join of lines p0,p1).
bool intersection(vec3 p0, vec3 p1, mat3 A, out vec3 q0, out vec3 q1) {
  // Any line through p0, p1 is expressible as p0+t*p1,
  // so solve (p0 + t*p1)A((p0 + t*p1) = 0 =>
  // p0*A*p0 + 2*t*p0*A*p1 + t^2*p1*A*p1 = 0
  // We have a quadratic equation:
  float a = dot(p1,A*p1), b = dot(p0,A*p1), c = dot(p0,A*p0);
  // a==0 or c==0 indicate p1 or p0 are actually on conic
  if (a == 0.0) { q0 = q1 = p1; return true; }
  if (c == 0.0) { q0 = q1 = p0; return true; }
  float t1,t2;
  if (!quadratic(a,b,c,t1,t2)) return false;
  q0 = p0 + t1*p1;
  q1 = p0 + t2*p1;
  return true;

}

// Find the tangents to a conic from the point z, since this involves
// solving a quadratic equation, there may be no (real) solution, so
// return a boolean to indicate success or otherwise.
bool tangents(vec3 z, mat3 A, out vec3 tan1, out vec3 tan2) {
  // Construct two lines through the point - assuming that z is not at
  // infinity, l1 and l2 will be distinct.
  vec3 l1 = join(z,vec3(1,0,0));
  vec3 l2 = join(z,vec3(0,1,0));
  return intersection(l1,l2,A,tan1,tan2);
}

// crossratio(infinity,p,q,r) = pr/pq
// Assumes p,q,r are collinear.
// -1 if p is midpoint of q and r
// r = p + k(q-p) or r = (1-k)p + kq where k = ratio(p,q,r)
float ratio(vec3 p, vec3 q, vec3 r) {
  p /= p.z; q /= q.z; r /= r.z;
  return dot(r-p,q-p)/dot(q-p,q-p);
}

vec3 getcolor(vec3 z, vec3 pointer) {
  // This is just a plain unit circle, which seems very dull,
  mat3 A = mat3(1,0,0,
                0,1,0,
                0,0,-1);
  // until we rotate it in projective space, when it transitions
  // to ellipse, parabola, hyperbola, then back to parabola,
  // ellipse and circle.
  float t = iTime;
  mat3 rot = mat3(cos(t),0,sin(t),
                  0,1,0,
                  -sin(t),0,cos(t));
  A = A*rot;

  // We use the inverse of the conic matrix to find lines.
  mat3 Ainv = inverse(A);

  float dconic = conic(z,A); // The distance to the conic

  // Draw the grid of tangent lines to the conic, with lines
  // passing through a baseline:
  vec3 base0 = vec3(-1,0,1);
  vec3 base1 = pointer;
  vec3 baseline = join(base0,base1);
  vec3 tan1, tan2; // Put tangent solutions here
  float ldist = dconic; // This will be minimum distance to a line,
  float pdist = 1e8; // and the minimum distance to a point
  pdist = min(pdist,point(z,2.0*base0-base1)); // Opposite baseline end
  if (tangents(z,Ainv,tan1,tan2)) {
    // Find where the tangents hit the baseline
    vec3 z1 = join(tan1,baseline); 
    vec3 z2 = join(tan2,baseline);
    float k1 = ratio(base0,base1,z1);
    float k2 = ratio(base0,base1,z2); 
    float N = 16.0;
    float t = 0.0;//0.1*iTime;
    k1 = round(N*(k1+t))/N-t;
    k2 = round(N*(k2+t))/N-t;
    // If the rounded baseline point is in the right range (and the
    // tangent from there is defined) then draw it. Since there will
    // be two tangents from the baseline point, we need to make sure
    // we use the right one - if baseline point and the pixel point
    // are on the same side of the conic, then use the same quadratic
    // root for both, otherwise use different roots. We find which
    // side the points are with the ratio function.
    //
    // If line l is a tangent to the conic A, then the intersection
    // point of the tangent is Ainv*l (and the tangent line at point
    // p is A*p). A is symmetric, so A*p = p*A and Ainv*l = l*Ainv.
    vec3 tan11,tan12;
    if (abs(k1) <= 1.0 && tangents(base0+k1*(base1-base0),Ainv,tan11,tan12)) {
      vec3 t1 = Ainv*tan1; // The point of tan1 on the conic
      if (ratio(t1,z1,z) > 0.0) ldist = min(ldist,line(z,tan11));
      else ldist = min(ldist,line(z,tan12));
    }
    vec3 tan21,tan22;
    if (abs(k2) <= 1.0 && tangents(base0+k2*(base1-base0),Ainv,tan21,tan22)) {
      vec3 t2 = Ainv*tan2; // The point of tan2 on the conic
      if (ratio(t2,z2,z) > 0.0) ldist = min(ldist,line(z,tan22));
      else ldist = min(ldist,line(z,tan21));
    }
  }
  ldist = min(ldist,line(z,baseline));
  pdist = min(pdist,point(z,pointer));
  vec3 col = vec3(0.75+0.25*cos(20.0*dconic),1,1);
  col *= smoothstep(0.01,max(fwidth(z.x),0.02),ldist);
  col *= smoothstep(0.08,0.1,pdist);
  return col;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  vec3 col = vec3(0);
  float scale = 5.0;
  vec2 pointer;
  if (iMouse.x > 0.0) pointer = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
  else pointer = cos(0.5*iTime-vec2(0,0.4*PI));
  for (int i = 0; i < AA; i++) {
    for (int j = 0; j < AA; j++) {
      vec2 z = (2.0*(fragCoord+vec2(i,j)/float(AA))-iResolution.xy)/iResolution.y;
      col += getcolor(vec3(scale*z,1),vec3(scale*pointer,1));
    }
  }
  col /= float(AA*AA);
  col = pow(col,vec3(0.4545));
  fragColor = vec4(col,1);
}

/*
 * 02 May 2020
 * I liked yesterday's sketch a lot. Wanted to make it a little less thick.
 * Fiddled with the values till I had a nice thin line that looked nice.
 * Wanted to go with a quick and dirty watercolour style sketch. Really happy
 * with the result.
 * The biggest problem I'm facing is the uniformity of the noise. Specifically
 * while filling in the colours (painting). So if I draw
 * multiple shapes that occupy the same space, their behaviour is identical which
 * doesn't look so great. So I need to figure out what infrastructure is required
 * so that the scale of noise is dependent on the size of the area being filled,
 * as well as use a different noise pattern for every individual shape (or something
 * along those lines) like passing a "seed" that is shared for the single fill.
 */
float PI = 3.14159;

vec3 paper_colour = vec3(0.9, 0.9, 0.85);
vec3 pencil_colour = vec3(0.3, 0.3, 0.40);

float line_thickness = 0.0;
float line_variation = 5.2508;
float line_smoothness = 1.5;
float fill_noise = 35.04;

float rand(vec2 c) {
    //return pow((sin(iTime/1.0)),2.0)*0.1 + 0.9*fract(sin(dot(c.xy ,vec2(15.868,781.233))) * 4378.5453);

	return fract(sin(dot(c.xy ,vec2(15.868,781.233))) * 4378.5453);
}

float noise(vec2 p, float freq ){
	float unit = iResolution.x/freq;
	vec2 ij = floor(p/unit);
	vec2 xy = mod(p,unit)/unit;
	//xy = 3.*xy*xy-2.*xy*xy*xy;
	xy = .5*(1.-cos(PI*xy));
	float a = rand((ij+vec2(0.,0.)));
	float b = rand((ij+vec2(1.,0.)));
	float c = rand((ij+vec2(0.,1.)));
	float d = rand((ij+vec2(1.,1.)));
	float x1 = mix(a, b, xy.x);
	float x2 = mix(c, d, xy.x);
	return mix(x1, x2, xy.y);
}

float pNoise(vec2 p, int res){
    p += vec2(iTime*14.50, 0.0);
	float persistance = .5;
	float n = 0.;
	float normK = 0.;
	float f = 30.3;
	float amp = 1.;
	int iCount = 0;
	for (int i = 0; i<50; i++){
		n+=amp*noise(p, f);
		f*=2.;
		normK+=amp;
		amp*=persistance;
		if (iCount == res) break;
		iCount++;
	}
	float nf = n/normK;
	return nf*nf*nf*nf;
}

float pNoise2(vec2 p, int res){
    p += vec2(iTime*12.0, iTime*52.0);
	float persistance = .5;
	float n = 0.;
	float normK = 0.;
	float f = 3.3;
	float amp = 1.;
	int iCount = 0;
	for (int i = 0; i<50; i++){
		n+=amp*noise(p, f);
		f*=2.;
		normK+=amp;
		amp*=persistance;
		if (iCount == res) break;
		iCount++;
	}
	float nf = n/normK;
	return nf*nf*nf*nf;
}

float get_noise(vec2 p) {
    float noise = pNoise(p, 3);
    noise *= 2.0;
    // noise -= 0.5;
    return noise;
}
float get_noise2(vec2 p) {
    float noise = pNoise2(p, 3);
    noise *= 2.0;
    // noise -= 0.5;
    return noise;
}

float triangle_sign (vec2 p1, vec2 p2, vec2 p3) {
    return (p1.x - p3.x) * (p2.y - p3.y) - (p2.x - p3.x) * (p1.y - p3.y);
}

bool point_in_triangle (vec2 pt, vec2 v1, vec2 v2, vec2 v3) {
    float d1, d2, d3;
    bool has_neg, has_pos;
    d1 = triangle_sign(pt, v1, v2);
    d2 = triangle_sign(pt, v2, v3);
    d3 = triangle_sign(pt, v3, v1);
    has_neg = (d1 < 0.0) || (d2 < 0.0) || (d3 < 0.0);
    has_pos = (d1 > 0.0) || (d2 > 0.0) || (d3 > 0.0);
    return !(has_neg && has_pos);
}

float rectangle(vec2 samplePosition, vec2 halfSize){
    vec2 componentWiseEdgeDistance = abs(samplePosition) - halfSize;
    float outsideDistance = length(max(componentWiseEdgeDistance, 0.0));
    float insideDistance = min(max(componentWiseEdgeDistance.x, componentWiseEdgeDistance.y), 0.0);
    return outsideDistance + insideDistance;
}

float distance_to_line(vec2 p, vec2 p1, vec2 p2) {
    float d;
    float l_sq = pow(distance(p1, p2), 2.0);
    if (l_sq == 0.0)
        d = distance(p, p1);
    else {
        float t = max(0.0, min(1.0, dot(p - p1, p2 - p1) / l_sq));
        vec2 projection = p1 + t * (p2 - p1);  // Projection falls on the segment
        d = distance(p, projection);
    }
    return d;
}

float triangle (vec2 p, vec2 p1, vec2 p2, vec2 p3) {
    float d, d2, d3;
    d = distance_to_line(p, p1, p2);
    d = min(d, distance_to_line(p, p2, p3));
    d = min(d, distance_to_line(p, p3, p1));
    if (point_in_triangle(p, p1, p2, p3))
        return -d;
    return d;
}

vec3 draw_line(vec2 p, vec2 p1, vec2 p2, vec3 current_colour) {
    // return current_colour;
    p += sin(iTime/1.2)*4.0*vec2(get_noise(p.xy), get_noise(p.yx));
    p1 += 5.0*vec2(get_noise(p.xy), get_noise(p.yx));
    p2 -= 5.0*vec2(get_noise(p.xy), get_noise(p.yx));
    float d = distance_to_line(p, p1, p2);
    float noise;
    // TODO (01 May 2020 sam): Figure out how to to better line check
    if (p1.y<p.y) noise = get_noise(p.yx);
    else noise = get_noise(p);
    d += noise*line_variation;
    float pencil = smoothstep(line_thickness, line_thickness+line_smoothness, d);
    return mix(pencil_colour, current_colour, pencil);
}

vec3 fill_box(vec2 p, vec2 p1, vec2 p3, vec3 current_colour, vec3 fill_colour) {
    vec3 colour = current_colour;
    vec2 p2 = vec2(p1.x, p3.y);
    vec2 p4 = vec2(p3.x, p1.y);
    vec2 box_center = (p1 + p3)/2.0;
    float bd = rectangle(p-box_center, (p3-p1)/2.0);
    float noise = get_noise2(p);
    noise *= fill_noise;
    if (bd+noise<0.0)
        colour = fill_colour;
    return colour;
}

vec3 draw_box(vec2 p, vec2 p1, vec2 p3, vec3 current_colour, bool fill_paper) {
    vec3 colour = current_colour;
    vec2 p2 = vec2(p1.x, p3.y);
    vec2 p4 = vec2(p3.x, p1.y);
    vec2 box_center = (p1 + p3)/2.0;
    float bd = rectangle(p-box_center, (p3-p1)/2.0);
    float noise = get_noise(p);
    noise *= 5.0;
    if (bd-noise<0.0 && fill_paper)
        colour = paper_colour;
    colour = draw_line(p, p1, p2, colour);
    colour = draw_line(p, p2, p3, colour);
    colour = draw_line(p, p3, p4, colour);
    colour = draw_line(p, p4, p1, colour);
    return colour;
}

vec3 fill_triangle(vec2 p, vec2 p1, vec2 p2, vec2 p3, vec3 current_colour, vec3 fill_colour) {
    vec3 colour = current_colour;
    float d = triangle(p, p1, p2, p3);
    float noise = get_noise2(p);
    noise *= fill_noise;
    if (d+noise<0.0)
        colour = fill_colour;
    return colour;
}

