}

vec4 GetWorleyNoise3D(vec3 uvw)
{
    float noise = 0.0;

    vec3 p = floor(uvw);
    vec3 f = fract(uvw);

    vec4 res = vec4(1.0);
    for(int x = -1; x <=1; ++x)
    {
        for(int y = -1; y <=1; ++y)
        {
            for(int z = -1; z <=1; ++z)
            {
                vec3 gp = p + vec3(x, y, z);	//grid point

                vec3 v = Random3D(gp);

				vec3 diff = gp + v - uvw;

                float d = length(diff);

                if(d < res.x)
                {
                    res.xyz = vec3(d, res.x, res.y);
                }
                else if(d < res.y)
                {
                    res.xyz = vec3(res.x, d, res.y);
                }
                else if(d < res.z)
                {
                    res.z = d;
                }

                res.w = Hash31(gp);
            }
        }
    }

    return res;
}

float fBMWorley(vec3 x, float lacunarity, float gain, int numOctaves)
{
    float total = 0.0;
    float frequency = 1.0;
    float amplitude = 1.0;
	float totalAmplitude = 0.0;
    for(int i = 0; i < numOctaves; ++i)
    {
        totalAmplitude += amplitude;

        vec4 n = GetWorleyNoise3D(x * frequency);
        total += amplitude * n.x;

        frequency *= lacunarity;
        amplitude *= gain;
    }

    return total/totalAmplitude;
}

