vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

float smin(float a, float b, float k)
{
    float h = clamp(.5+.5*(b-a)/k, 0., 1.);
    return mix(b,a,h) - k*h*(1.0-h);
}

float remap(float a, float min1, float max1, float min2, float max2) {
    return min2 + (max2-min2) * ((a-min1) / (max1-min1));
}

// angle
mat2 Rot(float a)
{
    float s = sin(a);
    float c = cos(a);
    return mat2(c,-s,s,c);
}

// a and b and the end positions, r is radius
float Capsule(vec3 p, vec3 a, vec3 b, float r)
{
    vec3 ab = b-a;
    vec3 ap = p-a;

    float t = dot(ab,ap) / dot(ab,ab);
    t = clamp(t,0.,1.);

    vec3 c = a + t*ab;
    return length(p-c)-r;
}

// a is position, r is outer,inner radii
float Torus(vec3 p, vec3 a, vec2 r)
{
    p -= a;
    float x = length(p.xz)-r.x;
    return length(vec2(x,p.y))-r.y;
}

// a is pos, s is size
float Box(vec3 p, vec3 a, vec3 s)
{
    p -= a;
    return length(max(abs(p)-s, 0.));
}

// This is really the scene itself
float GetDist(vec3 p)
{
    vec4 s = vec4(0,1,6,1);	// This is a sphere (x,y,z,radius)

    float sphereDist = length(p-s.xyz)-s.w;
    //float planeDist = p.y;	// Ground plane at 0;

    float cd = Capsule(p, vec3(0,.5,4), vec3(1,2,6), .2);
    float td = Torus(p, vec3(0,.5,6), vec2(1.5, .3));
    float bd = Box(p,vec3(-2,.5, 6),vec3(.5));

    // This is how you combine them
    float d = smin(cd, td, .2);
   // d = min(d, td);
    d = smin(d, bd, .2);

    return d;
}

vec2 RayMarch(vec3 ro, vec3 rd)
{
    // Distance from origin
    float dO = 0.;
    int steps = 0;
    float dS = 0.;

    //for (int i = 0; i < MAX_STEPS; i++) {
    do {
        vec3 p = ro + rd * dO;
        // Distance to scene
       	dS = GetDist(p);
        dO += dS;
        steps++;
        //if (dO > MAX_DIST || dS < SURF_DIST) break;	// Hit
    } while (steps < MAX_STEPS && dO < MAX_DIST && dS > SURF_DIST);
    //}
    float s = remap(float(steps), 0., float(MAX_STEPS), 1.0, 0.);
    return vec2(dO,s);
}

// Calculate normal vector for a point
vec3 GetNormal(vec3 p)
{
    float d = GetDist(p);
    vec2 e = vec2(.01,0);
    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

float GetLight(vec3 p, vec3 lightPos)
{
    lightPos.xz += vec2(sin(iTime), cos(iTime));	// Rotate around Y

    vec3 lv = normalize(lightPos-p);
    vec3 ln = GetNormal(p);

    float dif = clamp(dot(ln, lv), 0., 1.);

    // Shadows
    float d = RayMarch(p+ln*SURF_DIST*2., lv).x;
    if (d < length(lightPos-p)) dif *= .1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0);

    // "camera"
    vec3 ro = vec3(0,3,0);	// Ray origin (camera position)
    vec3 rd = normalize(vec3(uv.x,uv.y-.4,1.1));	// Ray direction (looking at)

    // Raymarch the scene
    vec2 march = RayMarch(ro, rd);
    vec3 p = ro + rd * march.x;

    // Calculate the diffuse lighting
    vec3 lightPos1 = vec3(0, 5, 3);	// Where light is
    vec3 lightPos2 = vec3(-.5, -5, 7);	// Where light is
    float difLight = GetLight(p, lightPos2) + GetLight(p, lightPos1);
    difLight = clamp(difLight, 0., 1.);

    // Shade the scene
    //col = pal( difLight, vec3(0.7,0.1,0.5),vec3(0.5,0.5,0.5),vec3(1.0,1.0,1.0),vec3(0.34,0.33,0.67) ) * march.y;
    col = vec3(difLight);

    // Output to screen
    fragColor = vec4(col,1.0);
}

