#define S(a, b, t) smoothstep(a, b, t)

float distLine(vec2 p, vec2 a, vec2 b){
	vec2 pa = p - a;
    vec2 ba = b - a;
    float t = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    return length(pa - ba*t);
}

float line(vec2 p, vec2 a, vec2 b){
	float d = distLine(p, a, b);
    float m = S(0.03, 0.01, d);
    float d2 =  length(a - b);
    m *= S(1.4, 0.8, d2) * 0.5 + S(0.05, 0.03, abs(d2 - 0.75));
    return m;
}

float distTriangle(in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
	vec2 e0 = p1 - p0;
	vec2 e1 = p2 - p1;
	vec2 e2 = p0 - p2;

	vec2 v0 = p - p0;
	vec2 v1 = p - p1;
	vec2 v2 = p - p2;

	vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
	vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
	vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );

    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min( min( vec2( dot( pq0, pq0 ), s*(v0.x*e0.y-v0.y*e0.x) ),
                       vec2( dot( pq1, pq1 ), s*(v1.x*e1.y-v1.y*e1.x) )),
                       vec2( dot( pq2, pq2 ), s*(v2.x*e2.y-v2.y*e2.x) ));

	return -sqrt(d.x)*sign(d.y);
}

float triangle(vec2 p, vec2 a, vec2 b, vec2 c){
	float d = distTriangle(p, a, b, c);
    float m = S(0.13, 0.01, d);
    float d2 =  length(a - b);
    m *= S(1.2, 0.8, d2) * 0.25 + S(0.05, 0.03, abs(d2 - 0.75));
    return m;
}

float N21(vec2 p){
	p = fract(p * vec2(233.34, 851.73));
    p += dot(p, p + 23.45);
    return fract(p.x * p.y);
}

vec2 N22(vec2 p){
	float n = N21(p);
    return vec2(n, N21(p + n));
}

vec2 getPos(vec2 id, vec2 offset){
    vec2 n = N22(id + offset) * iTime;
    return offset + sin(n) * 0.4;
}

float layer(vec2 uv){
	vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    vec2 p[9];
    int i = 0;
    for(float y = -1.0; y <= 1.0; y++){
    	for(float x = -1.0; x <= 1.0; x++){
        	p[i++] = getPos(id, vec2(x, y));
    	}    
    }


    float t = iTime * 10.0;
    float m = 0.0;
    for(int i = 0; i < 9; i++){
    	m += line(gv, p[4], p[i]);

        vec2 j = (p[i] - gv) * 20.0;

        for(int yi= i + 1; yi < 9; yi++){
    		for(int zi= yi + 1; zi < 9; zi++){

                float len1 = abs(length(p[i] - p[yi]));
                float len2 = abs(length(p[yi] - p[zi]));
                float len3 = abs(length(p[i] - p[zi]));

                if((len1 + len2 + len3) < 2.8){
                	m += triangle(gv, p[i], p[yi], p[zi]) * 0.8;
                }
    		}
    	}
    }
    m += line(gv, p[1], p[3]);
    m += line(gv, p[1], p[5]);
    m += line(gv, p[7], p[3]);
    m += line(gv, p[7], p[5]);

    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float m = 0.0;
    float t = iTime * 0.02;

    float gradient = min(-uv.y,uv.y) * 0.25;

    float s = sin(t);
    float c = cos(t);
    mat2 rot = mat2(c, -s, s, c);
    uv *= rot;

    for(float i = 0.0; i < 1.0; i += 1.0 / 4.0){
        float z = fract(i + t);
        float size = mix(10.0, 0.5, z);
        float fade = S(0.0, 0.5, z) * S(1.0, 0.8, z);

        m += layer(uv * size + i * 10.0) * fade* 0.35;
    }


    vec3 base = vec3(0.845, 0.156, 0.167) * 0.8 + 0.6;
    vec3 gradc = vec3(0.045, 0.056, 0.967) * 0.8 + 0.6;
    vec3 col = m * base;

    col -= gradient * gradc;

    fragColor = vec4(col,1.0);
}

////////////////////////////////////////////////////////////////
// The larger the DISTORTION, the smaller the glow
const float DISTORTION = 0.2;
// The higher GLOW is, the smaller the glow of the subsurface scattering
const float GLOW = 6.0;
// The higher the BSSRDF_SCALE, the brighter the scattered light
const float BSSRDF_SCALE = 3.0;
// Boost the shadowed areas in the subsurface glow with this
const float AMBIENT = 0.0;
// Toggle this to affect how easily the subsurface glow propagates through an object
#define ATTENUATION 0


// Adjust these to alter where the subsurface glow shines through and how brightly
const float AO_DIST = 0.085;
const float FIVETAP_K = 2.0;


const int RAY_STEPS = 256;
vec3 LIGHT_POS = vec3(0.0, 5., -2.0);

struct Intersection
{
    vec3 p;		// point of intersection in world space
    float t;	// t-value of ray to p
    int objHit;	// assign every object in scene a unique ID integer
    vec3 color;	// color of surface
};

vec3 rotateX(vec3 p, float a) {
    return vec3(p.x, cos(a) * p.y - sin(a) * p.z, sin(a) * p.y + cos(a) * p.z);
}    

// Takes in direction, rotates it by a radians
// result of applying a y rotation matrix to the vec3
// rotates about y-axis
vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}

// b consists of width, height, and depth VECTORS (center to edge)
float BoxSDF(vec3 p, vec3 b) {
  vec3 q = abs(p) - b;
  return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

// point, radius, center
float SphereSDF(vec3 p, float r, vec3 c) {
    return distance(p, c) - r;
}

float PlaneSDF(vec3 p, vec4 n)
{
    return dot(p,n.xyz)+n.w;
}

#define BOX_SDF BoxSDF(pos + vec3(2.5, 0.0, 0.0), vec3(1.0))
#define SPHERE_SDF SphereSDF(pos, 1., vec3(2.5, 0.0, 0.0))
#define PLANE_SDF PlaneSDF(pos, vec4(0.0, 1.0, 0.0, 1.0))

#define SUN_SDF SphereSDF(pos, 1., LIGHT_POS - vec3(0.0,1.5,0.0))

#define BOX 0
#define SPHERE 1
#define PLANE 2

#define SUN 3

float SceneSDF(vec3 pos, vec3 lightPos)
{
    float t = BOX_SDF;
    t = min(t,SPHERE_SDF);
    t = min(t,PLANE_SDF);
    //t = min(t,SUN_SDF);
    return t;
}

float shadowMap3D(vec3 pos)
{
    return SceneSDF(pos, vec3(0.0, 0.0, 0.0));
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k)
{
    float res = 1.0;
    float t = min_t;
    for (int i = 0; i < RAY_STEPS; ++i)
    {
        float m = shadowMap3D(origin + t * dir);
        if(m < 0.0001)
        {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

float shadow(vec3 dir, vec3 origin, float min_t)
{
    return softShadow(dir, origin, min_t, 6.0);
}

float fiveTapAO(vec3 p, vec3 n, float k)
{
    float aoSum = 0.0;
    for (float i = 0.0; i < 5.0; ++i)
    {
        float coeff = 1.0 / pow(2.0, i);
        aoSum += coeff * (i * AO_DIST - SceneSDF(p + n * i * AO_DIST, vec3(0.)));
    }
    return 1.0 - k * aoSum;
}

float subsurface(vec3 lightDir, vec3 normal, vec3 viewVec, float thickness) {
    vec3 scatteredLightDir = lightDir + normal * DISTORTION;
    float lightReachingEye = pow(clamp(dot(viewVec, -scatteredLightDir), 0.0, 1.0), GLOW) * BSSRDF_SCALE;
    float attenuation = 1.0;
    #if ATTENUATION
    attenuation = max(0.0, dot(normal, lightDir) + dot(viewVec, -lightDir));
    #endif
	float totalLight = attenuation * (lightReachingEye + AMBIENT) * thickness;
    return totalLight;
}

vec3 ComputeMaterial(int objHit, vec3 p, vec3 n, vec3 light, vec3 view, float thick)
{
    float lambert = dot(n, light) + 0.2;
    switch(objHit)
    {
        case BOX:
        vec3 color = vec3(1.0, 0.88, 0.7) * vec3(1.0, 0.67, 0.67) * max(0.0, dot(n, light)) * shadow(light, p, 0.1);
        return color + vec3(1.0, 0.67, 0.67) * subsurface(light, n, view, thick) * vec3(1.0, 0.88, 0.7);
        break;
    	case SPHERE:
        vec3 color1 = vec3(1.0, 0.88, 0.7) * vec3(1.0, 0.67, 0.67) * max(0.0, dot(n, light)) * shadow(light, p, 0.1);
        return color1 + vec3(1.0, 0.67, 0.67) * subsurface(light, n, view, thick) * vec3(1.0, 0.88, 0.7);
    	break;
    	case PLANE:
        return vec3(0.0, 0.0, 1.0) * lambert;
    	break;
        case SUN:
        return vec3(10.0);
    	break;
	}
	return vec3(1.0);    
}

vec3 ComputeNormal(vec3 pos, vec3 lightPos) {
    vec2 offset = vec2(0.0, 0.001);
    return normalize( vec3( SceneSDF(pos + offset.yxx, lightPos) - SceneSDF(pos - offset.yxx, lightPos),
                            SceneSDF(pos + offset.xyx, lightPos) - SceneSDF(pos - offset.xyx, lightPos),
                            SceneSDF(pos + offset.xxy, lightPos) - SceneSDF(pos - offset.xxy, lightPos)
                          )
                    );
}

void SceneSDF(vec3 pos, out float t, out int objHit, vec3 lightPos)
{
    t = BOX_SDF;
    objHit = BOX;

    float t2;
    if((t2 = SPHERE_SDF) < t)
    {
        t = t2;
        objHit = SPHERE;
    }
    if((t2 = PLANE_SDF) < t)
    {
        t = t2;
        objHit = PLANE;
    }
    if((t2 = SUN_SDF) < t)
    {
        t = t2;
        objHit = SUN;
    }
}

void RayMarch(vec3 origin, vec3 dir, out float t, out int objHit, vec3 lightPos)
{
    // starts slightly > 0
    // so that if we march from a different point in the scene that isnt the camera
    // we dont immediately intersect ourselves
    t = 0.001;
    // ray march not to a certain distance but for a certain number of iterations
    for (int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
        // minimum distance that pos is from all SDFs in the scene
        float m;
        SceneSDF(pos, m, objHit, lightPos);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    // hit nothing
    t = -1.0;
    objHit = -1;
}

Intersection SceneIntersection(vec3 eye, vec3 dir, vec3 lightPos)
{
    float t;
    int objHit;
    // Marches along ray to find point of intersection
    RayMarch(eye, dir, t, objHit, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = ComputeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);
    float thickness = fiveTapAO(isect, -nor, FIVETAP_K);

    vec3 surfaceColor = ComputeMaterial(objHit, isect, nor, lightDir, normalize(eye - isect), thickness);

    return Intersection(isect, t, objHit, surfaceColor);
}

const vec3 worldUp = vec3(0,1,0);
void RayCast(vec2 ndc, out vec3 eye, out vec3 dir, out vec3 ref)
{
    eye = vec3(0.0, 0.0, 10.0);
    ref = vec3(0.0, 0.0, 0.0);

    float len = tan(3.14159 * 0.08) * distance(eye, ref);

    vec3 H = normalize(cross(worldUp, ref - eye));
    vec3 V = normalize(cross(H, eye - ref));
    V *= len;
    H *= len * iResolution.x / iResolution.y;
    vec3 p = ref + ndc.x * H + ndc.y * V;
    dir = normalize(p - eye);
}

float calcSoftshadow( in vec3 ro, in vec3 rd, in float mint, in float tmax)
{
	float res = 1.0;
    float t = mint;
    float ph = 1e10; // big, such that y = 0 on the first iteration

    for( int i=0; i<100; i++ )
    {
		float h = SceneSDF( ro + rd*t , LIGHT_POS);
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, 10.0*d/max(0.0,t-y) );
        ph = h;

        t += h;

        if( res<0.0001 || t>tmax ) break;

    }
    return clamp( res, 0.0, 1.0 );
}

float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.001 + 0.15*float(i)/4.0;
        float d = SceneSDF( pos + h*nor, LIGHT_POS );
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 1.5*occ, 0.0, 1.0 );    
}

// mainImage: present in every single shader program on shadertoy
// outputs: the fragment color for the fragment it is operating on
// takes in: the pixel space coordinates of the fragment it is coloring
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime * 3.14159 * 0.1;
    LIGHT_POS = rotateY(vec3(0.0, 1.5, 12.), t);

    // iResolution: width and height of screen in pixels (on bottom toolbar of image output)
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Convert to range -1 to 1 for x and y
    // Normalized Device Coordinates w.r.t. the center of the screen being (0,0)
    uv = (uv * 2.0) - vec2(1.0);

    Intersection aaIsects[4];
    vec3 eye, dir, ref;
    int idx = 0;
    for (float i = 0.0; i < 1.0; i += 0.5)
    {
        for (float j = 0.0; j < 1.0; j += 0.5)
        {
            RayCast(uv + vec2(i, j) / iResolution.xy, eye, dir, ref);
            aaIsects[idx++] = SceneIntersection(eye, dir, LIGHT_POS);
        }
    }

    vec3 avgColor = vec3(0.0);
    for (int i = 0; i < 4; ++i)
    {
        avgColor += aaIsects[i].color;
    }
    avgColor *= 0.25;


    vec3 norm = ComputeNormal(aaIsects[0].p, LIGHT_POS);

            	vec3 color = vec3(0.0);
    	vec3 baseColor = avgColor;

    //    https://www.shadertoy.com/view/lsKcDD

    // key light
        vec3  lig = normalize(LIGHT_POS - aaIsects[0].p);
        vec3  hal = normalize( lig-dir );
        float dif = clamp( dot( norm, lig ), 0.0, 1.0 ) * 
                    calcSoftshadow( aaIsects[0].p, lig, 0.01, 15.0);

		float spe = pow( clamp( dot( norm, hal ), 0.0, 1.0 ),16.0)*
                    dif *
                    (0.04 + 0.96*pow( clamp(1.0+dot(hal,dir),0.0,1.0), 5.0 ));

		color = baseColor * 4.0*dif*vec3(1.00,0.70,0.5);
        color +=      12.0*spe*vec3(1.00,0.70,0.5);

            // ambient light
        float occ = calcAO( aaIsects[0].p, norm );
		float amb = clamp( 0.5+0.5*norm.y, 0.0, 1.0 );
        color += baseColor*amb*occ*vec3(0.0,0.08,0.1);
    color += baseColor * 0.4;

    fragColor = vec4(color * step(0.0, aaIsects[0].t), 1.);
}

// inspired by https://www.youtube.com/watch?v=cQXAbndD5CQ

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // uv - center and fix aspect ratio
    vec2 uv = (fragCoord - iResolution.xy * 0.5)/iResolution.y;

    // uv - rotate 45 deg
	float a = 0.7853982; // 45 in radians
    float sa = sin(a);
    float ca = cos(a);
    uv *= mat2(ca, -sa, sa, ca);

    float time = iTime * 2.0;
    vec3 col = vec3(0);

    float divisions = 11.0;
    vec2 gv = fract(uv * divisions) - 0.5;
    vec2 id = floor(uv * divisions);
    gv.x *= mod(id.x, 2.0) == 0.0 ? 1.0 : -1.0;
    gv.y *= mod(id.y, 2.0) == 0.0 ? 1.0 : -1.0;

    float d = length(gv + vec2(sin(time) * 2.0, cos(time) * 2.0) * 0.25);
    float radius = 0.5 * (0.1 + sin(time * 0.25 + length(uv) * 3.12) * 0.5 + 0.5);
    float circle = smoothstep(radius + 0.01, radius - 0.02, d);


    col = mix(vec3(0.125, 0.874, 0.733), vec3(0.874, 0.125, 0.266), circle);


    fragColor = vec4(col,1.0);
}

