   d.xy = 4.*U/iResolution.xy-2.;
   for (d.z=5.; a++ < 9;
    O +=-4.*d*(atan(cos(O.x),cos(O.y))+sin(.03*iTime+18.)));
   O /=5e1;
}

#define res iResolution.xy
#define ires ivec2(res)

#define decaysp vec2(3, 30)
#define colormat (mat2x3(.5,.6,.2, .9,.95,1) * mat2x2(1,0,-1,1))
#define op(x,y) x^y
#define speed 0x625b

int modinv(int a, int m) {
    int r = 1; int q = 0;
    for(int i = 0; i < m; i++) {
        q |= r & (1 << i);
        r += (-((r >> i) & 1)) & (a << i);
    }
    return q;
}

void mainImage(out vec4 color, in vec2 _pos) {
    int m = int(floor(log2(max(res.x, res.y)))); int s = 1 << m;
    ivec2 pos = ivec2(_pos) - (ivec2(ires.xy) >> 1);
    float t = fract(float(((op((pos.x), (pos.y))) * modinv(speed, m) + iFrame) & (s-1)) / float(s));
    color.rgb = colormat * exp(-t * decaysp);
}

#define S(a, b, t) smoothstep(a, b, t)

vec2 getGrid(vec2 uv)
{
    float freq = 50.;
    float shiftDown = - 0.9;

    // module by cos, then shift cos down to narrow the resulting "line"
    vec2 tmp = (cos(uv * freq) + shiftDown);
    // we'd need to clamp values if we are going to myltiply tmp by itself,
    // but I just use some random constant to remove the blur
    //tmp = clamp(tmp, vec2(0.), vec2(1.));
    tmp *= 10.;
	return S(0., 1., tmp);
}

vec3 getTargetDot(vec2 uv, float radius)
{
    vec2 tmp = sin(uv);
    float d = clamp(length(tmp), 0., radius);
    d /= radius;
    d = S(1., 0., d);

    return mix(vec3(0.), vec3(0., 6., 0.), d);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    // then move (0,0) to a center
    // then account for window ratio
    vec2 uv = fragCoord/iResolution.xy;
    uv     -= .5;
    uv.x   *= iResolution.x / iResolution.y;

    vec2 mapSpeed = vec2(iTime * 0.15, iTime) * 0.15;

    // bg color
    vec3 col  = vec3(0.066, 0.066, 0.094);

    // grid
    vec3 gridColor = vec3(0.176, 0.176, 0.243);
    vec2 grid = getGrid(uv + mapSpeed);
    col = mix(col, gridColor, grid.x);
    col = mix(col, gridColor, grid.y);

    // dots
    col += getTargetDot(uv + vec2(0.4, 0.37)+ mapSpeed, 0.05);
    col += getTargetDot(uv + vec2(0.2, -0.5) + mapSpeed, 0.05);
    col += getTargetDot(uv + vec2(1.5, 1.) + mapSpeed, 0.05);
    col += getTargetDot(uv + vec2(-0.5, 0.7) + mapSpeed, 0.05);

    fragColor = vec4(col, 1.0);
}

