int INSTRUCTIONS;

vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(p,ax)*ax,p,cos(ro))+sin(ro)*cross(ax,p);
}

//this is a multiplication by the quaternion (.5, .5, .5, .5)
//somehow this results in a good rotation
vec4 rot4d(vec4 b) {
    return vec4(dot(b,vec4(1)), b.yzw + b.zwy - b.wyz - b.xxx)*0.5;
}

float super(vec4 p) {
    return sqrt(length(p*p));
}

float box(vec4 p, vec4 d) {
    vec4 q = abs(p)-d;
    return super(max(q,0.))+min(0.,max(max(q.x,q.w),max(q.y,q.z)));
}

vec4 smin(vec4 a, vec4 b, float k) {
    vec4 h = max(vec4(0),-abs(a-b)+k)/k;
    return min(a,b)-h*h*h*k/6.;
}

float scene(vec3 p) {
    int inst = INSTRUCTIONS;
    int off = inst % 3; inst /= 3;
    vec4 pp = vec4(p,vec3(1.,0.5,0.)[off]);
    for (int i = 0; i < 6; i++) {
    	int op = inst % 3; inst /= 2;
        if (op == 0) pp = sqrt(pp*pp+0.001); //smooth abs
        if (op == 1) pp = rot4d(pp); //fixed 4d rotation
        if (op == 2) pp.x -= 0.25; //offset
    }
    pp = smin(pp,rot4d(pp),0.1);
    float b1 = box(pp,vec4(1.))-0.1;
    return b1;
}

vec3 norm(vec3 p) {
    mat3 k = mat3(p,p,p)-mat3(0.001);
    return normalize(scene(p) - vec3(scene(k[0]),scene(k[1]),scene(k[2])));
}

vec3 srgb(float r, float g, float b) {
    return vec3(r*r,g*g,b*b);
}

float bayer(vec2 uv) {
    return texelFetch(iChannel0,ivec2(uv)%8,0).x/128.;
}

int hash(int x) {
    x += 45758;
    for (int i = 0; i < 16; i++) {
    	x ^= x << 13;
		x ^= x >> 7;
		x ^= x << 17;
    }
    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    INSTRUCTIONS = hash(int(iTime));
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;

    vec3 cam = normalize(vec3(1.2,uv));
    vec3 samplepos = erot(vec3(-10,0,0), vec3(0,0,1), iTime*.2);
    float radius = 10.-scene(samplepos);
    vec3 init = vec3(-radius-4.,0,0);

    float yrot = 0.5;
    float zrot = iTime*.2;
    if (iMouse.z > 0.) {
        yrot += -4.*mouse.y;
        zrot = 4.*mouse.x;
    }
    cam = erot(cam, vec3(0,1,0), yrot);
    init = erot(init, vec3(0,1,0), yrot);
    cam = erot(cam, vec3(0,0,1), zrot);
    init = erot(init, vec3(0,0,1), zrot);

    vec3 p = init;
    bool hit = false;
    for (int i = 0; i < 250 && !hit; i++) {
        float dist = scene(p);
        hit = dist*dist < 1e-6;
        p+=dist*cam;
        if (distance(p,init)>50.) break;
    }
    vec3 n = norm(p);
    vec3 r = reflect(cam,n);
    float ss = smoothstep(-.1,.1,scene(p+vec3(.1)));
    float ao = smoothstep(-.1,.1,scene(p+n*.1));
    float diff = mix(length(sin(n*3.5)*0.5+0.5)/sqrt(3.),ss,.3)*ao*1.5;
    float spec = length(sin(r*3.)*0.5+0.5)/sqrt(3.);
    float frens = 1.-pow(dot(cam,n),2.)*0.98;
    vec3 col = mix(srgb(0.05,0.01,0.),srgb(0.45,0.6,0.8), diff) + pow(spec,5.)*frens;
    vec3 bg = vec3(smoothstep(1.5,0.,length(uv))*.1);
    fragColor.xyz = (hit ? col : bg) + bayer(fragCoord);
    fragColor.xyz = sqrt(fragColor.xyz);
}

