// originaly made using bonzomatic, so define fGlobalTime
#define fGlobalTime	iTime


float smin(float a,float b,float k)
{
  // found on internet from Iq I presume
  float h=max(k-abs(a-b),0.);
  return min(a,b)-h*h/(k*4.);
}

float sdSphere(vec3 p,float r)
{
    return length(p)-r;
}

float sdEllipse(vec3 p,vec3 r)
{
  float h=length(p/r);
  float n=length(p/r/r);
  return h*(h-1.)/n;  
}

vec2 scene(vec3 p)
{
    float matNum=0.;

    float d,h;

    p.x+=sin(fGlobalTime*3.1415926/4.);
    p.z+=fract(fGlobalTime/100.)*100.;

    float rng1=4.;
    float idx=floor((p.x+rng1/2.)/rng1)*rng1;
    float idz=floor((p.z+rng1/2.)/rng1)*rng1;
    p.xz=fract((p.xz+rng1/2.)/rng1)*rng1-rng1/2.;

    // coincoin

    vec3 pBeforeRot=p;
    float a=fGlobalTime*3.+idx*12.+idz*42.; // this will cut
    float sina=sin(a);
    float cosa=cos(a);


    // rotate coin
    p.xz*=mat2(cosa,-sina,sina,cosa);


    // head
    h=sdSphere(p-vec3(0.,0.,0.),0.15);
    matNum=3.;

     //body
    d=sdEllipse(p-vec3(-.2,-.3,0.),vec3(.4,.2,.3));
    h=smin(h,d,0.1);

    d=sdEllipse(p-vec3(-.5,-.15,0.),vec3(.05,.1,.08));
    h=smin(h,d,0.1);

    // bec
    vec3 p2=p-vec3(.15,.05,0.);
    d=sdEllipse(p2-vec3(.0,p2.x/4.,0.),vec3(.1,.03,.08));
    if(d<h)
    {
      matNum=4.;
    }
    h=smin(h,d,.01);

    p2=p-vec3(.15,0.,0.);
    d=sdEllipse(p2-vec3(.0,-p2.x/4.,0.),vec3(.1,.03,.08));
    if(d<h)
    {
      matNum=4.;
    }

    h=smin(h,d,.01);

    // eyes : just color
    // white
    p2=vec3(p.x,p.y,abs(p.z))-vec3(.16-p.y/1.5,.1,.05);
    d=sdEllipse(p2,vec3(.02,.04,.03));
    if(d<h)
    {
      matNum=5.;
    }
    // black
    p2=vec3(p.x,p.y,abs(p.z))-vec3(.16-p.y/1.5,.1,.05);
    d=sdEllipse(p2,vec3(.02,.04,.03)*0.7);
    if(d<h)
    {
      matNum=6.;
    }

    p=pBeforeRot;

    // floor
    float dfl=p.y+0.4;
    float hole=sdEllipse(p-vec3(0.,0.2,0.),vec3(2.,1.,2.));
    dfl=max(dfl,-hole);
    dfl=min(dfl,p.y+0.47+0.01*sin(length(p.xz)*20.0-(fract(fGlobalTime/100.0)*100.)*10.));

    if(dfl<h)
    {
        h=dfl;
        matNum=1.; // floor
        if(length(vec2(p.x,p.z))<(1.5+0.*sin(p.z*10.)/8.))
        {
          matNum=2.;
        }
    }


    return vec2(h,matNum);
}


vec2 raym(vec3 ro,vec3 rd)
{
  float t=0.0;
  float matNum=-1.;
  for(int i=0;i<100;i++)
  {
    vec3 p=ro+t*rd;

    vec2 hm=scene(p);
    matNum=hm.y;
    if(hm.x<0.0001)  break;
    t+=hm.x;
    if(t>20.)
    {
      matNum=-1.;
      break;    
    }
  }
    return vec2(t,matNum);
}


vec3 norm(vec3 p)
{
    vec2 e=vec2(0.0001,0.);
    return normalize(vec3(scene(p+e.xyy).x-scene(p-e.xyy).x,
                          scene(p+e.yxy).x-scene(p-e.yxy).x,
                          scene(p+e.yyx).x-scene(p-e.yyx).x));
}

float shadow(vec3 ro,vec3 rd)
{
  float res=1.;

  float t=0.01;
  for(int i=0;i<100;i++)
  {
    vec3 p=ro+t*rd;

    float h=scene(p).x;

    res=min(res,8.*h/t);

    if(h<0.00001) break;
    t+=h;
    if(t>20.) break;

  }
    return res;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);
  uv*=2.;


  vec3 col=vec3(0.1,0.2,0.5);

  col=mix(col,vec3(0.2),1.-uv.y);


  vec3 ro=vec3(0.,0.,-1);
  vec3 rd=normalize(vec3(uv.x,uv.y,0.)-ro);


  vec3 sunDir=normalize(vec3(1.,1.,0.));
  vec3 sunCol=vec3(4.,4.,1.);
  vec3 bounceDir=normalize(vec3(0.,-1.,0.));

  vec3 mate1=vec3(.01,.2,.01); // ground
  vec3 mate2=vec3(.01,.05,.5); // ground2
  vec3 mate3=vec3(.4,.4,.01); // body
  vec3 mate4=vec3(.4,.1,.01); // bec
  vec3 mate5=vec3(1.); // eye white
  vec3 mate6=vec3(.01); // eye black


  vec2 tm=raym(ro,rd);
  float t=tm.x;
  float matNum=tm.y;
  if(matNum>0.)
  {
      vec3 p=ro+t*rd;    
      vec3 nor=norm(p);

      vec3 newCol=col;

      float sunDif=clamp(dot(nor,sunDir),0.,1.);
      float sunSha=clamp(shadow(p+nor*0.002,sunDir),0.,1.);

      float bounceDif=clamp(dot(nor,bounceDir),0.,1.);

      vec3 curMate=vec3(0.);

      if(matNum<1.5) // floor 1
        curMate=mate1;
      else if(matNum<2.5) // floor 2
        curMate=mate2;
      else if(matNum<3.5) // body
        curMate=mate3;
      else if(matNum<4.5) // bec
        curMate=mate4;
      else if(matNum<5.5) // eye wh
        curMate=mate5;
      else if(matNum<6.5) // eye bl
        curMate=mate6;


      newCol=curMate*sunCol*sunDif*sunSha;
      newCol+=curMate*bounceDif*0.2;


      col=mix(newCol,col,clamp(t/20.,0.,1.));
  }



    col=pow(col,vec3(.4));



    // Output to screen
    fragColor = vec4(col,1.0);
}

