    p *= contrast;
    float G = sin(t * .21) * .05 + .1;
    float R = cos(t * .3) * .03 + .1;

    // mix
    vec3 rgb = vec3(R - p * .2-.1, G, .5) + p ;
    //vec3 col = vec3(rgb + p);

    fragColor = vec4(rgb, 1.0); 
}

// Similar to my OLD method for skinned skeletal mesh animation.
// Was used on Demise (Wizardry/Bard'sTale clone you never heard of)
// I called it "Boner" for laughs.
// Was helpful for the artists to rig their models really easily in 3DS MAX.
// "Boner II" got used on a few Tony Hawk ports back in the Dreamcast era.
// This is the same general idea, but only 2D and not using meshes at all,
// but rather simple pill SDFs, noise, and textures.
// As an example of skinned animation, it's truly simple,
// and has quite a few limitations when used in production.
// But it certainly can do the trick!

const int njoints = 4;

// parent joint of joint - can try alternative hierarchies
int[njoints] poseConnect = int[njoints] (
    -1, 0, 1, 2 //1, -1, 1, 2 //1, 2, -1, 2 //1, 2, 3, -1 //
    );

vec2[njoints] poseRest = vec2[njoints] (
    vec2(-1,0), vec2(-.33,0), vec2(.33,0), vec2(1,0)
    );
vec2[njoints] poseCurr;
// the scheme results in having to index by the *child* joint index everywhere.
// parent/child is a rather arbitrary distinction, could simply call "other".
// in any case, the directionality should be consistent, either way.

void InitPoseCurr()
{
  #if 0
	vec2[njoints] poseAnim = vec2[njoints] (
    	vec2(-.6,.3), vec2(-.4,-.3), vec2(.4,-.3), vec2(.6,.3)
    );
    for (int i = njoints; i-- > 0; ) {
        poseCurr[i].x = mix(poseRest[i], poseAnim[i], abs(sin(iTime*.2))).x;
        poseCurr[i].y = mix(poseRest[i], poseAnim[i],     sin(iTime*.2) ).y;
    }
  #else
    float animangle = .7*sin(iTime*.2);
    if (iMouse.z > 1.) animangle = ((iMouse.y * 2.) / iResolution.y - 1.) * 1.25;
    vec2 cs = sin(vec2(1.57,0) + animangle);
    mat2 R = mat2(cs.x, -cs.y, cs.y, cs.x), r = mat2(1);
    poseCurr[0] = poseRest[0] + vec2(0, 1.4*cs.y);
    for (int i = 1; i < njoints; ++i)
        poseCurr[i] = poseCurr[i-1] + (r = r * R) * (poseRest[i] - poseRest[i-1]);
  #endif
}

// distance to line segment ab from p
float dseg(vec2 p, vec2 a, vec2 b)
{
    p -= a; b -= a;
    return length(p - b*clamp(dot(p,b) / max(1e-3,dot(b,b)), 0., 1.));
}

float tnoise(vec2 p)
{
    return texture(iChannel1, p).x * 2. - 1.;
}

float dshape(vec2 p)
{
    float dcap = dseg(p, vec2(-1,0), vec2(1,0));
    dcap -= .25;
    dcap += tnoise(p*32./iChannelResolution[1].y) * .03;
    return dcap;
}

mat2 bone(int j, vec2[njoints] pose)
{
    return mat2(pose[max(0, poseConnect[j])], pose[j]);
}

mat2 inv(mat2 m)
{
  #if 1
    // scaling m rows then transposing seems identical to inverse, as expected
    m[0] /= dot(m[0],m[0]);
    m[1] /= dot(m[1],m[1]);
    mat2 i = transpose(m);
  #else
    // in other words, I'm pretty sure it's *not* the inverse implementation, after all.
    mat2 i = inverse(m);
  #endif
    return i;
}

mat2 joint2world(mat2 bon)
{
   vec2 a = bon[0]
      , b = bon[1]
    , r = b - a;
    if (dot(r,r) < 1e-7) r = vec2(1,0); // bugfix: avoid divide by zero!
    vec2 d = normalize(r);
    return mat2(r, vec2(-d.y, d.x));
}

vec2 worldToJoint(vec2 p, int j, vec2[njoints] pose)
{
	mat2 b = bone(j, pose); // the two relevant segment points of the joint
    mat2 j2w = joint2world(b);
    return inv(j2w) * (p - b[0]);
}

vec2 jointToWorld(vec2 p, int j, vec2[njoints] pose)
{
	mat2 b = bone(j, pose); // the two relevant segment points of the joint
    mat2 j2w = joint2world(b);
    return j2w * p + b[0];
}

const float jointpoof = .01;

// I was using custom meshes for the bones
// but iq's sdRoundCone would be quite appropriate here
float djoint(vec2 q, int i, vec2[njoints] pose)
{
    return dseg(q, pose[i], pose[max(0, poseConnect[i])]) - jointpoof;
}

float dAllJoints(vec2 q)
{
    float d = 3.4e38;
    for (int i = njoints; i-- > 0; )
        d = min(d, djoint(q, i, poseCurr));
    return d;
}

void drawArrow(inout vec3 c, vec2 q, vec2 d, vec3 tint, float w)
{
    float s = dseg(q, vec2(0), d) - w;
    float cov = clamp(.5 - .5*iResolution.y * s, 0., 1.);
    c = mix(c, tint, cov);
}

void drawTripod(inout vec3 c, vec2 q, mat3x2 m)
{
    q -= m[2]; float w = 1.5/iResolution.y;
    drawArrow(c, q, m[0], vec3(1,0,0), w);
    drawArrow(c, q, m[1], vec3(0,1,0), w);
//    drawArrow(c, q-m[0], m[1], vec3(0,0,1), w);
}

// how many bone weights to consider per pixel
const int nclosest = 2;

// can adjust weighting falloff; helps isolate the bone influences.
float hackw(float d, float d0)
{
     return pow(max(0., d), 2.); //max(0., d); //
}
    //d - d0; //d; // // HACK bring closest in to zero distance for weighting calcs

// refactored merely to simplify closestJoints logic
void insertCloser(int i, float d0, inout int[nclosest] n, inout float[nclosest] d)
{
	for (int j = 0; j < nclosest; ++j) {
	   	if (d0 < d[j]
		 || (d0 == d[j] && n[j] > i) // ties go to the smaller index
		//|| (d0 == 0. && d[j] == 0. && poseConnect[i] <= j)
		) {
			for (int k = nclosest-1; --k >= j; ) { // insert, shift down
				d[k+1] = d[k];
				n[k+1] = n[k];
			}
			n[j] = i; // insert
			d[j] = d0;
			break;
		}
	}
}

// track closest joints for weighting
void closestJoints(vec2 q
     , out   int[nclosest] n
     , out float[nclosest] d
     , out float[nclosest] w)
{    
    for (int j = nclosest; j-- > 0; ) {
        n[j] = 0;
        d[j] = 3.4e38;
    }
	// for each animated joint,
    for (int i = njoints; i-- > 0; ) {
	    // compute distance to pixel
    	float d0 =
            poseConnect[i] < 0 
            ? 3.4e38
            : djoint(q, i, poseCurr);
        d0 = max(d0, 0.);
        insertCloser(i, d0, n, d);
    }
    for (int j = nclosest; j-- > 0; )
        d[j] = max(0., d[j]); // seg poofed, can be negative
    float t = 0.; // total distances for weighting
    for (int j = nclosest; j-- > 0; )
        t += hackw(d[j], d[0]);
    t = max(t, 1e-3);
    for (int j = nclosest; j-- > 0; ) {
        w[j] = clamp((t - hackw(d[j], d[0])) / t, 0., 1.);
    }
    // TODO I may need to take bone relative angle vs. rest pose angle
    // into account for the weighting to handle the scaling issues.
 // simply normalize the weights wrt one again, and handle special case of failure
    float wtotal = 0.;
    for (int j = nclosest; j-- > 0; )
        wtotal += w[j];
    if (wtotal == 0.)
        w[0] = 1.;
    else
    for (int j = nclosest; j-- > 0; )
        w[j] /= wtotal;
}

vec2 mapToRestPose(vec2 q
     ,   int[nclosest] n
     , float[nclosest] w)
{
    // trying to sum weighted position in rest pose from nearest joints
    vec2 c = vec2(0);
    for (int ci = nclosest; ci-- > 0; ) {
        int i = n[ci];
    	vec2 l = worldToJoint(q, i, poseCurr);
        c += jointToWorld(l, i, poseRest) * w[ci];
    }
    return c;
}


vec3 jointColor(int j)
{
    return max(vec3(0), cos(10.2*float(j) + vec3(0,2,4)));
}


