    vec3 rect1 = vec3(rect(uv, rect1Pos, vec2(.15, .8)));
    vec3 rect2 = vec3(rect(uv, rect2Pos, vec2(1.4, .15 )));
    float m = line(uv,vec2(-.1,-.1),vec2(.25,.15));
    m += line(uv,vec2(-.1,1.1),vec2(.25,.85));

    vec3 col = vec3(.0941, .1725, .3215);
