float sph(vec3 c, vec4 p) { return length(c - p.xyz) - p.w; }

float generic(vec3 p) { return sph(p, vec4(-0.1,0.,1.8,0.25)); }

float march(vec3 ro, vec3 rd)
{
    float pr = 0.;

    for(int i = 0; i < 6; i++)
    {
     	vec3 p = ro + rd * pr;
        pr += generic(p);
        if(pr >= 8.)
            break;
    }
    return pr;
}

vec3 normal(vec3 p)
{
 	vec2 off = vec2(0.001, 0.0);
    float copy = generic(p);
    vec3 n = copy - vec3(generic(p- off.xyy), generic(p- off.yxy), generic(p- off.yyx));
    return normalize(n);
}

float lighting(vec3 p, vec3 lpos)
{
    vec3 n = normal(p);
    vec3 l = normalize(lpos - n);
    vec3 ll = normalize(lpos - p);
    float diff = dot(l, n) + 1.;
    diff+=pow(max(dot(l,n),0.),2.0)*2.0;
    return diff;
}

