        * vec3(lighting(p,vec3(lp.x,-lp.y/2.,lp.z)));
	col += vec3(0.25,0.35,0.3)/1.;
    col *= exp(-1. * pow(m,1.1));
    if(m > 3.)
    col+=vec3(smoothstep(0.998, 1., fract(cos(dot(uv, vec2(0.2, 7.13)))*5894.5458)))/4.;
    fragColor = vec4(col,1.0);
}

// Noise implementation
vec2 hash( vec2 p )
{
    p = vec2( dot( p, vec2( 127.1, 311.7 )), dot( p, vec2( 269.5, 183 )) );
    return -1.0 + 2.0 * fract( sin( p ) * 43758.5453123 );
}

float noise( in vec2 p )
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

    vec2 i = floor( p + (p.x + p.y) * K1 );
    vec2 a = p - i + ( i.x + i.y) * K2;
    float m = step( a.y, a.x );
    vec2 o = vec2( m, 1.0 - m );
    vec2 b = a - o + K2;
    vec2 c = a - 1.0 + 2.0 * K2;
    vec3 h = max( 0.5 - vec3( dot( a, a ), dot( b, b ), dot( c, c ) ), 0.0 );
    vec3 n = h*h*h*h*vec3( dot( a, hash( i + 0.0 )), dot( b, hash( i + o )), dot( c, hash( i + 1.0)));
    return dot( n, vec3( 70.0 ) );
}

// Draw a diagonal division
float tri(vec2 st, float pct){
    return 1.0 - smoothstep( pct, pct+0.05, st.y );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (fraom 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    // Center coordinates a
    uv -= vec2( 0.5 );
    // coordinates circular oscilation
    uv.x += sin( iTime / 5.0 );
    uv.y -= cos( iTime / 5.0 );
    // Set scale/zoom
    float s = 2.0 + sin(iTime / 10.0) * 15.0;
    // TODO: noise displacement
    uv += noise(uv);
    // spherical distortion
    uv *= vec2( cos( length( uv ) * cos( iTime / 10.0 ) * 10.0 ));
    // apply scale
    uv *= s;

    // set colors
    vec3 color_a = vec3( 0.2, 0.2, cos( iTime / 20.0 ));
    vec3 color_b = vec3( 1.0, sin( iTime / 10.0 ), 0.2 + cos( iTime / 2.0 ) * 0.2);

    float y1 = mod( uv.x, 1.0 ) + ( sin( uv.x / s + iTime * 1.0 )) + cos( uv.y / s + iTime );

    vec3 color = color_a;
    color -= vec3( tri( uv, y1 ));
    color += vec3( tri( uv + 0.5, y1));
    color += vec3( 1.0 );
    color *= color_b;

    // Output to screen
    fragColor = vec4(color,1.0);
}

