float antialias(float x) {
    float pixelsize = 4./iResolution.y;
    return smoothstep(pixelsize, 0., x);
}

const float PI = acos(-1.);
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy-0.5*iResolution.xy)/iResolution.y;
    uv*=3.; mouse*=3.;


    float col = 0.;
    float animlen = 27.;
    float timeline = abs(asin(sin(iTime*PI/animlen/2.))*animlen/PI*2.);

    float radius = 1.;
    float sharpness = pow(asin(sin(max(4.7, timeline*1.2)))/PI+1.,8.);
    float offset = sin(iTime*0.65)*0.25+0.25;

    float axis_fade = smoothstep(0., 1., timeline);
    float sphere_fade = smoothstep(2., 3., timeline);
    float point_fade = smoothstep(17., 16., timeline);
    float mirror_fade = smoothstep(9., 10., timeline)*smoothstep(20., 19., timeline);
    float cut_fade = abs(uv.x) > uv.y ? smoothstep(14., 13., timeline) : 1.;
    float rep_fade = smoothstep(16., 17., timeline+uv.y*.1);
    float sector_pulse = smoothstep(.5,0.,abs(timeline-13.5));

    col += antialias(min(abs(uv.x), abs(uv.y)))*.33*axis_fade; //axes
    col += antialias(abs(abs(uv.x)-abs(uv.y))/sqrt(2.))*.66*mirror_fade; //mirror axes
    col += antialias(abs(uv.x)-uv.y)*.2*sector_pulse; //sector to be mirrored
    col += antialias(length(uv+vec2(0, sharpness))-.01)*sphere_fade*point_fade; //circle center
	col += antialias(abs(length(uv+vec2(0, sharpness))-radius-sharpness))*sphere_fade*cut_fade*(1.-rep_fade); //circle
    vec2 uv_mapped = abs(uv); if (uv_mapped.x > uv_mapped.y) uv_mapped = uv_mapped.yx;
    col += antialias(abs(length(uv_mapped+vec2(0, sharpness))-radius-sharpness))*rep_fade; //mirrored circle

	fragColor = vec4(col);
}

