float noise2d (in vec2 pos) {

    vec2 ipos = floor(pos);
    vec2 fpos = fract(pos);

float a = random(ipos);
float b = random(ipos+vec2(1.,0.));
float c = random(ipos+vec2(0.,1.));
float d = random(ipos+vec2(1.,1.));

vec2 u= smoothstep(0.,1.,fpos); 
return mix(a, b, u.x) +
            (c - a)* u.y * (1.0 - u.x) +
            (d - b) * u.x * u.y;
}




float Rectangle ( vec2 position, vec2 uv, vec2 taille,float blur) {

uv *=   1. + position;
vec2 rectanglehaut = smoothstep(taille * blur, taille,uv);
vec2 rectanglebas = smoothstep(taille * blur, taille, 1.-uv);

float rectangle= rectanglebas.y *rectanglebas.x * rectanglehaut.y * rectanglehaut.x;
return rectangle;
}


float sharpRectangle (vec2 position, vec2 uv, vec2 taille) {


float noise = noise2d((vec2(uv.x,uv.y+iTime))*50.);
float noisemoins = noise2d(uv*(9.* (0.8*iTime)));
float noiseplus = noise2d(uv*500.);
float rectangle = Rectangle(position, uv, taille, float (1.0001));
float blurRectangle = 1.- Rectangle(position, uv  , taille*0.86, float (1.4));
rectangle = pow(noise*noiseplus,blurRectangle*(4.*noisemoins));

return rectangle;
}



