const vec3 bola = vec3(0, 1, .7);
const float radio = 0.5;
//distancia entre bolas duplicadas eternamente, la arista de la celda unidad
const float cellS = 5.;

//devuelve el resto de la divisiÃ³n con el signo de x
vec3 rep(vec3 pos, float spa){
	return mod(pos+spa/2.,spa)-spa/2.;
}

//se encarga de hallar el punto mÃ¡s cercano perteneciente a cualquier objeto que se encuentre en escena
//en este momento solo hay un plano y una esfera, la menor de las distancias es la devuelta por la funciÃ³n
//tambiÃ©n devuelte una tonalidad de rojo para distinguir un objeto de otro
vec2 getDistance(vec3 pos){
	float diff = 0.;
	float col = 0.;
	float diffG = abs(pos.y-groundHeight);

	float diffC = length(rep(pos,cellS)-bola) - radio;

	if (diffC > diffG){
        col = 1.;
		diff = diffG;
	}
	else{
		diff = diffC;
	}

	return vec2(abs(diffC), col);
}

//calcula la distancia y con ella determina lo vivo que es el color, si se pasa de la "rayo" devuelve color blanco
//si da contra algo devuelve un color que estÃ¡ objetivamente mal y no proporciona informaciÃ³n asÃ­ que mola
vec3 rayMarch(vec3 dir, vec3 pos){
	float dist = 0.;
	for (int i = 0; i < MAX_MARCH; i++){
		vec2 res = getDistance(pos);
		float surfDist = res.x;
		pos += dir * surfDist;
		dist += surfDist;

		//if it's	 BUT 	it's not
		if (surfDist < MIN_DIST){
			float coef = dist/MAX_DIST;
			return vec3(res.y, coef, coef);
		}
        else{
            if (i == MAX_MARCH-1 || dist > MAX_DIST)
			return vec3(1);
        }
	}
}

void mainImage ( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 relUV = vec2(uv.x-0.5,uv.y-0.5);	// "centramos" las coordenadas UV en el centro de la textura en vez de la esquina
    vec3 rotPixel = normalize(vec3(relUV.x*iResolution.x/iResolution.y, relUV.y, 1));	// vector direcciÃ³n para cada rayo
    vec3 camPos = vec3(cos(iTime)*4.,sin(iTime)*4.,(iTime)*20.2);//owo y paz	// posiciÃ³n cÃ¡mara, mov armÃ³nico simple en relaciÃ³n al tiempo no tiene pÃ©rdida
	fragColor = vec4(rayMarch(rotPixel,camPos),1.0);	// ahÃ­ va el colorsito

}

