void mainImage( out vec4 fragColor, in vec2 fragCoord )
//void main()
{
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    //vec2 p = (2.0 * gl_FragCoord.xy - iResolution.xy) / iResolution.y;
    vec2 uv = fragCoord.xy / iResolution.xy;

    // https://www.shadertoy.com/view/MsdGzn
    // first texture row is frequency data
	float fft  = texture( iChannel0, vec2(uv.x,0.25) ).x; 
	// convert frequency to colors
	vec3 freqCol = vec3( fft, 4.0*fft*(1.0-fft), 1.0-fft ) * fft;

    // perturb coords - highs
    float highsMod = 2.0 * freqCol.r;
    float m = sin(iTime + (p.y * 20.0)) * highsMod;
    m *= smoothstep(-0.35, 0.0, p.y) - (smoothstep(0.0, 0.2, p.y));
    // m *= p.y * 4.0;
    p.x += m;

    // perturb coords - lows
    float lowsMod = 0.1 * freqCol.b;
    float noiseScale = 10.0;
    float n = cnoise(vec2(p.x, p.y + (iTime * 0.5)) * noiseScale) * lowsMod;
    p.x += n;
    p.y -= n;

    vec2 origP = vec2(p.x, p.y);


    // mirror coords
    p.x = abs(p.x);


    bg(p, col, origP);
    cranium(p, col, origP);
    darkLines(p, col, origP);

	fragColor = vec4(col,1.0);
    //gl_FragColor = vec4(col, 1.0);
}

#define MAX_STEPS 100
#define MAX_DISTANCE 10.
#define MIN_SURF .001
#define PI 3.14159

//Rotates the given Vector around the X-Axis
vec3 rotateX(vec3 put,float angle)
{
    return vec3(
        put.x,
        put.y*cos(angle) - put.z*sin(angle),
        put.y*sin(angle) + put.z*cos(angle)
    );
}

//Rotates the given Vector around the Y-Axis
vec3 rotateY(vec3 put,float angle)
{
    return vec3(
        put.x*cos(angle) + put.z*sin(angle),
        put.y,
       -put.x*sin(angle) + put.z*cos(angle)
    );
}

//Rotates the given Vector around the Z-Axis
vec3 rotateZ(vec3 put,float angle)
{
    return vec3(
        put.x*cos(angle) - put.y*sin(angle),
        put.x*sin(angle) + put.y*cos(angle),
        put.z
    );
}

float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}

float sphereDist(vec3 point, vec4 sphere)
{
    return length(point-sphere.xyz)-sphere.w;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float sceneDist(vec3 point)
{
    float box1 = sdBox(mod(point,2.)-vec3(1.,1.,1.),vec3(0.3));
    float scene = box1;

    return scene;
}

float rayMarch(vec3 position, vec3 direction)
{
    float cD = 0.;

    for(int i=0;i<MAX_STEPS;i++)
    {
        vec3 p = position+direction*cD;
        float dS = sceneDist(p);
        cD += dS;
        if(cD>MAX_DISTANCE||dS<MIN_SURF)
            break;
    }

    return cD;
}

vec3 getNormal(vec3 point)
{
    float d = sceneDist(point);
    vec2 e = vec2(0.01,0);

    vec3 n = d-vec3(
        sceneDist(point-e.xyy),
        sceneDist(point-e.yxy),
        sceneDist(point-e.yyx)
    );
    return normalize(n);
}

float getLight(vec3 point, vec3 light)
{
    vec3 l = normalize(light-point);
    vec3 n = getNormal(point);

    float dif = clamp(dot(n,l),0.,1.);
    float d = rayMarch(point+MIN_SURF*n*2.,l);

    if(d<length(light.xyz-point))
        dif *= 0.1;
    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 1.;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( fragCoord - .5*iResolution.xy) /iResolution.y;

    vec3 camera = vec3(0.,0.,iTime*0.5);
    vec3 direction = normalize(rotateX(rotateY(vec3(uv.x*zoom,uv.y*zoom,1.),sin(iTime)*0.1),cos(iTime*0.2)*0.3));

    float d = rayMarch(camera,direction);


    vec3 p = camera+direction*d;

    vec3 light1 = camera;
    float brightness1 = getLight(p,light1);
    // Time varying pixel color
    vec3 col1 = vec3(0.2,0.18,0.1);

    // Output to screen
    fragColor = vec4(col1*brightness1+1./d,1.0);
}

const float GridSize = 32.0f;
const float Exp = 4.0f;
const float InvExp = 1.0f / Exp;


// Hull curve is "y = 1 - x^5"
// Inverse is "x = (1 - y) ^ (1/5)"
// Integral is "y = x - 1/6 x^6"
float hullIntegral(float x) 
{
    return x - (1.0f / (Exp + 1.0f)) * pow( x, (Exp + 1.0f));
}

// Area integral of Blob shape (0, 0) to (x, y). Sign is Positive (TopRight/BottomLeft) or Negative (other)
float areaIntegral(vec2 pos) 
{
    vec2 p = abs( pos);
    float plus = sign( pos.x) * sign( pos.y);

    // only top-right quadrant: calculate xpos of intersection between hull and upper area border
    float intersectPos = pow( clamp( 1.0f - p.y, 0.0f, 1.0f), InvExp);
    // area of the part completely inside the hull, 0 .. intersectPos
    float insideArea = min( p.x, intersectPos) * min( p.y, 1.0f);
    // area under hull function beyond intersect position, intersectPos .. right border
    float remainPos = clamp( p.x, intersectPos, 1.0f);
    float remainArea = hullIntegral( remainPos) - hullIntegral( intersectPos);

    return plus * (insideArea + remainArea);
}

float integral( vec2 blobCenter, float blobRadius, vec2 rectMin, vec2 rectMax)
{
    float invBlobRadius = 1.0f / blobRadius;
    vec2 minpos = invBlobRadius * (rectMin - blobCenter);
    vec2 maxpos = invBlobRadius * (rectMax - blobCenter);

    float totalArea = (maxpos.x - minpos.x) * (maxpos.y - minpos.y);
    float area
        = areaIntegral( maxpos)
        - areaIntegral( vec2( minpos.x, maxpos.y))
        - areaIntegral( vec2( maxpos.x, minpos.y))
        + areaIntegral( minpos);
    return area / totalArea;
}

float doBlob( vec2 center, float radius, vec2 pos)
{
    vec2 off = abs( pos - center) / radius;
    float y = 1.0f - pow( off.x, Exp);
    float i = off.y < y ? 1.0f : 0.0f;
    return i;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Blob controllable by mouse
    vec2 blobPos = iMouse.xy;
    float blobRadius = 150.0f + 50.0f * sin(0.5f * iTime);
	float blobPresence = doBlob( blobPos, blobRadius, fragCoord.xy);

    // a grid of rectangles
    ivec2 gridpos = ivec2(fragCoord.xy / GridSize);
    vec2 rectMin = vec2( gridpos) * GridSize;
    vec2 rectMax = rectMin + vec2( GridSize, GridSize);   

    // calculate the percentage of the area the blob occupies 
    float factor = integral( blobPos, blobRadius, rectMin, rectMax);
    // quick gamma correction
    factor *= factor;

    // base colour is rasterized blob
    vec3 res = vec3( factor, factor, factor);
    // paint real blob shape on top
    res = mix( res, vec3( 1, 0, 0), 0.3f * blobPresence);

    fragColor = vec4( res, 1.0);
}

#define S(a,b,t) smoothstep (a,b,t)
#define sat(x)  clamp(x,0.,1.)

float remap01 (float a, float b, float t)
{

return sat((t-a)/(b-a));

}


float remap (float a, float b, float c, float d, float t)
{

return sat(((t-a)/(b-a))*(d-c)+c);
}

vec2 within (vec2 uv, vec4 rect)
{

return (uv.xy-rect.xy)/(rect.zw-rect.xy);
}

vec4 Brow(vec2 uv,float smile)
{

 uv = within(uv,vec4(.03, .2, .4, .45));

     float offs = mix(.2,0.,smile);
    uv.y += offs;


 float y = uv.y;
    uv.y+= uv.x*mix(.5,.8,smile)-mix(.1,.3,smile);
    uv.x -=mix(0.,.1,smile);
    uv-=.5;

    vec4 col = vec4(0.);

float blur = .1;

    float d1 = length(uv);
    float s1 = S(.45,.45-blur,d1);
    float d2 = length(uv-vec2(.1,-.2)*.7);
    float s2 = S(.5,.5-blur,d2);

    float browmask = sat(s1-s2);

    float colMask = remap01(.7,.8,y)*.75;
    colMask *= S(.6,.9,browmask);
    colMask *=smile;
    vec4 browcol = mix(vec4(.5, .2, .2, 1.), vec4(1., .75, .5, 1.), colMask);



    uv.y += .15 - offs*.5;

    blur += .1*smile;
    d1 = length(uv);
    s1 = S(.45, .45-blur, d1);
    d2 = length(uv-vec2(.1, -.2)*.7);
    s2 = S(.5, .5-blur, d2);
    float shadowMask = sat(s1-s2);

    col = mix(col, vec4(0.,0.,0.,1.), S(.0, 1., shadowMask)*.5);

   col = mix(col,browcol,S(.2,.4,browmask));
return col;
}


vec4 Eye(vec2 uv,float side,vec2 m,float smile)
{

    uv = within(uv,vec4(.03,-0.1,.37,.25));
    uv-=.5;
    uv.x *=side;
    float d = length(uv);



    vec4 irisColor = vec4(.3,.5,1.,1.);



    vec4 col = mix(vec4(1.),irisColor,S(.1,.7,d)*0.5);
    col.a = S(.5,.48,d);

    col.rgb *= 1.-S(.4,.5,d)*0.5*sat(-uv.y-uv.x*side)*0.5;



    //uv.x += sin(iTime*10.)*0.2;
    //uv.y += cos(iTime*10.)*0.2;

    d = length(uv-m*0.5);
    col.rgb = mix(col.rgb,vec3(0.),S(.3,.28,d));

    irisColor.rgb *= 1.+S(0.3,.05,d);
    float irisMask = S(.27,.26,d);
    col.rgb = mix(col.rgb,irisColor.rgb,irisMask);

    d = length(uv-m*0.6);
    float pupilsize = mix(0.4,.16,smile);
    float pupilMask = S(pupilsize,pupilsize*0.8,d);
    pupilMask *= irisMask;
    col.rgb = mix(col.rgb,vec3(0.),pupilMask);

    float t = iTime*3.0;
    vec2 offs = vec2(sin(t+uv.y*25.),sin(t+uv.x*25.));
    offs *= 0.01*(1.-smile); 
    uv+= offs;

    float highlight = S(0.1,0.09,length(uv-vec2(-0.15,0.15)));
    highlight += S(0.07,0.05,length(uv+vec2(-0.08,0.08)));
    col.rgb = mix(col.rgb,vec3(1.0),highlight);



    return col;
}


vec4 Mouth(vec2 uv,float smile)
{
    uv = within(uv,vec4(-.3,-0.4,.3,-.1));
    uv-=.5;
    uv.y*=1.5;
    vec4 col = vec4(.5,.18,.05,1.);
    uv.y-=uv.x*uv.x*2.0*smile;
    uv.x *= mix(2.,1.,smile);

    vec2 tuv = uv;
    float d = length(uv);
    col.a = S(0.5,0.48,d);

    uv.y += abs(uv.x)*(1.-smile)*.7;
    float td = length(uv-vec2(0.,.6));




     vec3 toothcol = vec3(1.)*S(.6,.35,d);
    col.rgb= mix(col.rgb,toothcol,S(.4,.37,td));

    td = length(tuv+vec2(0.,.5));
    col.rgb = mix(col.rgb,vec3(1.,.5,.5),S(.5,.2,td));

    return col;
}

vec4 Head(vec2 uv)
{

    vec4 col = vec4(0.9,.65,.1,1.0);
    float d = length(uv);

    col.a = S(.5,0.49,d);


    float edgeshade = remap01(0.35,0.5,d);
    edgeshade = pow(edgeshade,2.);

    col.rgb *= 1.-edgeshade*0.5;
    float DO = S(.47,.48,d);
    col.rgb = mix(col.rgb, vec3(.6,.3,.1),DO);

    float highlight = S(.41,.405,d);
    highlight *= remap(.41,-0.1,.75,0.,uv.y);
    highlight *= S(.18,.19,length(uv-vec2(.21,.08)));
    col.rgb = mix(col.rgb,vec3(1),highlight);

    d = length(uv-vec2(.25,-.2));
    float cheek = S(.2,.1,d)*.4;

    col.rgb = mix(col.rgb,vec3(1.,.1,.1),cheek);


    return col;
}


vec4 Smiley(vec2 uv,vec2 m,float smile)
{
    float side = sign(uv.x);

    uv.x = abs(uv.x);


    vec4 col = vec4(0.);
    vec4 head = Head(uv);
    vec4 eye = Eye(uv,side,m,smile);
    vec4 mouth = Mouth(uv,smile);
    vec4 brow = Brow(uv,smile);

    col = mix(col, head,head.a);
    col = mix(col, eye,eye.a);
    col = mix(col, mouth,mouth.a);
    col = mix(col, brow,brow.a);



    return col;
}


void mainImage(out vec4 FragColor,in vec2 FragCoord)
{
    vec2 uv = FragCoord/iResolution.xy;
    uv -= vec2(0.5,0.5);

    uv.x *= iResolution.x/iResolution.y;

    vec2 m = iMouse.xy/iResolution.xy;
    m-=.5;

    float smile = cos(iTime)*.5+.5;
    //uv -=m*(.25-dot(uv,uv));
    uv -=m*(.25-length(uv)*length(uv));
    uv.y*=(sin(iTime*3.)*0.03)+1.;
    uv.x*=(cos(iTime*3.)*0.03)+1.;




    FragColor = Smiley(uv,m,smile);

}

// polyhedron morphing.
// octahedron, icosahedron, cuboctahedron, medium polyhedron, 
// hexahedron, dodecahedron, Stella octangula, tetrahedron.
// reference: raymarching:https://www.shadertoy.com/view/XtXGRS
// thanks!

const float pi = 3.14159;
// color palette.
const vec3 black = vec3(0.2);
const vec3 red = vec3(0.95, 0.3, 0.35);
const vec3 orange = vec3(0.98, 0.49, 0.13);
const vec3 yellow = vec3(0.95, 0.98, 0.2);
const vec3 green = vec3(0.3, 0.9, 0.4);
const vec3 lightgreen = vec3(0.7, 0.9, 0.1);
const vec3 purple = vec3(0.6, 0.3, 0.98);
const vec3 blue = vec3(0.2, 0.25, 0.98);
const vec3 skyblue = vec3(0.1, 0.65, 0.9);
const vec3 white = vec3(1.0);
const vec3 aquamarine = vec3(0.47, 0.98, 0.78);
const vec3 turquoise = vec3(0.25, 0.88, 0.81);
const vec3 coral = vec3(1.0, 0.5, 0.31);
const vec3 limegreen = vec3(0.19, 0.87, 0.19);
const vec3 khaki = vec3(0.94, 0.90, 0.55);
const vec3 navy = vec3(0.0, 0.0, 0.5);
const vec3 silver = vec3(0.5);
// points on axis.
const vec3 e1 = vec3(1.0, 0.0, 0.0);
const vec3 e2 = vec3(0.0, 1.0, 0.0);
const vec3 e3 = vec3(0.0, 0.0, 1.0);
const vec3 e123 = vec3(1.0, 1.0, 1.0);
// global points and faces.
vec3 V1, V2, V3, G;
vec3 P12, P23, P31, P123, Q12, Q23, Q31, R12, R23, R31;
vec3 corner0, corner1;
// drawing flags.
int version;
bool verticeFlag; // true: draw V1, V2, V3.
bool cornerFlag; // true: draw e123.
bool subCornerFlag; // true: draw corner0 while expanding.
// for bodyColor.
vec3 bodyColor;
// get parameter and set flags and bodyColor.
vec4 getParam(){
  float time = mod(iTime, 48.0);
  float f = fract(time / 3.0);
  verticeFlag = true;
  cornerFlag = false;
  subCornerFlag = false;
// in ver1 and ver2, draw e123.
  if(time > 24.0 && time < 39.0){ cornerFlag = true; }
// golden ratio - 1.0
  float k = (sqrt(5.0) - 1.0) / 2.0;
// è²ãæ±ºãããRGBã§é©å½ã«ã»ã»
  bodyColor = turquoise;
  if(time < 3.0){
    return vec4(0.0, 0.0, 0.0, 0.0); // octahedron.
  }else if(time < 6.0){
    bodyColor = mix(turquoise, skyblue, f);
    return vec4(k * f, 0.0, 0.0, 0.0);
  }else if(time < 9.0){
    bodyColor = skyblue;
    return vec4(k, 0.0, 0.0, 0.0); // icosahedron.
  }else if(time < 12.0){
    bodyColor = mix(skyblue, blue, f);
    return vec4(k + f * (1.0 - k), 0.0, 0.0, 0.0);
  }else if(time < 15.0){
    bodyColor = blue;
    return vec4(1.0, 0.0, 0.0, 0.0); // cuboctahedron.
  }else if(time < 18.0){
    bodyColor = mix(blue, purple, f);
    return vec4(1.0, 0.0, 0.0, f);
  }else if(time < 21.0){
    bodyColor = purple;
    return vec4(1.0, 0.0, 0.0, 1.0); // medium polyhedron.
  }else if(time < 24.0){
    subCornerFlag = true; // preparate for hexahedron, need more vertices.
    bodyColor = mix(purple, red, f);
    return vec4(1.0, 0.0, f, 1.0);
  }else if(time < 27.0){
    bodyColor = red;
    verticeFlag = false; return vec4(1.0, 0.0, 1.0, 1.0); } // hexahedron.
  else if(time < 30.0){
    bodyColor = mix(red, orange, f);
    return vec4(1.0 + f * (k - 1.0), k * f, 1.0, 1.0);
  }else if(time < 33.0){
    bodyColor = orange;
    return vec4(k, k, 1.0, 1.0); // dodecahedron.
  }else if(time < 36.0){
    bodyColor = mix(orange, yellow, f);
    return vec4(k * (1.0 - f), k * (1.0 - f), 1.0, 1.0);
  }else if(time < 39.0){
    bodyColor = yellow;
    return vec4(0.0, 0.0, 1.0, 1.0); } // Stella octangula.
  else if(time < 42.0){
    subCornerFlag = true; // preparate for tetrahedron, need more vertices.
    bodyColor = mix(yellow, green, f);
    return vec4(0.0, 0.0, 1.0 - f, 1.0);
  }else if(time < 45.0){
    bodyColor = green;
    verticeFlag = false; return vec4(0.0, 0.0, 0.0, 1.0); } // tetrahedron.
  bodyColor = mix(green, turquoise, f);
  return vec4(0.0, 0.0, 0.0, 1.0 - f);
}
// set global properties.
void prepareGlobal(){
  vec4 t = getParam();
  // set version.
  version = 2;
  if(max(t.z, t.w) == 0.0){ version = 0; }
  else if(min(t.z, t.w) == 1.0){ version = 1; }
  // set vertices.
  V1 = vec3(0.0, t.y + 1.0, t.x);
  V2 = vec3(t.x, 0.0, t.y + 1.0);
  V3 = vec3(t.y + 1.0, t.x, 0.0);
  G = (V1 + V2 + V3) / 3.0;
  // prepare faces.
  P12 = normalize(cross(V2 - e3, V1 - e3));
  P23 = normalize(cross(V3 - e1, V2 - e1));
  P31 = normalize(cross(V1 - e2, V3 - e2));
  P123 = normalize(cross(V2 - V1, V3 - V1));
  corner0 = (1.0 - t.z) * G + t.z * e123;
  corner1 = (1.0 - t.w) * G + t.w * e123;
  Q12 = normalize(cross(V1 - corner0, V2 - corner0));
  Q23 = normalize(cross(V2 - corner0, V3 - corner0));
  Q31 = normalize(cross(V3 - corner0, V1 - corner0));
  R12 = normalize(cross(V1 - corner1, V2 - corner1));
  R23 = normalize(cross(V2 - corner1, V3 - corner1));
  R31 = normalize(cross(V3 - corner1, V1 - corner1));
}
// rotation function for camera work.
vec2 rotate(vec2 p, float t){
  return p * cos(t) + vec2(-p.y, p.x) * sin(t);
}
vec3 rotateX(vec3 p, float t){
  p.yz = rotate(p.yz, t);
  return p;
}
vec3 rotateY(vec3 p, float t){
  p.zx = rotate(p.zx, t);
  return p;
}
vec3 rotateZ(vec3 p, float t){
  p.xy = rotate(p.xy, t);
  return p;
}
// sphere. (for vertices)
float sphere(vec3 p, float r){
  return length(p) - r;
}
// bar. (not use)
float bar(vec3 p, vec3 n, float r){
  return length(p - dot(p, n) * n) - r;
}
// halfBar. (not use)
float halfBar(vec3 p, vec3 n, float r){
  return length(p - min(0.0, dot(p, n)) * n) - r;
}
// drawing utility.
// 0: min(union)
// 1: max(intersection)
// 2: minus max(difference)
void updateDist(inout vec3 color, inout float dist, vec3 c, float d, int modeId){
  if(d < dist && modeId == 0){ color = c; dist = d; }
  if(d > dist && modeId == 1){ color = c; dist = d; }
  if(-d > dist && modeId == 2){ color = c; dist = -d; }
}
// map function.
vec4 map(in vec3 p){
  vec3 color;
  float parity = sign(p.x) * sign(p.y) * sign(p.z);
  p = abs(p);
  float t = -1.0;
  float face, vertice; // for calculate.
  if(version == 0 || version == 2){
    face = max(dot(p - e3, P12), max(dot(p - e1, P23), dot(p - e2, P31)));
    updateDist(color, t, bodyColor, face, 1);
  }
  if(version == 0){
    updateDist(color, t, bodyColor, dot(p - G, P123), 1);
  }
  if(version == 1 || (version == 2 && parity > 0.001)){
    face = max(dot(p - corner0, Q12), max(dot(p - corner0, Q23), dot(p - corner0, Q31)));
    updateDist(color, t, bodyColor, face, 1);
    if(subCornerFlag){
      updateDist(color, t, silver, sphere(p - corner0, 0.1), 0);
    }
  }
  if(version == 2 && parity < 0.001){
    face = max(dot(p - corner1, R12), max(dot(p - corner1, R23), dot(p - corner1, R31)));
    updateDist(color, t, bodyColor, face, 1);
    updateDist(color, t, silver, sphere(p - corner1, 0.1), 0);
  }
  if(verticeFlag){
    vertice = min(sphere(p - V1, 0.1), min(sphere(p - V2, 0.1), sphere(p - V3, 0.1)));
    updateDist(color, t, silver, vertice, 0);
  }
  if(cornerFlag){
    updateDist(color, t, silver, sphere(p - e123, 0.1), 0);
  }
  return vec4(color, t);
}
// get normal vector. (mathematical method)
vec3 calcNormal(vec3 p){
  const vec2 eps = vec2(0.0001, 0.0);
  vec3 n;
  n.x = map(p + eps.xyy).w - map(p - eps.xyy).w;
  n.y = map(p + eps.yxy).w - map(p - eps.yxy).w;
  n.z = map(p + eps.yyx).w - map(p - eps.yyx).w;
  return normalize(n);
}
// ray marching.
float march(vec3 ray, vec3 camera){
  const float maxd = 20.0; // searching limit.
  const float precis = 0.001; // precision.
  const int ITERATION = 64; // iteration limit.
  float h = precis * 2.0; // heuristics.

  float t = 0.0; // current distance.

  float result = -1.0;
  for(int i = 0; i < ITERATION; i++){
    if(h < precis || t > maxd){ break; }
    // adding heuristics value.
    h = map(camera + t * ray).w;
    t += h;
  }
  // if t < maxd, it means success(h < precis).
  if(t < maxd){ result = t; }
  return result;
}
// camera move.
void transform(inout vec3 p){
  float angleX = pi * iTime * 0.3;
  float angleY = pi * iTime * 0.15;
  p = rotateX(p, angleX);
  p = rotateY(p, angleY);
}
// èæ¯è²ãã¨ããããããã©ã§ãããã
vec3 getBackground(vec2 p){
// ã¾ãããã ã¨ç©ºéããããããã¦ãæãããªããããªã¼ã»ã»
// ä½ã®è²ã«åããã¦å¤ãã¦ã¿ããã¤ãã£ã¦ã¿ã¾ããã
  vec3 color = mix(bodyColor, white, 0.5);
  return color * (0.3 + p.y * 0.4);
}
// main.
void mainImage(out vec4 fragColor, in vec2 fragCoord){
  vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
  vec3 color;
  // ray vector.
  vec3 ray = normalize(vec3(p, -1.8));
  // camera position.
  vec3 camera = vec3(0.0, 0.0, 4.5);
  // light vector.
  vec3 light = normalize(vec3(0.5, 0.8, 3.0));
  // camera rotation.
  transform(ray);
  transform(camera);
  transform(light);
  // preparation.
  prepareGlobal();
  color = getBackground(p);
  // get ray marching result.
  float t = march(ray, camera);
  // if t > -0.001, it means success. if not, background color.
  if(t > -0.001){
    vec3 pos = camera + t * ray;
    vec3 n = calcNormal(pos);
    // lighting.
    float diff = clamp((dot(n, light) + 0.5) * 0.7, 0.3, 1.0);
    vec3 baseColor = map(pos).xyz;
    baseColor *= diff;
    // fadeout effect.
    color = mix(baseColor, color, tanh(t * 0.02));
  }
  fragColor = vec4(color, 1.0);
}

const float FOVY = 3.14159 * 0.25;
const int RAY_STEPS = 256;
const float SHADOW_HARDNESS = 6.0;


struct Intersection
{
    float t;
    vec3 color;
    vec3 p;
    int object;	// Assign every object in scene a unique ID int
};



void pR(inout vec2 p, float a)
{
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

vec3 rotatePtY(vec3 pt, float a)
{    
    pR(pt.yz, 0.);
    pR(pt.xz, a);
    pR(pt.xy, 0.);

    return pt;
}

vec3 rotatePtX(vec3 pt, float a)
{    
    pR(pt.yz, a);
    pR(pt.xz, 0.);
    pR(pt.xy, 0.);

    return pt;
}

// 2D rotation about the y-axis
vec3 rotateY(vec3 p, float a) {
    return vec3(cos(a) * p.x + sin(a) * p.z, p.y, -sin(a) * p.x + cos(a) * p.z);
}


void rayCast(vec3 eye, vec3 ref, vec2 ndc, out vec3 dir) {
    // Compute camera axis
    vec3 F = ref - eye;
    vec3 R = normalize(cross(F, vec3(0,1,0)));
    vec3 U = normalize(cross(R, F));

    vec3 V = U * length(F) * tan(FOVY * 0.5);
    vec3 H = R * length(F) * tan(FOVY * 0.5) * float(iResolution.x) / iResolution.y;

    vec3 p = ref + ndc.x * H + ndc.y * V; 

    dir = normalize(p - eye);
}


// SHAPE SDFs
float sphere(vec3 p, float r, vec3 c) {
	return distance(p, c) - r;
}

float cube(vec3 p, vec3 c) {
    return length(max(abs(p) - c, 0.0));
}

float squarePlane(vec3 p, vec4 n)
{
  // n must be normalized
  return dot(p,n.xyz) + n.w;
}

float torus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q) - t.y;
}


#define FLOOR_SDF	 	squarePlane(pos + vec3(0.0, 0.2, 0.0), vec4(0., 1., 0., 1.))
#define FLOOR_ID		1
#define SPHERE1_SDF		sphere(pos + vec3(3.0, -0.5, -6.0), 1.5, vec3(0.0, 0.0, 0.0))
#define SPHERE1_ID		2
#define SPHERE2_SDF		sphere(pos + vec3(-1.0, -4.0, -3.0), 1.5, vec3(0.0, 0.0, 0.0))
#define SPHERE2_ID		3
#define CUBE_SDF		cube(rotatePtX(rotatePtY(pos + vec3(1.5, -1.5, 6.0), 0.3), 0.8), vec3(1.2, 1.2, 1.2))
#define CUBE_ID			4
#define TORUS1_SDF		torus(rotatePtX(rotatePtY(pos + vec3(4.0, -3.5, 1.0), 0.5), 0.9), vec2(1.7, 0.5))
#define TORUS1_ID		5
#define TORUS2_SDF		torus(rotatePtX(rotatePtY(pos + vec3(6.0, 0.5, 3.0), -0.4), -0.5), vec2(1.0, 0.4))
#define TORUS2_ID		6
#define LIGHT_SDF		sphere(pos + vec3(-0.5, 0., 0.), 2.0, lightPos)
#define LIGHT_ID		7

void sceneMap3D(vec3 pos, out float t, out int obj, vec3 lightPos) {
    t = FLOOR_SDF;
    obj = FLOOR_ID;

    float t2;
    if ((t2 = SPHERE1_SDF) < t) {
    	t = t2;
      	obj = SPHERE1_ID;
    }

    if ((t2 = SPHERE2_SDF) < t) {
    	t = t2;
      	obj = SPHERE2_ID;
    }

    if ((t2 = CUBE_SDF) < t) {
    	t = t2;
      	obj = CUBE_ID;
    }

    if ((t2 = TORUS1_SDF) < t) {
    	t = t2;
      	obj = TORUS1_ID;
    }

    if ((t2 = TORUS2_SDF) < t) {
    	t = t2;
      	obj = TORUS2_ID;
    }
}


float sceneMap3D(vec3 pos, vec3 lightPos) {
    float t = FLOOR_SDF;

    float t2;
    if ((t2 = SPHERE1_SDF) < t) {
    	t = t2;
    }

    if ((t2 = SPHERE2_SDF) < t) {
    	t = t2;
    }

    if ((t2 = CUBE_SDF) < t) {
    	t = t2;
    }

    if ((t2 = TORUS1_SDF) < t) {
    	t = t2;
    }

    if ((t2 = TORUS2_SDF) < t) {
    	t = t2;
    }

    return t;
}


float shadowMap3D(vec3 pos) {
    float t = SPHERE1_SDF;

    float t2;
    if ((t2 = SPHERE2_SDF) < t) {
    	t = t2;
    }

    if ((t2 = CUBE_SDF) < t) {
    	t = t2;
    }

    if ((t2 = TORUS1_SDF) < t) {
    	t = t2;
    }

    if ((t2 = TORUS2_SDF) < t) {
    	t = t2;
    }

    if ((t2 = FLOOR_SDF) < t) {
    	t = t2;
    }

    return t;
}


vec3 computeNormal(vec3 pos, vec3 lightPos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}


void march(vec3 origin, vec3 dir, out float t, out int hitObj, vec3 lightPos) {
    t = 0.001;

    for (int i = 0; i < RAY_STEPS; ++i) {
    	vec3 pos = origin + t * dir;
        float m; // Minimum distance from this point to an object in the scene
        sceneMap3D(pos, m, hitObj, lightPos);
        if (m < 0.01) {
        	return;
        }
        t += m;
    }
	// We hit nothing
    t = -1.0;
    hitObj = -1;
}


float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = shadowMap3D(origin + t * dir);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}


vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec3 lightVec, vec3 view) {
    float lambert = dot(lightVec, n);
    switch(hitObj) {
        // Sphere 1
        case SPHERE1_ID:
        return vec3(1., 0.7, 0.2) * lambert * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        break;
        // Sphere 2
        case SPHERE2_ID:
        return vec3(0.4, 0.7, 0.3) * lambert * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        break;
        // Cube
        case CUBE_ID:
        return vec3(0.8, 0.4, 0.6) * lambert * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        break;
        // Torus 1
        case TORUS1_ID:
        return vec3(0.6, 0.4, 0.8) * lambert * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        break;
        // Torus 2
        case TORUS2_ID:
        return vec3(0.2, 0.5, 0.8) * lambert * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        break;
        // Light
        case LIGHT_ID:
        return vec3(1.0, 0.88, 0.7);
        break;
        // Floor
        case FLOOR_ID:
        return vec3(1., 1., 1.) * lambert * softShadow(lightVec, p, 0.1, SHADOW_HARDNESS);
        break;
        // Background
        case -1:
        return vec3(0.0);
        break;
    }
    return vec3(1.0);
}


Intersection sdf3D(vec3 dir, vec3 eye, vec3 lightPos) {
	float t;
    int hitObj;
    march(eye, dir, t, hitObj, lightPos);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, lightPos);
    vec3 lightDir = normalize(lightPos - isect);

    vec3 surfaceColor = computeMaterial(hitObj, isect, nor, lightDir, normalize(eye - isect));

    return Intersection(t, surfaceColor, isect, hitObj);
}


