#define P 3.14159265359
float t;
float aa;
vec2 mouse = vec2(0.);

mat2 rot(float a) { float c = cos(a), s = sin(a); return mat2(c,s,-s,c);}
float rand(vec2 st){ return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.585); }

float smoothunion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h);
}

float sph(vec3 p, float s) { return length(p) - s; }
float cyl( vec3 p, float h, float r ) {
    vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
    return max(length(max(abs(p.y) - h,0.)) - .01, length(p.xz) - r) - .01;
}

float ear(vec3 p) {
    p.x *= 1.8;
    p.z *= 1.2;
    float s1 = sph(p + vec3(0.,0.,0.),.2);
    float s2 = sph(p + vec3(0.,0.3,0.),.1);
    float s3 = sph(p + vec3(0.,.6,0.),.3);
    float d = smoothunion(smoothunion(s1,s2,0.45),s3,0.4);
    return d*.5;
}
float headShape(vec3 p) {
    vec3 pp;
    float s1 = sph(p*vec3(.95,1.3,1.25),.45)/1.5;

    pp = p;
    pp.z += pp.y*.2;
    float s2 = sph(pp*vec3(2.1,1.8,2.5) - vec3(0.,.6,0.),.45)/2.5;

    pp.x = abs(pp.x);
    pp.xy *= rot(-.21);
    float s3 = sph(pp*vec3(2.6,2.5,2.9) - vec3(0.8,.7,0.),.45)/2.5;
    float s4 = min(s2,s3);

    pp = p;
    pp.x = abs(pp.x);
    pp.x -= .33;
    pp.xy *= rot(-pp.y*.2);
    pp.xz *= rot(.21);
    float e1 = ear(pp);

    return smoothunion(smoothunion(e1,s1,.05),s4,.15);
}
float body(vec3 p) {
    float bd = cyl(p + vec3(0.,.7,0.),.55,.1 + pow(abs(-p.y)*.2,.89) + .02*(sin(p.y*12.)*.5+.5));
    return max(bd, -(p.y + 1.1 + (sin(p.z*15.)+sin(p.x*15.))*.01))*.99;
}
float smash2d(vec2 p) {
    vec2 pp = p + vec2(.2,.1);
    return max((length(p) - .45),-(min(abs(pp.x),abs(pp.y) + .055) - .1));
}
float smash(vec3 p) {
    return cyl(p + vec3(0.,1.21,0.),.02 + smoothstep(0.,0.001,smash2d(p.xz*.8))*.03,.7);
}

float anim1(float x, float sm) {
  return smoothstep(-sm,sm,fract(x) - .5) + floor(x);
}

int matid = 0;
vec3 eyMap;
float df(vec3 p) {
    p.yz *= rot(-.05);
    p.y += .75;
    p.yz *= rot((mouse.y - .5)*.3);
    p.xz *= rot((mouse.x - .5)*.5);
    p.y -= .75;
    float t1 = cos(t*2.)*.5+.5;

    float smash = smash(p);

    vec3 pp;

    float hs = headShape(p);
    float bd = body(p);

    pp = p;
    pp.x = abs(pp.x);
    eyMap = pp*vec3(1.,1.2,2.) + vec3(-0.13,-0.05,.72);
    float ey = sph(eyMap,.14)/1.2;

    pp = p;
    pp.y -= pow(abs(pp.x),2.8);
    float mo = sph(pp*vec3(1.2 + t1*.05,4.8,1.) + vec3(0.,0.5,0.5),.5)/4.8;

    pp = p;
    pp.x = abs(pp.x) - .25;
    float th = sph(pp*vec3(1,.6,1.) + vec3(0.,0.08,.2),.035);

    float d;
    d = min(bd,hs);
    d = max(d,-mo);
    d = min(d,ey);
    d = min(d,th);
    d = min(d,smash);

    if(d >= ey) matid = 3;
    else if(d >= th) matid = 5;
    else if(d >= mo) matid = 4;
    else if(d >= bd) matid = 1;
    else if(d >= hs) matid = 2;
    else if(d >= smash) matid = 6;

    return d;
}

#define E .0001
vec3 normal(vec3 p) {
    vec2 u = vec2(0.,E); float d = df(p);
    return normalize(vec3(df(p + u.yxx),df(p + u.xyx),df(p + u.xxy)) - d);
}

vec3 reflection(vec3 v){
    vec3 n = normal(v);
    return normalize(v - 2.*dot(v,n)*n);
}

#define MAX_D 5.
#define MIN_D 3.
#define MAX_STEP 70
#define EDP aa*15.
#define LIM .001
vec3 rm(vec3 cam, vec3 rayDir) {
    vec3 c = vec3(0.301, 0.388, 0.635)*1.9 - rayDir.y;
    vec3 pInit = cam + rayDir*MIN_D;
    vec3 p = pInit;
    float prevD = 10e9;
    float nbReflections = 0.;
    for(int i = 0; i < MAX_STEP; i++) {
        float d = df(p);
        if(prevD < EDP && d > prevD) return vec3(0.136,0.069,0.465);
        if(d < LIM) {
            vec3 n = normal(p);
            vec3 light = normalize(vec3(0.244,0.909,-1.080));

            float cl = clamp(dot(n,light),0.3,1.);
            float cv = clamp(dot(n,rayDir) + 1.,0.,1.);

            vec3 col = vec3(1.,0.,0.);
            if(matid == 1) {
                col = vec3(0.301, 0.388, 0.635) + cv*.2;
            } else if(matid == 2) {
                col = vec3(0.854, 0.549, 0.266) + cv*.3;
            } else if(matid == 3) {
                float rs = .05;
                float a = -1.;
                vec2 animShift = vec2(cos(t*30.),cos(t*305.));
                vec2 shift = vec2(cos(a)*rs,sin(a)*rs) + animShift*.002;
                return mix(vec3(0.136,0.069,0.465),vec3(1.), step(0.,(length(eyMap.xy + shift) - .04 - animShift.y*.001)));
            } else if(matid == 4) {
                return vec3(0.603, 0.227, 0.223)*1.5 - cv*.1;
            } else if(matid == 5) {
                col = vec3(1.);
            } else if(matid == 6) {
                col = vec3(1.000,0.762,0.130);
            }

            return col + smoothstep(0.4,0.5,cl)*.2 * (1.+float(i)*.105);//smoothstep(0.4,0.5,cl)
        }
        if(distance(pInit,p) > MAX_D) return c*vec3(0.141,0.292,0.465) + float(i)/100.;
        p += d*rayDir;
        prevD = min(prevD,d);
    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy/iResolution.xy - .5;
