//this is the technique in https://www.shadertoy.com/view/td2fRD
//but a 3d SDF mapping to a 2d sdf, instead of a 4d to a 3d.
//should be easier to understand.

#define FK(k) floatBitsToInt(k)^floatBitsToInt(cos(k))
float hash(float a, float b) {
    int x = FK(a); int y = FK(b);
    return float((x*x-y)*(y*y+x)+x)/2.14e9;
}

//return the SDF for a sphere, or the SDF for an empty region surrounded by spheres
float gated_domain(vec3 p, float scale, bool gated) {
    if (!gated) {
        p.xy = abs(p.xy);
        if (p.x > p.y) p.xy = p.yx;
        p.y -= 1./scale;
    }
    return length(p)-.2;
}

float scene3d(vec3 p) {
    float scale = 2.;
    vec2 id = floor(p.xy*scale);
    p.xy = (fract(p.xy*scale)-0.5)/scale;
    bool gated = hash(id.x, id.y) > 0.;
    return gated_domain(p, scale, gated);
}

vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(ax,p)*ax,p,cos(ro)) + sin(ro)*cross(ax,p);
}

int pittingtype;
float scene2d(vec2 p) {
    float circle = length(p)-1.;

    float top = circle;
    float last = circle;
    for (int i = 0; i < 5; i++) {
        float scale = 1./float(i+1);
        //map 3d coordinates to 4d using the distance to the SDF
    	vec3 p3d = vec3(p, last)/scale;
		//cut out mapped spheres from SDF
        float holes = scene3d(p3d)*scale;
    	top = max(top, -holes);

        if (pittingtype == 0) last = holes; //add pitting to existing pits
        if (pittingtype == 1) last = top; //add pitting everywhere
        if (pittingtype == 2) last = circle; //add pitting only to original surface
    }
    return top;
}

vec3 norm(vec3 p) {
    mat3 k = mat3(p,p,p)-mat3(0.001);
    return normalize(scene3d(p)-vec3(scene3d(k[0]),scene3d(k[1]),scene3d(k[2])));
}

vec3 render3d(vec2 uv) {

    vec3 cam = normalize(vec3(2,uv));
    vec3 init = vec3(-5,0,2);

    cam = erot(cam,vec3(0,1,0), .3);
    cam = erot(cam,vec3(0,0,1), iTime*.1);

    vec3 p = init; 
    bool hit = false;
    //raymarch
    for (int i = 0; i < 100 && !hit; i++) {
        float dist = scene3d(p);
        hit = dist*dist < 1e-6;
        p+=cam*dist*.9;
        if (distance(p,init) > 100.) break;
    }
    //shading
    vec3 n = norm(p);
    return hit ? sin(n)*.5+.5 : vec3(0.1);
}

vec3 shadeDistance(float d) {
    float dist = d*150.0;
    float banding = max(sin(dist), 0.0);
    float strength = sqrt(1.-exp(-abs(d)*2.));
    float pattern = mix(strength, banding, (0.6-abs(strength-0.5))*0.3);

    vec3 color = vec3(pattern);
    color *= d > 0.0 ? vec3(1.0,0.56,0.4) : vec3(0.4,0.9,1.0);

    return color;
}

float antialias(float x) {
    float pixelsize = 3./iResolution.y;
    return smoothstep(-pixelsize, pixelsize, x);
}

vec3 render2d(vec2 uv, vec2 mouse) {
    uv *= 3.; mouse *= 3.;
    vec3 col = shadeDistance(scene2d(uv));

    float mousedist = scene2d(mouse);
    if (iMouse.z > 0.) {
        col *= antialias(distance(mouse, uv) - abs(mousedist))*0.5+0.5;
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy-.5*iResolution.xy)/iResolution.y;
    pittingtype = int(iTime)%3;

    if (uv.x > .0) {
        fragColor.xyz = render3d(uv-vec2(0.45,0.));
    } else {
        fragColor.xyz = render2d(uv+vec2(0.45,0.), mouse+vec2(0.45,0.));
    }
}

// License CC0: Spiral galaxy
//  Would benefit from anti-aliasing but looks okish when I run it in fullscreen in FF
//  Lots of random coding and little thought so the code is kind of messy
#define PI  3.141592654
#define TAU (2.0*PI)

#define TIME (iTime*0.1)

#define LESS(a,b,c) mix(a,b,step(0.,c))

#define SABS(x,k)    LESS((.5/k)*x*x+k*.5,abs(x),abs(x)-k)

#define RESOLUTION   iResolution

const float twirly =2.5;

vec2 toPolar(vec2 p) {
  return vec2(length(p), atan(p.y, p.x));
}

vec2 toRect(vec2 p) {
  return p.x*vec2(cos(p.y), sin(p.y));
}

vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

float noise1(vec2 p) {
  float s = 1.0;

  p *= tanh(0.1*length(p));
  float tm = TIME;

  float a = cos(p.x);
  float b = cos(p.y);

  float c = cos(p.x*sqrt(3.5)+tm);
  float d = cos(p.y*sqrt(1.5)+tm);

  return a*b*c*d;
}

void rot(inout vec2 p, float a) {
  float c = cos(a);
  float s = sin(a);
  p = vec2(c*p.x + s*p.y, -s*p.x + c*p.y);
}

vec2 twirl(vec2 p, float a, float z) {
  vec2 pp = toPolar(p);
  pp.y += pp.x*twirly + a;
  p = toRect(pp);

  p *= z;

  return p;
}

float galaxy(vec2 p, float a, float z) {
  p = twirl(p, a, z);

  return noise1(p);
}

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


vec2 raySphere(vec3 ro, vec3 rd, vec3 center, float radius)
{
    //get the vector from the center of this circle to where the ray begins.
    vec3 m = ro - center.xyz;

    //get the dot product of the above vector and the ray's vector
    float b = dot(m, rd);

    float c = dot(m, m) - radius*radius;

    //exit if r's origin outside s (c > 0) and r pointing away from s (b > 0)
    if(c > 0.0 && b > 0.0) return vec2(-1.0, -1.0);

    //calculate discriminant
    float discr = b * b - c;

    //a negative discriminant corresponds to ray missing sphere
    if(discr < 0.0) return vec2(-1.0);

    //ray now found to intersect sphere, compute smallest t value of intersection
    float normalMultiplier = 1.0;
    float s = sqrt(discr);
    float t0 = -b - s;
    float t1 = -b + s;;

    // return the time t that the collision happened, as well as the surface normal
    return vec2(t0, t1);
}


vec3 stars(vec2 p) {
  float l = length(p);

  vec2 pp = toPolar(p);
  pp.x /= (1.0+length(pp.x))*0.5;
  p = toRect(pp);

  float sz = 0.0075;

  vec3 s = vec3(10000.0);

  for (int i = 0; i < 3; ++i) {  
    rot(p, 0.5);
    vec2 ip = p;
    vec2 n = mod2(ip, vec2(sz));
    float r = rand(n);
    vec2 o = -1.0 + 2.0*vec2(r, fract(r*1000.0));
    s.x = min(s.x, length(ip-0.25*sz*o));
    s.yz = n*0.1;
  }

  return s;
}

float height(vec2 p) {
  float ang = atan(p.y, p.x);
  float l = length(p);
  float sp = mix(1.0, pow(0.75 + 0.25*sin(2.0*(ang + l*twirly)), 3.0), tanh(6.0*l));
  float s = 0.0;
  float a = 1.0;
  float f = 15.0;
  float d = 0.0;
  for (int i = 0; i < 11; ++i) {
    float g = a*galaxy(p, TIME*(0.025*float(i)), f);
    s += g;
    a *= sqrt(0.45);
    f *= sqrt(2.0);
    d += a;
  }

  s *= sp;

  return SABS((-0.25+ s/d), 0.5)*exp(-5.5*l*l);
}

vec3 normal(vec2 p) {
  vec2 eps = vec2(0.000125, 0.0);

  vec3 n;

  n.x = height(p - eps.xy) - height(p + eps.xy);
  n.y = 2.0*eps.x;
  n.z = height(p - eps.yx) - height(p + eps.yx);

  return normalize(n);
}

const vec3 colDust = vec3(1.0, 0.9, 0.75);

vec3 galaxy(vec2 p, vec3 ro, vec3 rd, float d) {
  rot(p, 0.5*TIME);

  float h = height(p);
  vec3 s = stars(p);
  float th = tanh(h);
  vec3 n = normal(p);

  vec3 p3 = vec3(p.x, th, p.y);
  float lh = 0.5;
  vec3 lp1 = vec3(-0.0, lh, 0.0);
  vec3 ld1 = normalize(lp1 - p3);
  vec3 lp2 = vec3(0.0, lh, 0.0);
  vec3 ld2 = normalize(lp2 - p3);

  float l = length(p);
  float tl = tanh(l);

  float diff1 = max(dot(ld1, n), 0.0);
  float diff2 = max(dot(ld2, n), 0.0);

  vec3 col = vec3(0.0);
  col += vec3(0.5, 0.5, 0.75)*h;
//  col += vec3(0.5)*pow(diff1, 20.0);
  col += 0.25*pow(diff2, 4.0);
  col += pow(vec3(0.5)*h, n.y*1.75*(mix(vec3(0.5, 1.0, 1.5), vec3(0.5, 1.0, 1.5).zyx, 1.25*tl)));
//  col += 0.9*vec3(1.0, 0.9, 0.75)*exp(-10*l*l);


  float sr = rand(s.yz);
  float si = pow(th*sr, 0.25)*0.001;
  vec3 scol = sr*5.0*exp(-2.5*l*l)*tanh(pow(si/(s.x), 2.5))*mix(vec3(0.5, 0.75, 1.0), vec3(1.0, 0.75, 0.5), sr*0.6);
  scol = clamp(scol, 0.0, 1.0);
  col += scol*smoothstep(0.0, 0.35, 1.0-n.y);

  float ddust = (h - ro.y)/rd.y;
  if (ddust < d) {
    float t = d - ddust;
    col += 0.7*colDust*(1.0-exp(-2.0*t));
  }

  return col;
}

vec3 render(vec3 ro, vec3 rd) {
  float dgalaxy = (0.0 - ro.y)/rd.y;

  vec3 col = vec3(0);

  if (dgalaxy > 0.0) {
    col = vec3(0.5);
    vec3 p = ro + dgalaxy*rd;

    col = galaxy(p.xz, ro, rd, dgalaxy);
  }

  vec2 cgalaxy = raySphere(ro, rd, vec3(0.0), 0.125);

  float t;

  if (dgalaxy > 0.0 && cgalaxy.x > 0.0) {
    float t0 = max(dgalaxy - cgalaxy.x, 0.0);
    float t1 = cgalaxy.y - cgalaxy.x;
    t = min(t0, t1);
  } else if (cgalaxy.x < cgalaxy.y){
    t = cgalaxy.y - cgalaxy.x;
  }

  col += 1.7*colDust*(1.0-exp(-1.0*t));


  return col;
}


vec3 postProcess(vec3 col, vec2 q)  {
  col=pow(clamp(col,0.0,1.0),vec3(0.75)); 
  col=col*0.6+0.4*col*col*(3.0-2.0*col);  // contrast
  col=mix(col, vec3(dot(col, vec3(0.33))), -0.4);  // satuation
  col*=0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);  // vigneting
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 ro = vec3(0.0, 0.7, 2.0)*0.75;
  vec3 la = vec3(0.0, 0.0, 0.0);
  vec3 up = vec3(-0.5, 1.0, 0.0);
  vec3 ww = normalize(la - ro);
  vec3 uu = normalize(cross(up, ww));
  vec3 vv = normalize(cross(ww,uu));
  vec3 rd = normalize(p.x*uu + p.y*vv + 2.5*ww);


  vec3 col = render(ro, rd);

  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

// All the distance functions from:http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 100
#define MAX_DIST 16.
#define SURF_DIST .002
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define matRotateX(rad) mat3(1,0,0,0,cos(rad),-sin(rad),0,sin(rad),cos(rad))
#define matRotateY(rad) mat3(cos(rad),0,-sin(rad),0,1,0,sin(rad),0,cos(rad))
#define matRotateZ(rad) mat3(cos(rad),-sin(rad),0,sin(rad),cos(rad),0,0,0,1)
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)

vec4 combine(vec4 val1, vec4 val2 ){
    return (val1.w < val2.w)?val1:val2;
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
    return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdBox(vec2 p, vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,0.0)) + min(max(d.x,d.y),0.0);
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdTriangle( in vec2 p, in vec2 p0, in vec2 p1, in vec2 p2 )
{
    vec2 e0 = p1-p0, e1 = p2-p1, e2 = p0-p2;
    vec2 v0 = p -p0, v1 = p -p1, v2 = p -p2;
    vec2 pq0 = v0 - e0*clamp( dot(v0,e0)/dot(e0,e0), 0.0, 1.0 );
    vec2 pq1 = v1 - e1*clamp( dot(v1,e1)/dot(e1,e1), 0.0, 1.0 );
    vec2 pq2 = v2 - e2*clamp( dot(v2,e2)/dot(e2,e2), 0.0, 1.0 );
    float s = sign( e0.x*e2.y - e0.y*e2.x );
    vec2 d = min(min(vec2(dot(pq0,pq0), s*(v0.x*e0.y-v0.y*e0.x)),
                     vec2(dot(pq1,pq1), s*(v1.x*e1.y-v1.y*e1.x))),
                     vec2(dot(pq2,pq2), s*(v2.x*e2.y-v2.y*e2.x)));
    return -sqrt(d.x)*sign(d.y);
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdVesica(vec2 p, float r, float d)
{
    p = abs(p);
    float b = sqrt(r*r-d*d);
    return ((p.y-b)*d>p.x*b) ? length(p-vec2(0.0,b))
                             : length(p-vec2(-d,0.0))-r;
}

// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float ndot(vec2 a, vec2 b ) { return a.x*b.x - a.y*b.y; }
float sdRhombus( in vec2 p, in vec2 b ) 
{
    vec2 q = abs(p);
    float h = clamp((-2.0*ndot(q,b)+ndot(b,b))/dot(b,b),-1.0,1.0);
    float d = length( q - 0.5*b*vec2(1.0-h,1.0+h) );
    return d * sign( q.x*b.y + q.y*b.x - b.x*b.y );
}

float heart(vec2 p, vec3 col) {
    vec2 prevP = p;
    p.y-=0.05;
    float d = sdVesica(p,0.15,0.05);
    d = max(p.y,d);
    p.x = abs(p.x);
    p.x -=0.05;
    float d2 = length(p)-0.05;
    d = min(d,d2);
    return d;
}

float spades(vec2 p, vec3 col) {
    vec2 prevP = p;

    float sc = mix(1.5,0.1,smoothstep(-0.2,0.6,p.y));
    p.x*=sc;
    p.y*=1.3;
    float d = sdTriangle(p,vec2(0.1,0.0),vec2(0.0,0.15),vec2(-0.1,0.0));
  	p = prevP;
    p.x = abs(p.x);
    p.x -=0.05;
    p.y +=0.02;
    float d2 = length(p)-0.05;
    d = opSmoothUnion(d,d2,0.06);

    p = prevP;
    float d3 = sdTriangle(p,vec2(0.03,-0.1),vec2(0.0,-0.04),vec2(-0.03,-0.1));
    d = min(d,d3);

    return d;
}

float clover(vec2 p, vec3 col) {
    vec2 prevP = p;

    float d = length(p-vec2(0.0,0.05))-0.05;
    p.x = abs(p.x);
    p.x -=0.047;
    p.y +=0.02;
    float d2 = length(p)-0.05;
    d = min(d,d2);

    p = prevP;
    float d3 = sdTriangle(p,vec2(0.03,-0.1),vec2(0.0,-0.04),vec2(-0.03,-0.1));
    d = min(d,d3);
    return d;
}

float diamond(vec2 p, vec3 col) {
    vec2 prevP = p;
    float d = sdRhombus(p,vec2(0.05,0.1));
    return d;
}

float trumpIcon(vec2 p, vec3 col, int type) {
    float d = heart(p,col);
  if(type == 2){
        d = spades(p,col);
    } else if(type == 3){
        d = clover(p,col);
    } else if(type == 4){
        d = diamond(p,col);
    }
    return d;
}

vec3 trumpTex(vec2 p, vec3 col, int num, int type) {
    vec2 prevP = p;
    float d = 0.0;
    if(num == 1) {
        d = trumpIcon(p,col,type);
    } else if(num == 2) {
        p.y = mod(p.y,0.8)-0.4;
        d = trumpIcon(p,col,type);
        p = prevP;
        d = max(p.y-0.8,d);
        d = max(-p.y-0.8,d);
    } else if(num == 3) {
        p.y+=-0.2;
    p.y = mod(p.y,0.4)-0.2;
        d = trumpIcon(p,col,type);
    p = prevP;
        d = max(p.y-0.6,d);
        d = max(-p.y-0.6,d);
    } else if(num == 4) {
        p.x = abs(p.x);
        p.x-=0.2;
    p.y = mod(p.y,0.8)-0.4;
        d = trumpIcon(p,col,type);
        p = prevP;
        d = max(p.y-0.8,d);
        d = max(-p.y-0.8,d);
    } else if(num == 5) {
        float d2 = trumpIcon(p,col,type);
    p.x = abs(p.x);
        p.x-=0.2;
    p.y = mod(p.y,0.8)-0.4;
        d = trumpIcon(p,col,type);
        d = min(d,d2);
        p = prevP;
        d = max(p.y-0.8,d);
        d = max(-p.y-0.8,d);
    } else if(num == 6) {
        p.x = abs(p.x);
        p.x-=0.2;
        p.y+=-0.2;
    p.y = mod(p.y,0.4)-0.2;
        d = trumpIcon(p,col,type);
    p = prevP;
        d = max(p.y-0.6,d);
        d = max(-p.y-0.6,d);
    } else if(num == 7) {
        float d2 = trumpIcon(p-vec2(0.0,0.2),col,type);
        p.x = abs(p.x);
        p.x-=0.2;
        p.y+=-0.2;
    p.y = mod(p.y,0.4)-0.2;
        d = trumpIcon(p,col,type);
        d = min(d,d2);
    p = prevP;
        d = max(p.y-0.6,d);
        d = max(-p.y-0.6,d);
    } else if(num == 8) {
    float d2 = trumpIcon(p-vec2(0.0,0.2),col,type);
        float d3 = trumpIcon(p-vec2(0.0,-0.2),col,type);
        p.x = abs(p.x);
        p.x-=0.2;
        p.y+=-0.2;
    p.y = mod(p.y,0.4)-0.2;
        d = trumpIcon(p,col,type);
        d = min(d,min(d2,d3));
    p = prevP;
        d = max(p.y-0.6,d);
        d = max(-p.y-0.6,d);
    } else if(num == 9) {
        float d2 = trumpIcon(p,col,type);
    p.x = abs(p.x);
        p.x-=0.2;
    p.y = mod(p.y,0.27)-0.135;
        d = trumpIcon(p,col,type);
        d = min(d,d2);
    p = prevP;
        d = max(p.y-0.55,d);
        d = max(-p.y-0.55,d);
    } else if(num == 10) {
        float d2 = trumpIcon(p-vec2(0.0,0.25),col,type);
        float d3 = trumpIcon(p-vec2(0.0,-0.25),col,type);
    p.x = abs(p.x);
        p.x-=0.2;
    p.y = mod(p.y,0.27)-0.135;
        d = trumpIcon(p,col,type);
        d = min(d,min(d2,d3));
    p = prevP;
        d = max(p.y-0.55,d);
        d = max(-p.y-0.55,d);
    }
    p = prevP;
    p*=1.5;
    float d2 = trumpIcon(p-vec2(0.6,0.95),col,type);
    p.y*=-1.0;
    float d3 = trumpIcon(p-vec2(-0.6,0.95),col,type);
    d = min(d,min(d2,d3));
    col = mix(col,vec3(0.7,0.0,0.0),S(d,0.0));
    return col;
}


vec4 GetDist(vec3 p) {

    vec3 prevP = p;
    vec2 uv = p.xy;
    uv.y-=1.25;
    uv*=0.5;

    p.z+=iTime*10.0;
    p.z = mod(p.z,4.0)-0.2;

    float y = 0.25;

    // card
    vec3 size = vec3(1.0,1.5,0.001);
    float edge = 0.02;
    float card = sdBox(p-vec3(0.0,1.0+y,0.0),size)-edge;
    vec3 col = trumpTex(uv,vec3(1.0),1,1);
    vec4 resA = vec4(col,card);

    float card2 = sdBox(p-vec3(0.0,1.0+y,1.0),size)-edge;
    vec3 col2 = trumpTex(uv,vec3(1.0),3,2);
    vec4 resB = vec4(col2,card2);

    float card3 = sdBox(p-vec3(0.0,1.0+y,2.0),size)-edge;
    vec3 col3 = trumpTex(uv,vec3(1.0),5,3);
    vec4 resC = vec4(col3,card3);

    float card4 = sdBox(p-vec3(0.0,1.0+y,3.0),size)-edge;
    vec3 col4 = trumpTex(uv,vec3(1.0),7,4);
    vec4 resD = vec4(col4,card4);

    float card5 = sdBox(p-vec3(0.0,1.0+y,4.0),size)-edge;
    vec3 col5 = trumpTex(uv,vec3(1.0),9,2);
    vec4 resE = vec4(col5,card5);

    vec4 model = combine(resA,combine(resB,combine(resC,combine(resD,resE))));
    return model;
}

vec4 RayMarch(vec3 ro, vec3 rd) {
    vec4 dO= vec4(0.0);

    for(int i=0; i<MAX_STEPS; i++) {
        vec3 p = ro + rd*dO.w;
        vec4 dS = GetDist(p);
        dO.w += dS.w;
        dO.xyz = dS.xyz;
        if(dO.w>MAX_DIST || dS.w<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).w;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).w,
        GetDist(p-e.yxy).w,
        GetDist(p-e.yyx).w);

    return normalize(n);
}

vec2 GetLight(vec3 p) {
    vec3 lightPos = vec3(2,8,3);
    vec3 l = normalize(lightPos-p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
    float d = RayMarch(p+n*SURF_DIST*2., l).w;

    float lambert = max(.0, dot( n, l))*0.5;

    return vec2((lambert+dif),max(0.9, 1.0)) ;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;
    float t = mod(iTime,8000.0);
    vec3 col = vec3(0);

    vec3 ro = vec3(0, 8, -4.5);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-30.0));
    ro.xz *= Rot(t*.3+1.0);
    #endif

    vec3 rd = R(uv, ro, vec3(0,1,0), 1.);

    vec4 d = RayMarch(ro, rd);

    if(d.w<MAX_DIST) {
        vec3 p = ro + rd * d.w;

        vec2 dif = GetLight(p);
        col = vec3(dif.x)*d.xyz;
        col *= dif.y;

    } else {
        // background
        col = vec3(0.0,0.6,0.3);
    }

    fragColor = vec4(col,1.0);
}

const float PI = 3.14159265;
const float PI_2 = PI / 2.;

float fold(float x) {
    return abs(mod(x, 2.0) - 1.0);
}

float foldPlus(float x) {
    return log(0.5 + fold(x));
}

float smoothThres(float x, float strength) {
    return smoothstep(0.5 - strength, 0.5 + strength, x);
}

float smoothFold(float x, float strength) {
    return smoothThres(fold(x), strength);
}

vec3 colMap(float x, vec3 a, vec3 b) {
    return a * (1.0 - x) + b * x;
}

vec3 hue(float x) {
    return (vec3(
    	sin(x),
        sin(x + 2. * PI / 3.),
        sin(x + 4. * PI / 3.)
    ) + 1.0) * 0.6;
}

float star(float angle, float d, float roundness) {
    return foldPlus(angle * 10.) + d * roundness;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = fract(iTime / 8.);

    // Normalized pixel coordinates
    float scale = min(iResolution.x, iResolution.y);
    vec2 uv = fragCoord / scale;
	uv -= iResolution.xy / scale / 2.;
    uv *= 2.;
    float dist = log(uv.x*uv.x+uv.y*uv.y); // not real distance, but useful for log spirals
    float angle = atan(uv.x, uv.y) / PI / 2.;
    float angleLayer = t / 5.;
    float offsetLayer = t * 3.;
    float spaceLayer = 0.5;
    vec3 colA = vec3(1.0, 0.3, 0.8);
    vec3 colB = vec3(0.3, 0.7, 1.0);
    vec3 colC = hue(t * 2. * PI);
    vec3 col = vec3(fold(t * 16.));

    if (dist >= -8.) {
        for (float iRing = 0.; iRing < 25.; iRing += 1.) {
            if (star(angle - (iRing * angleLayer), dist, 1.1) < spaceLayer * (iRing - offsetLayer) - 7.){
                switch (int(iRing) % 3) {
                    case 0:
                        col = colA;
                        break;
                    case 1:
                        col = colB;
                        break;
                    case 2:
                        col = colC;
                }
                break;
            }
        }
    }

    // Output to screen
    fragColor = vec4(col, 1.0);
}

