void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 st = fragCoord/iResolution.xy;

    vec3 color = vec3(0.0);

    vec2 pos = vec2(0.5)-st;

    float r = length(pos)*2.0;
    float a = atan(pos.y,pos.x)+iTime;

    float f = abs(cos(a*(sin(iTime*.0001)+1.))*cos(a*3.))*1.360;

    color = vec3( 1.-smoothstep(f,f+0.932,r) ) - vec3( 1.-smoothstep(f,0.268,r-cos(iTime*2.)*0.2-0.2) );

    fragColor = vec4(color,1.0);
}

float random (in vec2 _st) {
    return fract(sin(dot(_st.xy, vec2(0.89,-0.90)))*757.153);
}

// Based on Morgan McGuire @morgan3d
// https://www.shadertoy.com/view/4dS3Wd
float noise (in vec2 _st) {
    vec2 i = floor(_st);
    vec2 f = fract(_st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3. - 2. * f);

    return mix(a, b, u.x) + (c - a)* u.y * (1. - u.x) + (d - b) * u.x * u.y;
}

float fbm ( in vec2 _st) {
    float v = sin(iTime*0.5)*0.2;
    float a = 0.8;
    vec2 shift = vec2(100.0);
    // Rotate to reduce axial bias
    mat2 rot = mat2(cos(0.5), sin(1.0), -sin(0.5), acos(0.5));
    for (int i = 0; i < 5; ++i) {
        v += a * noise(_st);
        _st = rot * _st * 2. + shift;
        a *= 0.01;
    }
    return v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 st = (2.*fragCoord.xy-iResolution.xy) / min(iResolution.x, iResolution.y) * 0.3;

    vec2 co = st;    
    float len;    
    for (int i = 0; i < 3; i++) {
        len = length(co);  
        co.x +=  sin(co.y + iTime * 0.620)*0.3;
        co.y +=  cos(co.x + iTime * 0.164 + cos(len))*0.3;
    }    
    len -= 3.;

    vec3 col = vec3(0.);

    vec2 q = vec2(0.);
    q.x = fbm( st + 1.);
    q.y = fbm( st + vec2(-0.450,0.650));

    vec2 r = vec2(0.);
    r.x = fbm( st + q + vec2(0.570,0.520)+ 0.5*iTime );
    r.y = fbm( st + q + vec2(0.340,-0.570)+ 0.4*iTime);

    for (float i = 0.; i < 3.; i++) {
        r += 1.0 / abs(mod(st.y + st.x, 0.1 * i) * 1000.) * 1.;
        r += 1.0 / abs(mod(st.y - st.x, 0.5 * i) * 10.) * 1.;
    }
    float f = fbm(st+r);

    col = mix(col, cos(len + vec3(0.8, 0.0, 0.5)), 1.);
    col = mix(vec3(0.667,0.527,0.586), vec3(0.101,0.551,0.667), col);

    fragColor = vec4(1.8*(f*f*f+.6*f*f+.5*f)*col,1.);
}

const float INFINITY = 3.402823466e+38;
//const float INFINITY = 1.0 / 0.0;
const float M_PI = 3.1415926;
const float DURATION = 2.;

const float earthRadius = 6360e3;
const float atmosphereRadius = 6420e3;
const float Hr = 7994.;
const float Hm = 1200.;
const vec3 betaR = vec3(3.8e-6, 13.5e-6,33.1e-6);
const vec3 betaM = vec3(21e-6);
// from 'the graphics codex'
// Ray-Sphere Intersection
bool raySphereIntersect(vec3 P, vec3 w, vec3 C, float r, out float t0, out float t1) { 
    vec3 v = P - C;

    float b = 2.0 * dot(w, v);
    float c = dot(v,v) - r * r;

    float d = b * b - 4.0 * c;

    if(d<0.0) return false;

    float s = sqrt(d);
    t0 = (-b-s) * 0.5;
    t1 = (-b+s) * 0.5;

    return true;
}

vec3 computeIncidentLight(vec3 o, vec3 d, float tmin, float tmax, vec3 sunDirection)
{
    float t0, t1;
    if(!raySphereIntersect(o, d, vec3(0.0), atmosphereRadius, t0, t1) || t1 < 0.) return vec3(0.0);
    if(t0>tmin && t0>0.) tmin = t0;
    if(t1<tmax) tmax = t1;
    uint numSamples = 3u;
    uint numSamplesLight = 3u;
    float segmentLength = (tmax-tmin)/float(numSamples);
    float tCurrent = tmin;
    vec3 sumR = vec3(0.0);
    vec3 sumM = vec3(0.0);
    float opticalDepthR = 0.0;
    float opticalDepthM = 0.0;
    float mu = dot(d, sunDirection);
    float phaseR = 3. / (16. * M_PI) * ( 1. + mu * mu);
    float g = 0.76;
    float phaseM = 3. / (8. * M_PI) * ((1.-g*g)*(1. + mu*mu)) / ((2. + g*g) * pow(1. + g*g - 2. * g * mu, 1.5));
    vec3 betaRt = betaR*(1.-(iMouse.y/iResolution.y) + vec3(0.,2.,3.)*(iMouse.x/iResolution.x-.2));
    vec3 betaMt = betaM*(1. + 16.*0.); //iMouse.y/iResolution.y);

    for(uint i = 0u; i < numSamples; ++i) {
        vec3 samplePosition = o + (tCurrent + segmentLength * 0.5) * d;
        float height = length(samplePosition) - earthRadius;
        float hr = exp(-height/ Hr) * segmentLength;
        float hm = exp(-height/Hm) * segmentLength;
        opticalDepthR += hr;
        opticalDepthM += hm;
        float t0Light;
        float t1Light;
        raySphereIntersect(samplePosition, sunDirection, vec3(0.0), atmosphereRadius, t0Light, t1Light);
        float segmentLengthLight = t1Light / float(numSamplesLight);
        float opticalDepthLightR = 0.;
        float opticalDepthLightM = 0.;
        float tCurrentLight = 0.;
        uint j;
        for(j=0u;j<numSamplesLight;++j){
            vec3 samplePositionLight = samplePosition + (tCurrentLight + segmentLengthLight * 0.5) * sunDirection;
            float heightLight = length(samplePositionLight) - earthRadius;
            if(heightLight < 0.0) break;
            opticalDepthLightR += exp(-heightLight / Hr) * segmentLengthLight;
            opticalDepthLightM += exp(-heightLight / Hm) * segmentLengthLight;
            tCurrentLight += segmentLengthLight;
        }
        if(j==numSamplesLight) {
            vec3 tau = vec3(betaRt * (opticalDepthR + opticalDepthLightR) + betaMt * 1.1 * (opticalDepthM + opticalDepthLightM));
            vec3 attenuation = exp(-tau);
            sumR += attenuation * hr;
            sumM += attenuation * hm;
        }	
        tCurrent += segmentLength;
    }

    return vec3(sumR * betaRt * phaseR + sumM * betaMt * phaseM) * 20.;
}

