#define NO_UNROLL(k) (k+min(0,iFrame))
const float blur_strength = 0.1;

#define FK(k) floatBitsToInt(cos(k))^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a);int y = FK(b);
    return float((x*x-y)*(y*y+x)-x)/2.14e9;
}

float super(vec3 p) {
    return sqrt(length(p*p));
}

float box(vec3 p, vec3 d) {
    vec3 q = abs(p)-d;
    return super(max(vec3(0), q)) + min(0., max(q.x, max(q.y, q.z)));
}

float linedist(vec3 p, vec3 a, vec3 b) {
    float k = dot(p-a,b-a)/dot(b-a,b-a);
    return distance(p, mix(a,b,clamp(k,0.,1.)));
}

vec3 erot(vec3 p, vec3 ax, float ro) {
    return mix(dot(ax,p)*ax, p, cos(ro))+sin(ro)*cross(ax,p);
}

float smin(float a, float b, float k) {
    float h = max(0., k-abs(b-a))/k;
    return min(a,b)-h*h*h*k/6.;
}

float domino(vec3 p, mat3 topholes, mat3 bottomholes) {
    float body = box(p, vec3(0.0,0.4,0.9))-0.2;
    float holes = linedist(p, vec3(.2,0.45,0), vec3(0.2,-0.45,0))-0.04; //divider
    for (int i = 0; i < 3; i++) {
        for (int j = 0; j < 3; j++) {
            vec2 holepos = vec2(float(i-1), float(j-1))*0.3;
            if (topholes[i][j] == 1.) {
                holes = min(holes, length(p-vec3(.2,holepos+vec2(0.,0.55)))-0.08);
            }
            if (bottomholes[i][j] == 1.) {
                holes = min(holes, length(p-vec3(.2,holepos+vec2(0.,-0.55)))-0.08);
            }
        }
    }
    return -smin(-body, holes, 0.05);
}

float scene(vec3 p) {
    float table = box(p-vec3(0,0,-1.2), vec3(3,3,0.05))-0.05;
    float d1 = domino(erot(p-vec3(-1,0,0),vec3(0,0,1),3.1415-0.05), mat3(1,1,1,0,0,0,1,1,1), mat3(1,0,1,0,1,0,1,0,1));
    float d2 = domino(erot(p-vec3(1,0,0),vec3(0,0,1),3.1415+0.1), mat3(1,0,1,0,0,0,1,0,1), mat3(0,0,1,0,0,0,1,0,0));
    float dominos = min(d1,d2);
    return min(dominos,table);
}

// suggested from tdhooper. Thanks!
// improve compilation time & overall fps.
const int NORMAL_STEPS = 6;
vec3 norm(vec3 pos) {
    vec3 eps = vec3(.01, 0, 0);
    vec3 nor = vec3(0);
    float invert = 1.;
    for (int i = 0; i < NORMAL_STEPS; i++) {
        nor += scene(pos + eps * invert) * eps * invert;
        eps = eps.zxy;
        invert *= -1.;
    }
    return normalize(nor);
}

vec3 srgb(float r, float g, float b) {
    return vec3(r*r,g*g,b*b);
}

vec3 srgb(float k) {
    return vec3(k*k);
}

vec3 trilinear_texture(vec3 p, vec3 n) {
    return mix(mix(texture(iChannel0,p.xz), texture(iChannel0,p.yz), n.x*n.x), texture(iChannel0,p.xy), n.z*n.z).xyz;
}

vec3 shade(vec3 p, vec3 cam) {
	vec3 n = norm(p);
    vec3 r = reflect(cam, n);
    float ao = smoothstep(-1.,1.,scene(p+n*0.1)/.1);
    float specao = smoothstep(0.,1.,scene(p+r*0.3)/.3)*0.5;

    vec3 tex = trilinear_texture(p*.2,n);
    float specmix = smoothstep(0.4,0.6,tex.y);
    float specex = mix(50.,20.,specmix);

    float diff = ao*length(sin(n*2.5)*0.5+0.5)/sqrt(3.)*0.9+0.1;
    float spec = length(sin(r*2.5)*0.5+0.5)/sqrt(3.);
    return tex*diff + pow(spec, specex)*specao;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 mouse = (iMouse.xy - 0.5*iResolution.xy)/iResolution.y;
    float blur_level = pow(0.5-cos(iTime)*0.5,2.);
    if (fragCoord.y < 10.) {
        float grad = sin(fragCoord.y/10.*3.14);
        float bright = pow(clamp(sin(fragCoord.y/15.*3.14),0.,1.),20.);
        float barpos = smoothstep(-1., 1., blur_level*iResolution.x - fragCoord.x);
        fragColor.xyz = grad*mix(vec3(.1), vec3(.9,0.1,0), barpos) + bright*0.3;
        return;
    }

    vec3 cam = normalize(vec3(2.,uv));
    vec3 init = vec3(-10,0,0);

   	float yrot = 0.6;
    float zrot = 0.9;
    if (iMouse.z > 0.) {
        yrot += -4.*mouse.y;
        zrot += 4.*mouse.x;
    }
    cam = erot(cam, vec3(0,1,0), yrot);
    init = erot(init, vec3(0,1,0), yrot);
    cam = erot(cam, vec3(0,0,1), zrot);
    init = erot(init, vec3(0,0,1), zrot);

    vec3 p = init;
    bool hit = false;
    for (int i = 0; i < 500 && !hit; i++) {
        float dist = scene(p);
        hit = dist*dist < 1e-6;
        p+=dist*cam;
        if (distance(p, init) > 100.) break;
    }

    vec4 col = vec4(0);
    vec3 n = norm(p);
    float maxdist = max(scene(p+n*blur_strength),0.);
    float seed = hash(hash(uv.x,uv.y), float(iFrame));
    for (int i = 0; i < NO_UNROLL(10); i++) {
        float blurfactor = mix(0.0,maxdist,blur_level);
        vec3 p2 = p + n*blurfactor;
        for (int v = 0; v < NO_UNROLL(5); v++) {
            float h1 = hash(seed, hash(float(i),float(v)));
            float h2 = hash(h1,h1);
            float h3 = hash(h2,h2);
            vec3 dir = normalize(tan(vec3(h1,h2,h3))-n);
            float dist = scene(p2);
            p2 += dir*dist;
        }
        col += vec4(shade(p2,cam), 1.);
    }
    fragColor.xyz = hit ? col.xyz/col.w : vec3(0.);
    fragColor.xyz = sqrt(fragColor.xyz);
}

