    else if (min == m2 || min == n2) {obj = 6;}
    else if (min == m3 || min == n3) {obj = 5;}
    else if (min == b7) {obj = 7;}
    else if (min == l0 || min == l1) {obj = 8;}
    else {obj = 2;}

}
float ambientOcclusion(vec3 p, vec3 nor, float k) 
{
    float sum = 0.0;
    for (float i = 0.0; i < 5.0; i++) 
    {
        sum += 1.0 / pow(2.0, i) * (i * 0.15 - sceneMap3D(p + nor * i * 0.15, LIGHT_POS));
    }

    return 1.0 - k * sum;
}

void march(vec3 origin, vec3 dir, out float t, out int hitObj)
{
    t = 0.001;
    for(int i = 0; i < RAY_STEPS; ++i)
    {
        vec3 pos = origin + t * dir;
    	float m;
        sceneMap3D(pos, m, hitObj, LIGHT_POS);
        if(m < 0.01)
        {
            return;
        }
        t += m;
    }
    t = -1.0;
    hitObj = -1;
}

vec3 computeNormal(vec3 pos, vec3 lightPos) {
    vec3 epsilon = vec3(0.0, 0.001, 0.0);
    return normalize( vec3( sceneMap3D(pos + epsilon.yxx, lightPos) - sceneMap3D(pos - epsilon.yxx, lightPos),
                            sceneMap3D(pos + epsilon.xyx, lightPos) - sceneMap3D(pos - epsilon.xyx, lightPos),
                            sceneMap3D(pos + epsilon.xxy, lightPos) - sceneMap3D(pos - epsilon.xxy, lightPos)));
}

vec3 computeMaterial(int hitObj, vec3 p, vec3 n, vec2 uv) {
    float t;
    switch(hitObj) {
        case 0:
        // platform
        return vec3(249., 136., 104.)/255.;
        break;

        case 1:
        // blue mountains
        return vec3(0., 84., 105.)/255.;
        break;

        case 2:
        // white
        return vec3(1.0, 1.0, 1.0);
        break;

        case 3:
        // Floor
        vec3 color = vec3(0.0);
        color += waterFunc(vec3(0, 85., 106.)/255., uv, 3.,  2.5, 1.15, 1.5, false, 1.5);
        color += waterFunc(vec3(24., 181., 177.)/255., uv, 6., 3., .4, 1., true, 0.);
        color += waterFunc(vec3(6., 126., 135.)/255., uv, 8., 4., .2, 1.9, true, 4.5);
        return color;

        case 4:
        // mountains front
        return vec3(0., 104., 106.)/255.;
        break;

        case 5:
        // mountains back
        float y = smoothstep(0.1,0.9, sin(iTime) * 0.3);
        return mix(vec3(85., 196., 179.)/255., vec3(24., 220., 189.)/255., y);
        //return vec3(24., 220., 189.)/255.;
        break;

        case 6:
        // mountains middle
        return vec3(2., 143., 131.)/255.;
        break;

        case 7:
        // platform accent
        return vec3(243., 116., 114.)/255.;
        break;

        case 8:
        // lilypads
        return vec3(125., 178., 53.)/255.;
        break;

        case -1:
        // Background
        return vec3(0.0);
        break;
    }
    return vec3(1.0);
}

float hardShadow(vec3 dir, vec3 origin, float min_t) {
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = sceneMap3D(origin + t * dir, LIGHT_POS);
        if(m < 0.0001) {
            return 0.0;
        }
        t += m;
    }
    return 1.0;
}

float softShadow(vec3 dir, vec3 origin, float min_t, float k) {
    float res = 1.0;
    float t = min_t;
    for(int i = 0; i < RAY_STEPS; ++i) {
        float m = sceneMap3D(origin + t * dir, LIGHT_POS);
        if(m < 0.0001) {
            return 0.0;
        }
        res = min(res, k * m / t);
        t += m;
    }
    return res;
}

float shadow(vec3 dir, vec3 origin, float min_t) {
    #if HARD_SHADOW
    return hardShadow(dir, origin, min_t);
    #else
    return softShadow(dir, origin, min_t, SHADOW_HARDNESS);
    #endif
}

Intersection sdf3D(vec3 dir, vec3 eye, vec2 uv)
{
    float t;
    int hitObj;
    march(eye, dir, t, hitObj);

    vec3 isect = eye + t * dir;
    vec3 nor = computeNormal(isect, LIGHT_POS);

    vec3 material = computeMaterial(hitObj, isect, nor, uv);

    vec3 warmDir = rotateY(normalize(vec3(0.5, 0.75, 1.0)), sin(iTime * 0.5));
    vec3 coolDir = rotateY(normalize(vec3(-1.0, 0.0, -1.0)), sin(iTime * 0.5));

    float warmDot = max(0.0, dot(nor, warmDir));
    float coolDot = max(0.0, dot(nor, coolDir));

    vec3 overallColor = warmDot * vec3(1.0, 0.88, 0.7) * shadow(warmDir, isect, 0.1);
    overallColor += coolDot * vec3(0.05, 0.15, 0.4);
    overallColor *= material;
    overallColor = clamp(overallColor + vec3(0.05, 0.1, 0.15), 0.0, 1.0);
    if (overallColor.x < 0.1 && overallColor.x > 0.045) {overallColor += vec3(0.07, 0.0, 0.07);}

    vec3 sdfColor = overallColor + vec3(0.03);

    return Intersection(t, sdfColor, isect, hitObj);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    // [-1, 1]
    vec2 uv2 = 2.0 * uv - vec2(1.0);

    Intersection aaIsects[4];
    vec3 dir, eye, ref;
    int idx = 0;
    for(float i = 0.0; i < 1.0; i += 0.5) {
        for(float j = 0.0; j < 1.0; j += 0.5) {
            raycast(uv2 + vec2(i, j) / iResolution.xy, dir, eye, ref);
            aaIsects[idx++] = sdf3D(dir, eye + vec3(-30.0, 25.0, 0.0), uv);
        }
    }
    vec3 avgColor = vec3(0.0);
    for(int i = 0; i < 4; ++i) {
        avgColor += aaIsects[i].color;
    }

    float ao = ambientOcclusion(aaIsects[0].p, computeNormal(aaIsects[0].p, LIGHT_POS), 2.0);

    avgColor *= 0.45;
    avgColor *= ao;
    fragColor = vec4(avgColor * step(0.0, aaIsects[0].t), 1.0);
    float fogT = smoothstep(30.0, 50.0, distance(aaIsects[0].p, eye));
    fragColor.rgb = mix(fragColor.rgb, vec3(0.0), fogT);

    if (fragColor.rgb == vec3(0.0)) {	

		vec3 color = vec3(85., 196., 179.)/255.;
		if (rand(fragCoord.xy / iResolution.xy) > 0.996)
		{
			float r = rand(fragCoord.xy);
            //fragColor.rgb = mix(color, vec3(1.0), sin(iTime) * r);
            fragColor.rgb = vec3(1.0);
        } 
        else 
        {
            vec2 st = fragCoord/iResolution.xy;

            // Smooth interpolation between 0.1 and 0.9
            float y = smoothstep(0.5,0.9,st.y);

            fragColor.rgb = mix(vec3(85., 196., 179.)/255., vec3(224., 135., 126.)/255., y);
        }
    } else {

    fragColor.rgb = mix(fragColor.rgb, vec3(85., 196., 179.)/255., fogT);
    }

}

