const float CAM_FAR = 20.0;
const vec3 BACKGROUND = vec3(0.1, 0.1, 0.13);
const int WATER_MARCH_ITERATIONS = 12;
const int WATER_NORMAL_ITERATIONS = 39;
const float PI = 3.14159265359;

const int NUM_PARTICLES = 20;
vec4 ppos[NUM_PARTICLES];

// calculated per fragment
vec3 artifactOffset;
mat3 artifactRotation;
vec3 artifactAxis;
float flicker;
vec3 camFwd;
vec3 camUp;

float rand(float n) {
    return fract(sin(n) * 43758.5453123);
}
float hash(float n) { return fract(sin(n) * 1e4); }
float noise(float x) {
    float i = floor(x);
    float f = fract(x);
    float u = f * f * (3.0 - 2.0 * f);
    return mix(hash(i), hash(i + 1.0), u);
}
mat4 viewMatrix (vec3 dir, vec3 up) { 
    vec3 f = normalize(dir);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4( s,   0.0),
        vec4( u,   0.0),
        vec4(-f,   0.0),
        vec4( 0.0, 0.0, 0.0, 1)
    );
}
mat3 rotationAlign(vec3 d, vec3 z) {
    vec3  v = cross(z, d);
    float c = dot(z, d);
    float k = 1.0/(1.0+c);
    return mat3(v.x*v.x*k + c,     v.y*v.x*k - v.z,    v.z*v.x*k + v.y,
                v.x*v.y*k + v.z,   v.y*v.y*k + c,      v.z*v.y*k - v.x,
                v.x*v.z*k - v.y,   v.y*v.z*k + v.x,    v.z*v.z*k + c    );
}
float intersectPlane(vec3 origin, vec3 direction, vec3 point, vec3 normal) { 
    return clamp(dot(point - origin, normal) / dot(direction, normal), -1.0, 9991999.0); 
}
vec3 calcRay(vec2 uv, float fov, float aspect) {
    uv = uv * 2.0 - 1.0;
    float d = 1.0 / tan(radians(fov) * 0.5);
    return normalize(vec3(aspect * uv.x, uv.y, d));
}
vec2 getWave(vec2 position, vec2 dir, float speed, float frequency, float timeshift) {
    float x = dot(dir, position) * frequency + timeshift * speed;
    float wave = exp(sin(x) - 1.0);
    float dist = wave * cos(x);
    return vec2(wave, -dist);
}
float heightmap(vec2 worldPos, int iterations) {
    const float scale = 0.13;
    vec2 p = worldPos * scale;
    vec2 p2 = (artifactOffset.xz - vec2(0.0, 1.0)) * scale;
    float d = clamp(length(p2 - p) / 0.8, 0.0, 1.0);
    d = (1.0 - smoothstep(0.0, 1.0, d)) * 0.8;
    float angle     = 0.0;
    float freq      = 5.0;
    float speed     = 2.0;
    float weight    = 1.9;
    float wave      = 0.0;
    float waveScale = 0.0;
    vec2 dir;
    vec2 res;
    for (int i = 0; i < iterations; i++) {
        dir = vec2(cos(angle), sin(angle));
        res = getWave(p, dir, speed, freq, time);
        p += dir * res.y * weight * 0.05;
        wave += res.x * weight - d;
        angle += 12.0;
        waveScale += weight;
        weight = mix(weight, 0.0, 0.2);
        freq *= 1.18;
        speed *= 1.06;
    }
    return wave / waveScale;
}
vec3 waterNormal(vec2 p, float eps) {
    vec2 h = vec2(eps, 0.0);
    #define i WATER_NORMAL_ITERATIONS
    return normalize(vec3(heightmap(p - h.xy, i) - heightmap(p + h.xy, i),
                          2.0 * eps,
                          heightmap(p - h.yx, i) - heightmap(p + h.yx, i)));
}
float octahedron(vec3 p, float s) {
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}
void artifact(vec3 p, inout float curDist, inout vec3 glowColor, inout int id) {
    p -= artifactOffset;
    p = artifactRotation * p;
    float dist = octahedron(p, 1.2);
    const float glowDist = 4.8;
    if (dist < glowDist) {
        float d = dist + rand(dist) * 1.7;
        glowColor += vec3(0.75, 0.55, 0.45) * clamp(1.0 - pow((d / glowDist), 5.0), 0.0, 1.0) * 0.035 * flicker; // glow
    }
    if (dist < curDist) {
        curDist = dist;
        id = 1;
    }
}
void particles(vec3 p, inout float curDist, inout vec3 glowColor, inout int id) {
    float t;
    float angle;
    float radius;
    float dist = CAM_FAR;
    const float glowDist = 0.2;
    for (int i = 0; i < NUM_PARTICLES; i++) {
        dist = length(p - ppos[i].xyz) - 0.005;
        if (dist < glowDist && false) {
            float d = dist + rand(dist) * 0.5;
            glowColor += clamp(1.0 - d / glowDist, 0.0, 1.0) * 0.005;
        }
        if (dist < curDist) {
            curDist = dist;
            id = 2;
        }
    }
}
float objects(vec3 p, inout vec3 glowColor, inout int objId) {
    float dist = CAM_FAR;
    artifact(p, dist, glowColor, objId);
    particles(p, dist, glowColor, objId);
    return dist;
}
float artifactDist(vec3 p) {
    p -= artifactOffset;
    p = artifactRotation * p;
    return octahedron(p, 1.2);
}
float objectsDist(vec3 p) {
    return artifactDist(p);
}
vec3 objectsNormal(vec3 p, float eps) {
    vec2 h = vec2(eps, 0);
    #define f artifactDist
    return normalize(vec3(f(p + h.xyy) - f(p - h.xyy),
                          f(p + h.yxy) - f(p - h.yxy),
                          f(p + h.yyx) - f(p - h.yyx)));
}
vec3 objectsColor(int id, vec3 normal, vec3 ray) {
    if (id == 1) { // artifact
        float l = dot(normal, normalize(vec3(0.0, 1.0, 0.5)));
        float hl = mix(0.8, 1.5, l * 0.5 + 0.5);
        return vec3(0.85, 0.65, 0.55) * hl * flicker;
    }
    if (id == 2) {
        return vec3(0.85, 0.65, 0.55) * 1.5;
    }
    return vec3(1.0, 1.0, 0.0); // shouldn't happen
}
void marchObjects(vec3 eye, vec3 ray, float wDepth, inout vec4 color) {
    float dist = 0.0;
    int id;
    vec3 rayPos = eye + ray * dist;
    vec3 c;
    float depth = CAM_FAR;
    vec3 glowColor = vec3(0.0);
    for (int i = 0; i < 100; i++) {
        dist = objects(rayPos, color.rgb, id);
        depth = distance(rayPos, eye);
        if (depth > wDepth) {
            break;
        }
        if (dist < 0.01) {
            vec3 normal = objectsNormal(rayPos, 0.01);
            color = vec4(objectsColor(id, normal, ray), depth);
            return;
        }
        rayPos += ray * dist;
    }
}
vec3 waterColor(vec3 ray, vec3 normal, vec3 p) {
    vec3 color = vec3(0.0);
    float fogDist = length(p - vec3(0.0, 0.0, -6.));
    float dist = 0.0;
    int objId = 0;
    vec3 refl = reflect(ray, normal);
    vec3 rayPos = p + refl * dist;
    vec3 dir = normalize(artifactOffset - p);
    if (length(p.xz - artifactOffset.xz) < 8.5 && dot(refl, dir) > -0.25) { // hacky but this way we aren't reflecting on every single fragment
        for (int i = 0; i < 40; i++) {
            dist = objects(rayPos, color, objId);
            if (dist < 0.01) {
                vec3 objNormal = objectsNormal(rayPos, 0.001);
                color = objectsColor(objId, objNormal, rayPos);
                break;
            }
            rayPos += refl * dist;    
        }
    }
    float fresnel = (0.04 + 0.9 * (pow(1.0 - max(0.0, dot(-normal, ray)), 7.0)));
    vec3 lightOffset = artifactOffset - p;
    float d = length(lightOffset);
    const float r = 14.0;
    float atten = clamp(1.0 - (d*d) / (r*r), 0.0, 1.0);
    atten *= atten;
    vec3 point = vec3(0.75, 0.55, 0.45) * atten * (1.0 + fresnel) * 0.07;
    vec3 ambient = vec3(dot(normal, normalize(vec3(0.0, 1.0, 0.5)))) * max(fresnel, 0.06) * vec3(0.1, 0.5, 1.0) * 0.85;
    float fog = smoothstep(25.0, 6.0, fogDist) / (fogDist * 0.1);

    return color + (point + ambient) * fog;
}
void marchWater(vec3 eye, vec3 ray, inout vec4 color) {
    const vec3 planeNorm = vec3(0.0, 1.0, 0.0);
    const float depth = 3.0;
    float ceilDist = intersectPlane(eye, ray, vec3(0.0, 0.0, 0.0), planeNorm);
    vec3 normal = vec3(0.0);
    if (dot(planeNorm, ray) > -0.05) {
        normal = vec3(0.0);
        color = vec4(vec3(0.0), CAM_FAR);
        return;
    }
    float height = 0.0;
    vec3 rayPos = eye + ray * ceilDist;
    for (int i = 0; i < 80; i++) {
        height = heightmap(rayPos.xz, WATER_MARCH_ITERATIONS) * depth - depth;
        if (rayPos.y - height < 0.1) {
            color.w = distance(rayPos, eye);
            vec3 normPos = (eye + ray * color.w);
            normal = waterNormal(normPos.xz, 0.005);
            color.rgb = waterColor(ray, normal, normPos);
            return;
        }
        rayPos += ray * (rayPos.y - height);
    }
}
vec3 march(vec2 uv, vec3 camPos) {
    mat4 vm = viewMatrix(camFwd, camUp);
    vec3 ray = (vm * vec4(calcRay(uv, 80.0, iResolution.x / iResolution.y), 1.0)).xyz;
    vec4 color = vec4(BACKGROUND, CAM_FAR);
    vec3 waterColor;
    marchWater(camPos, ray, color);
    marchObjects(camPos, ray, color.w, color);
    return color.rgb;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    // simulate  particles
    float pR;
    float pA;
    float gen;
    float t;
    float loop;
    float height;
    vec4 p;
    const float emitR = 1.7;
    for (int i = 0; i < NUM_PARTICLES; i++) {
        t = time * 0.035 + float(i) * 0.07;
        gen = floor(t);
        loop = fract(t);
        pR = rand(gen + float(i)) * emitR;
        pA = rand(float(i)) * PI * 2.0;
        p.xz = vec2(cos(pA), sin(pA)) * pR + vec2(0.0, -5.2);
        height = mix(3.0, 2.3, (abs(pR) / emitR));
        p.y = mix(-3.5, height, sqrt(loop));
        //p.w = cos(loop * PI * 2.0) * min(1.0, 1.0 - (loop / 0.9)); // not currently used :(
        ppos[i] = p;
    }

    // artifact animation
    t = time;
    float s = sin(t);
    float c = cos(t);
    artifactRotation = mat3x3(c,0,s,
                              0,1,0,
                             -s,0,c);
    artifactRotation *= rotationAlign(vec3(0.0, 1.0, 0.0), vec3(sin(t) * 0.2, 1.0, cos(t) * 0.2 + 0.3));
    artifactOffset = vec3(sin(time) * 0.4, cos(time * 0.5) * 0.3 - 1.7, -6.);
    flicker = mix(1.0, 1.1, sin(time * 2.0) * 0.5 + 0.5) + noise(time * 4.0) * -0.1 + 0.05;

    // camera animation
    camFwd = vec3(0.0, 0.7 + noise(time * 0.8 + 4.0) * 0.08 - 0.04, 1.0);
    camUp = vec3(noise(time * 1.2) * 0.02 - 0.01, 1.0, 0.0);

    // scene
    vec3 color = march(uv, vec3(0.0, 1.9, 1.0));

    // vignette
    color -= (length(uv - 0.5) - 0.3) * 0.05;
    fragColor = vec4(color, 1.0);
}

#define SCALE 15.0
#define TILESIZE 30.0
#define PI 3.14159265359

// COLORS
vec3 yellow = vec3(241./255.,242./255.,208./255.);
vec3 green = vec3(127./255., 173./255., 110.0/255.);
vec3 laurel = vec3(146./255., 173./255., 148.0/255.);
vec3 bg = vec3(226./255., 214./255., 134.0/255.);
vec3 middlegreen = vec3(88./255.,129./255.,87./255.);
vec3 huntergreen = vec3(68./255.,100./255.,74./255.);
vec3 blackolive = vec3(53./255.,56./255.,49./255.);

float hash21(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float rect(vec2 uv, vec2 bottomLeft, vec2 topRight) {
    vec2 step1 = step(bottomLeft, uv);
    vec2 step2 = step(1.0-topRight, 1.0-uv);
    return step1.x*step1.y*step2.x*step2.y;
}

vec4 tile1(vec2 uvT) {
    // Draw First Tile flowers
    vec3 col = vec3(0.0);
    float mask = rect(uvT, vec2(2.0, 10.0), vec2(6.0, 14.0));
    mask += rect(uvT, vec2(10.0, 10.0), vec2(14.0, 14.0));
    mask += rect(uvT, vec2(6.0, 6.0), vec2(10.0, 10.0));
    mask += rect(uvT, vec2(6.0, 14.0), vec2(10.0, 16.0));

    mask += rect(uvT, vec2(18.0, 20.0), vec2(22.0, 24.0));
    mask += rect(uvT, vec2(22.0, 24.0), vec2(26.0, 26.0));
    mask += rect(uvT, vec2(22.0, 16.0), vec2(26.0, 20.0));
    mask += rect(uvT, vec2(26.0, 20.0), vec2(30.0, 24.0));
    col = mix(col, yellow, mask);
    float mask2 = rect(uvT, vec2(2.0, 8.0), vec2(6.0, 10.0));
    mask2 += rect(uvT, vec2(6.0, 10.0), vec2(10.0, 14.0));
    mask2 += rect(uvT, vec2(10.0, 8.0), vec2(14.0, 10.0));
    mask2 += rect(uvT, vec2(6.0, 4.0), vec2(10.0, 6.0));

    mask2 += rect(uvT, vec2(18.0, 18.0), vec2(22.0, 20.0));
    mask2 += rect(uvT, vec2(22.0, 20.0), vec2(26.0, 24.0));
    mask2 += rect(uvT, vec2(26.0, 18.0), vec2(30.0, 20.0));
    mask2 += rect(uvT, vec2(22.0, 14.0), vec2(26.0, 16.0));
    col = mix(col, green, mask2);
    return vec4(col,clamp(mask+mask2, 0.0, 1.0));
}

vec4 tile2(vec2 uvT) {
    // Draw Second Tile flowers
    vec3 col = vec3(0.0);
    float mask = rect(uvT, vec2(0.0, 22.0), vec2(12.0, 26.0));
    mask += rect(uvT, vec2(4.0, 18.0), vec2(8.0, 28.0));

    mask += rect(uvT, vec2(16.0, 12.0), vec2(28.0, 16.0));
    mask += rect(uvT, vec2(20.0, 8.0), vec2(24.0, 18.0));
    col = mix(col, yellow, mask);
    float mask2 = rect(uvT, vec2(0.0, 20.0), vec2(4.0, 22.0));
    mask2 += rect(uvT, vec2(4.0, 22.0), vec2(8.0, 26.0));
    mask2 += rect(uvT, vec2(4.0, 16.0), vec2(8.0, 18.0));
    mask2 += rect(uvT, vec2(8.0, 20.0), vec2(12.0, 22.0));

    mask2 += rect(uvT, vec2(16.0, 10.0), vec2(20.0, 12.0));
    mask2 += rect(uvT, vec2(20.0, 12.0), vec2(24.0, 16.0));
    mask2 += rect(uvT, vec2(20.0, 6.0), vec2(24.0, 8.0));
    mask2 += rect(uvT, vec2(24.0, 10.0), vec2(28.0, 12.0));
    col = mix(col, green, mask2);
    return vec4(col, clamp(mask+mask2, 0.0, 1.0));
}

vec4 tile3(vec2 uvT) {
    // Draw Third Tile flowers
    vec3 col = vec3(0.0);
    float mask = rect(uvT, vec2(0.0, 18.0), vec2(12.0, 22.0));
    mask += rect(uvT, vec2(4.0, 14.0), vec2(8.0, 24.0));
    mask += rect(uvT, vec2(18.0, 24.0), vec2(30.0, 28.0));
    mask += rect(uvT, vec2(22.0, 20.0), vec2(26.0, 30.0));
    mask += rect(uvT, vec2(10.0, 6.0), vec2(22.0, 10.0));
    mask += rect(uvT, vec2(14.0, 2.0), vec2(18.0, 12.0));
    col = mix(col, yellow, mask);
    float mask2 = rect(uvT, vec2(0.0, 16.0), vec2(4.0, 18.0));
    mask2 += rect(uvT, vec2(4.0, 18.0), vec2(8.0, 22.0));
    mask2 += rect(uvT, vec2(8.0, 16.0), vec2(12.0, 18.0));
    mask2 += rect(uvT, vec2(4.0, 12.0), vec2(8.0, 14.0));
    mask2 += rect(uvT, vec2(18.0, 22.0), vec2(22.0, 24.0));
    mask2 += rect(uvT, vec2(22.0, 24.0), vec2(26.0, 28.0));
    mask2 += rect(uvT, vec2(26.0, 22.0), vec2(30.0, 24.0));
    mask2 += rect(uvT, vec2(22.0, 18.0), vec2(26.0, 20.0));
    mask2 += rect(uvT, vec2(10.0, 4.0), vec2(14.0, 6.0));
    mask2 += rect(uvT, vec2(14.0, 6.0), vec2(18.0, 10.0));
    mask2 += rect(uvT, vec2(18.0, 4.0), vec2(22.0, 6.0));
    mask2 += rect(uvT, vec2(14.0, 0.0), vec2(18.0, 2.0));
    col = mix(col, green, mask2);
    return vec4(col, clamp(mask+mask2, 0.0, 1.0));
}

float weed(vec2 uvT, vec2 pos, float speed) {
	// Grass movement
    // let stop and move again periodically
    float ct1 = iTime*2.0*speed;
    float ct2 = iTime*1.0*speed;
    float stopEveryAngle1 = PI/2.;
    float stopEveryAngle2 = PI;
    float stopRatio1 = 0.9;
    float stopRatio2 = 0.9;
    float tm1 = (floor(ct1) + smoothstep(0.0, 1.0-stopRatio1, fract(ct1)) )*stopEveryAngle1;
    float tm2 = (floor(ct2) + smoothstep(0.0, 1.0-stopRatio2, fract(ct2)) )*stopEveryAngle2;

    float x1 = -4.0*cos(tm1);
    float x2 = -2.0*cos(tm2);
    float mask = rect(uvT, vec2(pos.x+x1, pos.y+6.0), vec2(pos.x+2.0+x1, pos.y+10.0));
    mask += rect(uvT, vec2(pos.x+x2, pos.y+3.0), vec2(pos.x+2.0+x2, pos.y+6.0));
    mask += rect(uvT, vec2(pos.x, pos.y), vec2(pos.x+3.0, pos.y+3.0));
	return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;
    vec2 uvStatic = uv;

    // TIME
    uv.x += iTime/8.0;
    uv.y += iTime/32.0;
    uv *= SCALE;
    vec2 guv = fract(uv);
    vec2 id = floor(uv);

    // Background
    vec2 uvBack = uv*SCALE/1.0;
    vec2 guvBack = fract(uvBack);
    vec2 idB = floor(uvBack);
    vec2 uvTBack = guvBack*TILESIZE;
    vec3 col = huntergreen;
    float noiseGreen = hash21(idB);
    if (noiseGreen < 0.6) col = middlegreen;
    else if (noiseGreen < 0.8) col = huntergreen;
    else if (noiseGreen < 0.9) col = blackolive;

    float noise = hash21(id);
    if (noise < 0.5) col.r -= noise*0.1;
    noise = hash21(id+0.4);
    if (noise < 0.4) col.b -= noise*0.1;

    vec2 uvT = guv*TILESIZE;

    // Flowers
    vec4 flower;
    float isTile = hash21(id+0.1);
    if (isTile < 0.1) {
        flower = tile3(uvT);
    	col = mix(col, flower.rgb, flower.a);
    } else if (isTile < 0.22) {
        flower = tile2(uvT);
    	col = mix(col, flower.rgb, flower.a);
    } else if (isTile < 0.35) {
        flower = tile1(uvT);
    	col = mix(col, flower.rgb, flower.a);
    }

    // Weed
    float r1 = hash21(id+0.34);
    float r2 = hash21(id+0.71);
    vec2 pos = vec2(4.8+19.0*r1, 18.0*r2);
    //float speed = 1.5*sin(id.x+iTime*PI/2.)+2.0;
    float maskWeed = weed(uvT, pos, 1.0);
    r1 = hash21(id+0.64);
    r2 = hash21(id+0.55);
    maskWeed += weed(uvT, vec2(4.8+19.0*r1, 18.0*r2), 1.0);
   	maskWeed = clamp(maskWeed, 0.0, 1.0);
    col = mix(col, bg, maskWeed);

    fragColor = vec4(col,1.0);
}

float odd(in float number){

	return step(1.0,mod(number,2.0));
}

vec2 zoomAndMove(in vec2 st,in float scale){

    st *= scale; 
    st.x += max(0.0,odd(st.y)*sin(iTime));
    st.y += min(0.0, odd(st.x)*sin(iTime));
	return fract(st);
}


float circle(in vec2 st, in vec2 origin, in float radius){

	float dist = length(st-origin);
    return step(radius, dist);
}






