#define ITER 64.
#define time iTime
#define width 5.
#define height 2.
#define PI acos(-1.)
#define BPM 165./60.

#define CRAZYNESS

// taken from YX here : https://www.shadertoy.com/view/tdlXW4
// rough shadertoy approximation of the bonzomatic noise texture
vec4 texNoise(vec2 uv)
{
    float f = 0.;
    f += texture(iChannel0, uv*.125).r*.5;
    f += texture(iChannel0, uv*.25).r*.25;
    f += texture(iChannel0, uv*.5).r*.125;
    f += texture(iChannel0, uv*1.).r*.125;
    f=pow(f,1.2);
    return vec4(f*.45+.05);
}

float hash21 (vec2 x)
{return fract(sin(dot(x,vec2(18.4,16.4)))*1524.4);}

mat2 rot( float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

void mo(inout vec2 p, vec2 d)
{
    p = abs(p)-d;
    if(p.y>p.x) p = p.yx;
}

float stmin(float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5*(u+a+abs(mod(u-a+st,2.*st)-st)));
}

float box (vec3 p, vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

float cyl (vec3 p, float r, float h)
{return max(length(p.xy)-r,abs(p.z)-h);}


float tore (vec3 p, vec2 t)
{return length(vec2(length(p.xz)-t.x,p.y))-t.y;}

float sid,g1=0.;
float spot (vec3 p)
{
    float per = 10.;
    sid = floor(p.z/per);
    p.z = mod(p.z,per)-per*0.5;
    p.y = abs(p.y)-height*0.9;
    p.x = abs(p.x)-width*0.98;
    p.z = abs(p.z)-1.;
    float d = length(p)-0.2;
    g1 += 0.07/(0.07+d*d);
    return d;
}

float g2=0.;
float pipes (vec3 p)
{
    float per = 10.;
    p.z = mod(p.z,per)-per*0.5;
    vec3 pp = p;
    p.x = abs(p.x)-width;
    float d = tore(p,vec2(2.,0.2));

    p = pp;
    mo(p.xy, vec2(2.5));
    p.xz*=rot(PI/2.);
    d = min(d, tore(p.xzy, vec2(1., 0.2)));

    p = pp;
    mo(p.xy, vec2(1.5));
    d = min(d, cyl(p,0.1,1e10));

    g2 += 0.001/(0.001+d*d);
    return d;
}

float g3 = 0.;
float prim1 (vec3 p)
{
    float per = 10.;
    float id = floor((p.z-per*0.5)/per);
    p.xy *= (mod(id,2.)==0.) ? rot(p.z*0.1+time) : rot(p.z*0.1-time);
    p.z = mod(p.z-per*0.5,per)-per*0.5;
    mo(p.xy, vec2(width*(0.1+exp(-fract(time*BPM))*0.4)));
    p.xy *= rot(PI/4.);
    float d = cyl(p.xzy, 0.1, height*2.);
    g3 += 0.01/(0.01+d*d);
    return d;
}

float g4 = 0.;
float gem (vec3 p)
{
    p.z -= 5.-time*3.;
    p.xy *= rot(time);
    mo(p.xz, vec2(0.1));
    mo(p.xy,vec2(0.2));
    p.x -= exp(-fract(time*BPM));
    p.xz *= rot(time);

    float d =  dot(p,normalize(sign(p)))-0.1;
    d = stmin(d,tore(p,vec2(0.3,0.1)),0.2,5.);
    g4 += 0.1/(0.1+d*d);
    return d;
}

float room (vec3 p)
{
    float per = 10.;
    float d = -box(p,vec3(width,height,1e10));
    vec3 pp = p;
    p.z = mod(p.z-per*0.5,per)-per*0.5;
    p.x = abs(p.x)-width*0.98;
    d = min(d, box(p,vec3(1.,height, 0.5)));

    p = pp;
    p.x = abs(p.x)-width*0.98;
    d = stmin(d, box(p,vec3(0.5,0.5,1e10)),0.5,5.);

    p = pp;
    p.y = abs(p.y)-height*0.98;
    d = stmin(d, box(p,vec3(0.5,0.5,1e10)),0.5,3.);

    return d;
}

float SDF (vec3 p)
{
    p.x+= sin(p.z*0.5)*0.5;
    float d = min(gem(p),min(prim1(p),stmin(pipes(p),min(room(p), spot(p)),0.2,4.)));
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
    uv -= 0.5;
    uv /= vec2(iResolution.y / iResolution.x, 1);

    float dither = hash21(uv);

    #ifdef CRAZYNESS
    uv = mix(abs(uv)-vec2(0.2,0.3), uv, floor(sin(time*(PI/4.)*BPM)+1.));
    uv = mix(uv*rot(PI/4.),uv*rot(-PI/4.),floor(sin(time*2.*PI*BPM)+1.));
    #endif

    vec3 ro = vec3(0.001,0.001,-time*3.),
        rd = normalize(vec3(uv,1.)),
        col = vec3(0.),
        p = ro;

    float shad,d = 0.;
    for (float i=0.; i<ITER; i++)
    {
        d = SDF(p);
        if (d<0.01)
        {
            shad = i/ITER;
            break;
        }
        d *= 0.7+dither*0.05;
        p += d*rd;
    }

    float t = length(ro-p);

    col = vec3(shad);
    col += (mod(sid,2.) == 0.) ? g1*vec3(0.1,0.6,0.7) : g1*vec3(0.7,0.2,0.75);
    col += g2*0.5;
    col += g3*0.5;
    vec3 gemcol = mix(vec3(0.1,0.6,0.7), vec3(0.7,0.2,0.75), floor(sin(time*PI*BPM)+1.));
    col += g4*texNoise(p.xy*0.2).r*gemcol;

    col = mix(col,vec3(0.1),1.-exp(-0.01*t*t));

    fragColor = vec4(clamp(col,0.,1.), 1.);
}

vec2 hash22(vec2 p)
{
    p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)));

    return -1.0 + 2.0 * fract(sin(p)*43758.5453123);
}


float perlin_noise(vec2 p)
{
	vec2 pi = floor(p);
    vec2 pf = p-pi;

    vec2 w = pf*pf*(3.-2.*pf);

    float f00 = dot(hash22(pi+vec2(.0,.0)),pf-vec2(.0,.0));
    float f01 = dot(hash22(pi+vec2(.0,1.)),pf-vec2(.0,1.));
    float f10 = dot(hash22(pi+vec2(1.0,0.)),pf-vec2(1.0,0.));
    float f11 = dot(hash22(pi+vec2(1.0,1.)),pf-vec2(1.0,1.));

    float xm1 = mix(f00,f10,w.x);
    float xm2 = mix(f01,f11,w.x);

    return mix(xm1,xm2,w.y);
}

// Official HSV to RGB conversion 
vec3 hsv2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );

	return c.z * mix( vec3(1.0), rgb, c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uv2 = uv + perlin_noise(1.2 * vec2(uv.x + 38.913 + iTime * 0.04, uv.y + 81.975 + iTime * 0.04));
    uv2 = uv2 + vec2(iTime * 0.01, 0.0);

    // Time varying pixel color
    float f = perlin_noise(2.0 * uv2);

    // Output to screen
    f = (f + iTime * .01) * 8.0;
    f = f - floor(f);
    float f2 = 0.02;
    f2 = f2 + 0.08 * smoothstep(0.08, 0.12,f);
    f2 = f2 + 0.08 * smoothstep(0.21, 0.25, f);
    f2 = f2 + 0.06 * smoothstep(0.33, 0.37, f);
    f2 = f2 + 0.23 * smoothstep(0.46, 0.50, f);
    f2 = f2 + 0.08 * smoothstep(0.58, 0.62, f);
    f2 = f2 + 0.12 * smoothstep(0.71, 0.75, f);
    f2 = f2 + 0.12 * smoothstep(0.83, 0.87, f);
    f2 = f2 + 0.23 * smoothstep(0.96, 1.00, f);
    vec3 col2 = hsv2rgb(vec3(f2, 1.0, 1.0));

    fragColor = vec4(col2.rgb, 1.0);
}

#define PI 3.14159265359

const float rad = .6;
const float bRad = .07;
const float speed = 5.;

const float lRad = 3.;
const float lInt = .4;
const float lAtten = 5.;

const vec4 c1 = vec4(27., 231., 255., 255.) / 255.;
const vec4 c2 = vec4(227., 108., 201., 255.) / 255.;
const vec4 bg = vec4(48., 13., 68., 255.) / 255.;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;

    //Ball point and angle
    float t = iTime * speed;
    vec2 p = vec2(cos(t), sin(t)) * rad;

    //Trail
    float rAngle = dot(normalize(uv), normalize(p)) * 0.5 + 0.5;
    rAngle *= ceil(cross(vec3(uv, 0.), vec3(p, 0.)).z);

    float gAngle = dot(normalize(uv), normalize(-p)) * 0.5 + 0.5;
    gAngle *= ceil(cross(vec3(uv, 0.), vec3(-p, 0.)).z);

    //Use trail in path
    float rTRad = mix(0., bRad, rAngle);
    rAngle *= clamp(ceil(1. - abs(length(uv) - rad) / rTRad), 0., 1.);

    float gTRad = mix(0., bRad, gAngle);
    gAngle *= clamp(ceil(1. - abs(length(uv) - rad) / gTRad), 0., 1.);

    //Ball colors
    float rb = ceil(bRad - length(uv - p));
    rb = clamp(rb + rAngle, 0., 1.);

    float gb = ceil(bRad - length(uv + p));
    gb = clamp(gb + gAngle, 0., 1.);

    //Lighting
    float rDist = clamp(lRad - length(uv - p), 0., lRad) / lRad;
    rb += pow(rDist, lAtten) * lInt;

    float gDist = clamp(lRad - length(uv + p), 0., lRad) / lRad;
    gb += pow(gDist, lAtten) * lInt;

    //Color output
    vec4 c = c1 * rb + c2 * gb;
    c += bg * (1. - c.a);
    fragColor = c;
}

