// Simple LoDless Volumetric clouds. It does NOT perform level of detail (LOD) for faster rendering

// A white noise function.
float rand(vec3 p) {
    return fract(sin(dot(p, vec3(12.345, 67.89, 412.12))) * 42123.45) * 2.0 - 1.0;
}

// A perlin noise function. Since we are not using textures, we am gonna sample 8 corners of a cube.
float perlin(vec3 p) {
    vec3 u = floor(p);
    vec3 v = fract(p);
    vec3 s = smoothstep(0.0, 1.0, v);

    float a = rand(u);
    float b = rand(u + vec3(1.0, 0.0, 0.0));
    float c = rand(u + vec3(0.0, 1.0, 0.0));
    float d = rand(u + vec3(1.0, 1.0, 0.0));
    float e = rand(u + vec3(0.0, 0.0, 1.0));
    float f = rand(u + vec3(1.0, 0.0, 1.0));
    float g = rand(u + vec3(0.0, 1.0, 1.0));
    float h = rand(u + vec3(1.0, 1.0, 1.0));

    return mix(mix(mix(a, b, s.x), mix(c, d, s.x), s.y),
               mix(mix(e, f, s.x), mix(g, h, s.x), s.y),
               s.z);
}

// The fbm function. iq unrolled the loop, so I am doing it too.
// If you wonder what fbm is, check this out: https://thebookofshaders.com/13/
float fbm(vec3 p) {
    vec3 off = vec3(0.0, 0.1, 1.0) * iTime;
    vec3 q = p - off;

    // fbm
    float f = 0.5 * perlin(q); q *= 2.0;
    f += 0.25 * perlin(q); q *= 2.0;
    f += 0.125 * perlin(q); q *= 2.0;
    f += 0.06250 * perlin(q); q *= 2.0;
    f += 0.03125 * perlin(q);
    return clamp(f - p.y, 0.0, 1.0);
}

// volmetric raymarching, which is kinda like the core algorithm.
// I ripped lighting calculations and other stuffs off, so this is bare bones raymarching
vec3 volumetricTrace(vec3 ro, vec3 rd) {
    // at first there's no depth
    float depth = 0.0;

    // and the color's black
    vec4 sumColor = vec4(0.0);

    // then we begin to march
    for (int i = 0; i < 100; i++) {
        vec3 p = ro + depth * rd;

        // and we get the cloud density at said position
        float density = fbm(p);
        // if there is an unignorable amount of density (the cloud is thick enough) then
        if (density > 1e-3) {
            // we estimate the color with density (the thicker, the whiter)
            vec4 color = vec4(mix(vec3(0.0), vec3(1.0), density), density);
            // and we multiply it by a factor so it makes the clouds softer
            color.w *= 0.4;
            color.rgb *= color.w;
            // sumColor.w will rise steadily, which stands for when the ray hits thick enough cloud,
            // its color won't change anymore
            sumColor += color * (1.0 - sumColor.a);
        }
        // we march forward
        depth += max(0.05, 0.02 * depth);
    }
    return clamp(sumColor.rgb, 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // standard raymarching routine
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    float aspect = iResolution.x / iResolution.y;
    uv.x *= aspect;

    vec3 ro = vec3(0.0, 1.0, 5.0);
    vec3 center = vec3(0.0);

    vec3 front = normalize(center - ro);
    vec3 right = normalize(cross(front, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(right, front));

    mat4 lookAt = mat4(
        vec4(right, 0.0),
        vec4(up, 0.0),
        vec4(front, 0.0),
        vec4(0.0, 0.0, 0.0, 1.0)
    );
    vec3 rd = normalize(vec3(lookAt * vec4(uv, 2.0, 1.0)));

    vec3 objColor = volumetricTrace(ro, rd);

    // gamma correction (yeah, that's definitely not needed here)
    objColor = pow(objColor, vec3(0.4545));

    fragColor = vec4(objColor, 1.0);
}

#ifdef GL_ES
precision highp float;
#endif

#define PI (3.141)
#define TWOPI (2.*PI)
#define RAND_SEED (87629.3453)

#define MARCH_MAX_STEPS (512)
#define MARCH_MAX_DIST (1000.)
#define MARCH_HIT_DIST (0.001)

#define AO_ITERATIONS (5)
#define GLOBAL_AO (0.327)

#define SHADE_AO_ONLY (false)


struct Ray {
    vec3 o;			// origin
    vec3 d;			// direction
};

struct PointLight { 
    vec3 o;			// origin
    vec3 d;			// diffuse intensity
    vec3 s; 		// specular intensity
    vec3 a;			// ambient intensity
};

struct DirectionLight {
    vec3 dir;		// direction
    vec3 d;			// diffuse intensity
    vec3 s; 		// specular intensity
    vec3 a;			// ambient intensity
};

struct Material {
    vec3 d;			// diffuse
    vec3 s;			// specular
    vec3 a;			// ambient
    float shiny;	// shininess
};

struct SceneObj {
    float sdf;
	int matId;
};

struct Hit {
    vec3 p;
    vec3 n;
    SceneObj obj;
};

/********* Transformations
**********/

vec3 translate(vec3 p, vec3 t) {
    return p - t;
}

mat2 rotate2d(float theta) {
    return mat2(cos(theta), -sin(theta), sin(theta), cos(theta));
}


/********* Signed Distance Functions (SDFs)

References for SDFs:
Sphere, Cube, Torus, Capsule, Cylinder - https://youtu.be/Ff0jJyyiVyw (ArtOfCode)
Rotate, Scale, Union, Intersection, Difference - https://youtu.be/AfKGMUDWfuE (ArtOfCode)
Onioning, Displacement Mapping, Twisting - https://youtu.be/Vmb7VGBVZJA (ArtOfCode)

https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm (Inigo Quilez)
**********/

float sdfCube(vec3 p, vec3 halfSize) {
    vec3 edgeDist = abs(p) - halfSize;
    return length(max(edgeDist, 0.))
        + min(max(edgeDist.x, max(edgeDist.y, edgeDist.z)), 0.);
}

SceneObj objUnion(SceneObj s1, SceneObj s2) {
    if (s1.sdf < s2.sdf) {
        return s1;
    } else {
        return s2;
    }
}

vec3 fold(vec3 p, vec3 n) {
    n = normalize(n);
    float d = dot(p, n);
    return p - (2. * max(d, 0.) * n);
}

vec3 fold(vec3 p, float pitch, float yaw) {
	float cp = cos(pitch);
    vec3 n = vec3(cp*sin(yaw), sin(pitch), cp*cos(yaw));
    return fold(p, n);
}

// Define all objects in the scene
// Use material Ids so we can wait until later to calculate Material colors
// Materials using textures need scene normal, which we can't get until after
// scene SDF is defined
SceneObj mapScene(vec3 p) {

    vec3 sphereP = p;
	float hPi = PI / 2.;
    hPi = iTime;

	sphereP.y *= -1.;
    sphereP /= 2.;
    #define iterations (4)
    sphereP = translate(sphereP, vec3(0., -0.25, 0.));
    for (int i = 0; i < iterations; i++) {
        sphereP *= 3.;
    	sphereP = fold(sphereP, vec3(0., 1., 0.));
    	sphereP = translate(sphereP, vec3(0., -2., 0.));
    	sphereP = fold(sphereP, vec3(-1., 0., 0.));
    	sphereP = translate(sphereP, vec3(2., 0., 0.));
		sphereP = fold(sphereP, 0., 0.);
    	sphereP = translate(sphereP, vec3(0., 0., -2.));
    	sphereP = fold(sphereP, -hPi/2., 0.);
    	sphereP = fold(sphereP, vec3(1., 1., 0.));
    	sphereP = translate(sphereP, vec3(-2., 0., 0.));
    	sphereP = fold(sphereP, vec3(1.,0.,0.));
    	sphereP = translate(sphereP, vec3(-1., 0., 0.));
    	sphereP = fold(sphereP, vec3(1.,0.,0.));
		sphereP = translate(sphereP, vec3(-1.,0.,0.));
    	sphereP = fold(sphereP, vec3(1., 1.,0.));
    }


    float sphere1Sdf = sdfCube(sphereP, vec3(1.));
    sphere1Sdf /= pow(3., float(iterations));
    SceneObj sphere1 = SceneObj(sphere1Sdf, 0);

    SceneObj s = sphere1;
    return s ;
}

float sdfScene(vec3 p) {
	SceneObj objAtP = mapScene(p);
    return objAtP.sdf;
}


// Scene normals using SDF gradient
// References for normal calculation: 
// http://www.michaelwalczyk.com/blog/2017/5/25/ray-marching (Michael Walczyk)
// https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm (Inigo Quilez)
vec3 sceneNormal(vec3 p) {
    vec2 epsilon = vec2(0.01, 0.);
    return normalize(vec3(
        sdfScene(p + epsilon.xyy) - sdfScene(p - epsilon.xyy),
        sdfScene(p + epsilon.yxy) - sdfScene(p - epsilon.yxy),
        sdfScene(p + epsilon.yyx) - sdfScene(p - epsilon.yyx)
    ));
}


/********** MATERIALS **********/

Material materialLookup(Hit hit) {
    int matId = hit.obj.matId;
    if (matId == 0) {
        // Plane material
        return Material(
        	vec3(0.535,0.535,0.535),
        	vec3(0.245,0.245,0.245), 
        	vec3(0.245,0.239,0.245),
        	256.
        );
    }
}


// References for raymarching:
// http://www.michaelwalczyk.com/blog/2017/5/25/ray-marching (Michael Walczyk)
// https://youtu.be/PGtv-dBi2wE (ArtofCode)
bool rayMarch(Ray r, out Hit hit) {
    // Accumulated distance to ray origin
    float dO = 0.;
    for (int i = 0; i < MARCH_MAX_STEPS; i++) {
        vec3 p = r.o + (dO * r.d);
        // Current distance to scene
        float dS = sdfScene(p);
        dO += dS;
        if (abs(dS) < MARCH_HIT_DIST) {
            hit = Hit(
                p,
                sceneNormal(p),
                mapScene(p)
            );
            return true;
        }
        if (dO >= MARCH_MAX_DIST) {
            return false;
        }
    }
    return false;
}


/********** LIGHTING **********/

// Reference for shadows: 
// https://youtu.be/2YZClgDWCaM (3dGraphicsFromScratch)
// http://www.polygonpi.com/?p=318 (Polygon Pi)
// https://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm (Inigo Quilez)
float calcShadow(Ray shadowRay, float maxMarch, float k) {
    // Shadow increases when ray from surface to light gets closer to scene (dS gets smaller)
    // More distant objects cast darker shadows (dO gets larger)
    float shadowMultiplier = 1.;
    float dO = 0.;
    for (int i = 0; i < MARCH_MAX_STEPS; i++) {
        if (dO > maxMarch) { break; }
        vec3 p = shadowRay.o + (dO * shadowRay.d);
        float dS = sdfScene(p);
        if (abs(dS) < MARCH_HIT_DIST) {
            // Hit surface -- we are in a shadow
            return 0.;
        }
        dO += dS;
        shadowMultiplier = min(shadowMultiplier, k * dS / dO);
    }
	return clamp(shadowMultiplier, 0., 1.);
}

// Reference for ambient occlusion:
// https://youtu.be/6zYTrFRVGiU (3dGraphicsFromScratch)
float calcAO(vec3 p, vec3 normal, float epsilon) {
    float aoMultiplier = 0.;
    float weight = 0.5;
    for (int i = 1; i <= AO_ITERATIONS ; i++) {
        float t = epsilon * float(i);
        aoMultiplier += (weight * (1. - (t - sdfScene(p + t*normal))));
        weight /= 2.;
    }
    return aoMultiplier;
}


// Reference for lighting:
// Basics - http://www.michaelwalczyk.com/blog/2017/5/25/ray-marching (Michael Walczyk)
// Diffuse - https://youtu.be/9VJReTr7YXY (3dGraphicsFromScratch)
// Diffuse - shadows - https://youtu.be/PGtv-dBi2wE (ArtOfCode)
vec3 illuminateSingleLight(
    Hit hit, 
    vec3 lDir, 
    vec3 lDiffuse, 
    vec3 lSpec, 
    vec3 lAmbient, 
    vec3 eye,
    float maxMarch
) {
    Material mat = materialLookup(hit);
    // DIRECT LIGHTING
    // Diffuse illumination
    float diffuse = dot(hit.n, lDir);
    vec3 diffuseIllum = diffuse * lDiffuse * mat.d;
    // Specular illumination
    vec3 r = reflect(lDir, hit.n);
    vec3 viewDir = normalize(eye - hit.p);
    float specular = dot(r, viewDir);
    vec3 specularIllum = pow(specular, mat.shiny) * mat.s * lSpec;
    vec3 directLight = diffuseIllum + specularIllum;
    // Estimate shadow
    vec3 pNearSurface = hit.p + (hit.n * MARCH_HIT_DIST * 2.);
    float shadow = calcShadow(Ray(pNearSurface, lDir), maxMarch, 3.);
    directLight *= shadow;
    // INDIRECT LIGHTING
    vec3 ambientIllum = lAmbient * mat.a;
    float ambientOcclusion = calcAO(hit.p, hit.n, GLOBAL_AO);
    vec3 indirectLight = ambientIllum * ambientOcclusion;

    vec3 illum = directLight + indirectLight;

    if (SHADE_AO_ONLY) {
        return vec3(ambientOcclusion);
    }

    return clamp(illum, 0., 1.);
}


// DirectionalLight
vec3 illuminateSingleLight(Hit hit, DirectionLight l, vec3 eye, float maxMarch) {
    vec3 illum = illuminateSingleLight(hit, l.dir, l.d, l.s, l.a, eye, maxMarch);
    return illum;
}


// Point light
vec3 illuminateSingleLight(Hit hit, PointLight l, vec3 eye) {
    vec3 lDir = normalize(l.o - hit.p);
    // Light intensity decayed by 1/(dist^2)
    vec3 lDiff = (hit.p - l.o);
    float falloff = 1. / dot(lDiff, lDiff);
    vec3 illum = illuminateSingleLight(hit, DirectionLight(lDir, l.d*falloff, l.s*falloff, l.a), eye, distance(hit.p, l.o));

    return illum;
}


// Define all lights in the scene
vec3 illuminateScene(Hit hit, vec3 eye) {
	DirectionLight l1 = DirectionLight(
        -vec3(0.800,0.000,0.535), 
        vec3(1.),
        vec3(1.),
        vec3(0.1)
    );

    vec3 l2Pos = vec3(4., 2., 0.);
    vec3 l2Intensity = vec3(15.);
    PointLight l2 = PointLight(
        l2Pos, 			
        l2Intensity, 
        l2Intensity, 
        vec3(0.075,0.075,0.075)
    );

    DirectionLight l3 = DirectionLight(
        vec3(-0.200,0.000,0.835), 
        vec3(1.),
        vec3(1.),
        vec3(0.1)
    );

    vec3 i1 = illuminateSingleLight(hit, l1, eye, 100.);
    vec3 i2 = illuminateSingleLight(hit, l2, eye);
    vec3 i3 = illuminateSingleLight(hit, l3, eye, 100.);
    return i1 + i2 + i3;
}


/********* RENDER SCENE *********/

// References for camera:
// https://youtu.be/PBxuVlp7nuM (ArtofCode)
// https://youtu.be/2SaGgTlkhSs (3dGraphicsFromScratch)
Ray cameraViewRay(vec2 uv, vec3 eye, vec3 lookat, float fovDegrees, float roll) {
    // Axes
    vec3 f = normalize(lookat - eye);
    vec3 r = cross(vec3(0., 1., 0.), f);
    vec3 u = cross(f, r);
    // Image plane
    float zoom = 1. / tan(radians(fovDegrees) / 2.);
    vec3 c = eye + f*zoom;
    uv *= rotate2d(roll);
    vec3 i = c + (uv.x*r) + (uv.y*u);
    // Ray from camera origin to intersection with image plane
    return Ray(eye, normalize(i - eye));
}


vec3 renderScene(Ray viewRay) {
    Hit sceneHit;
    // rayMarch returns false if no scene intersection
    if(!rayMarch(viewRay, sceneHit)) return vec3(0.012,0.025,0.006);
    else return illuminateScene(sceneHit, viewRay.o);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.xy;
    // Move origin to center of viewing plane
    uv -= 0.5;
    // Normalize aspect ratio
    uv.x *= iResolution.x/iResolution.y;
    // Remap to [-1, -1] x [1, 1]
    uv *= 2.;

    vec2 mouse = iMouse.xy / iResolution.xy;
    mouse -= 0.5;

    // CAMERA SETUP
    vec2 cameraXZ = 7. * vec2(cos(mouse.x*TWOPI*2.), sin(mouse.x*TWOPI*2.));
    vec3 eye = vec3(cameraXZ[0], 6. + sin(mouse.y)*10., cameraXZ[1]);
    vec3 lookat = vec3(0., 0., 0.);
    Ray viewRay = cameraViewRay(uv, eye, lookat,  65., 0.);

    // CALCULATE PIXEL COLOR
    vec3 color = vec3(0.);
    color = renderScene(viewRay);
    // Gamma correction
    color = pow(color, vec3(0.6));

    fragColor = vec4(color,1.0);
}

#define MAX_STEPS 100
#define MAX_DIST  1000.
#define SURF_DIST .01
#define N_SPHERES 2
#define N_PLANES 1
#define N_BOXES 0
#define N_REFLECTIONS 0
#define SOURCE_TO_CAM_DISTANCE 10.
#define M_PI 3.1415926535897932384626433832795

struct Sphere  {    
    vec3  spherePosition;
    vec3  sphereColor;
    float sphereRadius;
};

struct Plane {
    vec3  planeColor;
    float planeY;
};

struct Box {
    vec3 boxPosition;
    vec3 boxSize;
    vec3 boxColor;
};

float GetSphereDistance(in Sphere spheres[N_SPHERES], in vec3 p, out int sphereID) {
    float minLen = 10000.;
    sphereID = 0;
    for (int i = 0; i < N_SPHERES; i++) {
        float sLen = length(p - spheres[i].spherePosition) - spheres[i].sphereRadius;
        minLen = min(minLen, sLen);
        sphereID = (minLen == sLen) ? i : sphereID;
    }
    return minLen;
}

float GetPlaneDistance(in Plane planes[N_PLANES], in vec3 p, out int planeID) {
    float minLen = 10000.;
    planeID = 0;
    for (int i = 0; i < N_PLANES; i++) {
        float pLen = abs(p.y - planes[i].planeY);
        minLen     = min(minLen, pLen);
        planeID    = (minLen == pLen) ? i : planeID;
    }
    return minLen;
}

float GetBoxDistance(in Box box, in vec3 p) {
    return length(max(abs(p) - box.boxSize, 0.));
}

mat2 Rot(float a) {
    float s = sin(a);
    float c = cos(a);

    return mat2(c, -s, s, c);
}

vec3 RotateAboutNormal(in vec3 rayNorm, in vec3 objNorm) {
    float angle     =  M_PI;
    rayNorm         = -rayNorm;
    vec3   paraComp = (dot(rayNorm,objNorm) / dot(objNorm,objNorm))*objNorm;
    vec3   perpComp =  rayNorm - paraComp;
    vec3   w        =  cross(objNorm, perpComp);
    float  x1       =  cos(angle) / length(perpComp);
    float  x2       =  sin(angle) / length(w);

    vec3 perpObjAng =  length(perpComp) * (x1 * perpComp + x2 * w);
    vec3 rotVec     =  perpObjAng + paraComp;
    return rotVec;
}

float GetDist(in vec3 p, out vec3 objColor) {
    int sphereID = 0;
    int planeID = 0;

    Sphere sphere[N_SPHERES] = Sphere[N_SPHERES](
        	Sphere(vec3( -.5,  1.,  0.),  vec3(0.5,0.8,0.5),  .4),
    		Sphere(vec3(  .5,  1.,  0.),  vec3(0.5,0.5,0.8),    .4) );

    Plane  plane[N_PLANES] = Plane[N_PLANES](
        	Plane(vec3(1.,1.,1.), 			 0.  ));
    //Box    box 	 = Box(   vec3(-5., 2., 5.), vec3(.5, .5, .5), vec3(1.,.8,.8) );

    float SphereDist  = GetSphereDistance(sphere, p, sphereID);
    float PlaneDist   = GetPlaneDistance(plane, p, planeID); 
    float d = min(SphereDist, PlaneDist);

    if (d == SphereDist)     { objColor = sphere[sphereID].sphereColor; }
    else if (d == PlaneDist) { objColor = plane[planeID].planeColor; }
    //else if (d == BoxDistance) { objColor = box.boxColor; }

    return d;
}

float RayMarch(in vec3 ro, in vec3 rd, out vec3 objCol) {
	float pointPos = 0.;

    for (int i = 0; i < MAX_STEPS; i++) {
    	vec3 p = ro + rd * pointPos;
        float dS = GetDist(p, objCol);
        pointPos += dS;

        if ( pointPos > MAX_DIST || dS < SURF_DIST) break;
    }

    return pointPos;
}

vec3 GetNormal(in vec3 p) {
    vec3 cOlDummy = vec3(0);
    float d = GetDist(p, cOlDummy);
    vec2  e = vec2(.01,0);

    vec3 n = d - vec3(
        GetDist(p - e.xyy, cOlDummy),
        GetDist(p - e.yxy, cOlDummy),
        GetDist(p - e.yyx, cOlDummy));
    return normalize(n);
}

float GetLight(in vec3 p) {
    vec3 cOlDummy = vec3(0.);
    vec3 lightPosition = vec3(0,5,0);
    lightPosition.xz += vec2(sin(iTime * .5) * 10.,cos(iTime * .5) * 10.);

    vec3 l = normalize(lightPosition - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p + n*SURF_DIST*1.5, l, cOlDummy);

    if (d < length(lightPosition - p)) dif *= 0.3* clamp(d, 0.,1.);
	return dif;
}

vec3 RayDirection(in vec3 pixelPos, in vec3 intermediatePlane) {    
    vec3 rDir = normalize(pixelPos - intermediatePlane);
    return rDir;
}

float RayReflection(in vec3 p, in vec3 rayNorm, out vec3 refCol, in int N){
    float d = 0.;
    for (int i = 0; i < N; i++) {
        vec3 objNormal = GetNormal(p);
        vec3 refNormal = RotateAboutNormal(rayNorm, objNormal);
       	vec3 coL = vec3(0.);    

        d = RayMarch(p + objNormal*SURF_DIST*1.6, refNormal, coL);
        refCol += (coL * 1./ float(N))*.9;
        p += refNormal*d;
    }

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec3 colorOfPixel = vec3(0.);

    vec3 cameraPosition = vec3(0.,5.,-20.);
    vec3 viewNorm = normalize(vec3(0.,1.,0.) - cameraPosition);
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    vec3 camRightNorm = normalize(cross(vec3(0.,-1.,0.), viewNorm));
    vec3 camUpNorm = normalize(cross(camRightNorm, viewNorm));

    vec3 ro = (-camRightNorm*uv.x)+(camUpNorm*uv.y) + cameraPosition;
    vec3 ri = (-camRightNorm*uv.x * 0.5) + (camUpNorm*uv.y * 0.5) + cameraPosition + viewNorm * (-SOURCE_TO_CAM_DISTANCE*.5);
    vec3 rd = RayDirection(ro, ri);

    float d = RayMarch(ro, rd, colorOfPixel);
    vec3 p = ro + rd*d;
    vec3 dumCol = colorOfPixel;
    if (N_REFLECTIONS != 0) {
    	float dRef = RayReflection(p, rd, dumCol, N_REFLECTIONS);
    	colorOfPixel = (colorOfPixel + dumCol) * (1./float(N_REFLECTIONS)) ;
    }

	float dif = GetLight(p);

    fragColor = vec4(colorOfPixel,1.0) * dif;
}

#define BACKFACE_CULL true
#define TEXTURED true


struct Vertex
{
    vec4 Pos;
    vec2 Uv;
};


// Test a position against a triangle and return
// the non-perspective-correct barycentric coordinates in the triangle
// Vertex z and w values are ignored.
vec2 BaryTri2D ( vec2 pos, Vertex v1, Vertex v2, Vertex v3 )
{
    vec2 posv1 = pos - vec2(v1.Pos);

    vec2 v21 = vec2(v2.Pos) - vec2(v1.Pos);
    vec2 v31 = vec2(v3.Pos) - vec2(v1.Pos);

    float scale = v21.x * v31.y - v21.y * v31.x;
    if ( BACKFACE_CULL && ( scale < 0.0 ) )
    {
        return vec2 ( -1.0, -1.0 );
    }

    float rscale = 1.0 / scale;
    float baryi = ( posv1.x * v31.y - posv1.y * v31.x ) * rscale;   
    float baryj = ( posv1.x * v21.y - posv1.y * v21.x ) * -rscale;

    return vec2 ( baryi, baryj );
}


// Test a position against a triangle and return
// the perspective-correct barycentric coordinates in the triangle
// Note the z value in the vertex is ignored, it's the w that matters.
vec2 BaryTri3D ( vec2 pos, Vertex v1, Vertex v2, Vertex v3 )
{
    vec2 posv1 = pos - vec2(v1.Pos);

    vec2 v21 = vec2(v2.Pos) - vec2(v1.Pos);
    vec2 v31 = vec2(v3.Pos) - vec2(v1.Pos);

    float scale = v21.x * v31.y - v21.y * v31.x;
    if ( BACKFACE_CULL && ( scale < 0.0 ) )
    {
        return vec2 ( -1.0, -1.0 );
    }

    float rscale = 1.0 / scale;
    float baryi = ( posv1.x * v31.y - posv1.y * v31.x ) * rscale;
    float baryj = ( posv1.x * v21.y - posv1.y * v21.x ) * -rscale;

    // Now interpolate the canonical coordinates (0,0,1,v1.w), (1,0,1,v2.w) and (0,1,1,v3.w)
    // with perspective correction
    // So we project all three by their respective w:
    // (0,0,v1.w) -> (0,     0,     1/v1.w)
    // (1,0,v2.w) -> (1/v2.w,0,     1/v2.w)
    // (0,1,v3.w) -> (0,     1/v3.w,1/v3.w)
    // Then interpolate those values linearly to produce (nx,ny,nw),
    // then divide by nw again.
    vec3 recipw = vec3 ( 1.0/v1.Pos.w, 1.0/v2.Pos.w, 1.0/v3.Pos.w );

    float baryk = 1.0 - baryi - baryj;
    float newi = recipw.y * baryi;
    float newj = recipw.z * baryj;
    //float neww = recipw.x * baryk + recipw.y * baryi + recipw.z * baryj;
    float neww = recipw.x * baryk + newi + newj;

    // ...and project back.
    float rneww = 1.0/neww;
    float perspi = newi * rneww;
    float perspj = newj * rneww;

    return vec2 ( perspi, perspj );
}

const int GridW = 5;
const int GridH = 5;
const int NumVerts = GridH*GridW*2;
const int NumTris = (GridH-1)*(GridW-1)*2*2;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // NDC (-1 to +1)
    vec2 uv = -1.0 + 2.0f * (fragCoord/iResolution.xy);

    float wobble = float(iTime);

    // Create the mesh. This would of course be done offline.
    Vertex Verts[NumVerts];
    for ( int w = 0; w < GridW; w++ )
    {
        for ( int h = 0; h < GridH; h++ )
        {
            Vertex Vert0, Vert1;
            Vert0.Pos.x = float(w - GridW/2);
            Vert0.Pos.y = float(h - GridH/2);
            float wf = -2.0 + float(w);
            float hf = -3.0 + float(h);
            float d = sqrt(wf*wf + hf*hf);
            Vert0.Pos.z = 0.3 * cos (d * 2.0 + wobble);
            Vert0.Pos.w = 1.0f;
            Vert0.Uv.x = float(w) / float(GridW-1);
            Vert0.Uv.y = float(h) / float(GridH-1);

            Vert1.Pos = Vert0.Pos;
            Vert1.Pos.z += 1.0f;
            Vert1.Uv = Vert0.Uv;

            int Index = (w*GridH+h)*2;
            Verts[Index+0] = Vert0;
            Verts[Index+1] = Vert1;
        }
    }

    int Indices0[NumTris];
    int Indices1[NumTris];
    int Indices2[NumTris];
    for ( int w = 0; w < GridW-1; w++ )
    {
        for ( int h = 0; h < GridH-1; h++ )
        {
            int VertIndex = (w*GridH+h)*2;
            int TriIndex = (w + (GridH-1)*h) * 2 * 2;
            Indices0[TriIndex+0] = VertIndex  ;
            Indices1[TriIndex+0] = VertIndex  +GridH*2;
			Indices2[TriIndex+0] = VertIndex  +2;
            Indices0[TriIndex+1] = VertIndex+1;
            Indices2[TriIndex+1] = VertIndex+1+GridH*2;
			Indices1[TriIndex+1] = VertIndex+1+2;

            Indices2[TriIndex+2] = VertIndex  +GridH*2;
            Indices0[TriIndex+2] = VertIndex  +GridH*2+2;
			Indices1[TriIndex+2] = VertIndex  +2;
            Indices1[TriIndex+3] = VertIndex+1+GridH*2;
            Indices0[TriIndex+3] = VertIndex+1+GridH*2+2;
			Indices2[TriIndex+3] = VertIndex+1+2;
        }
    }

    float hfov = 0.6;
    float vfov = hfov * iResolution.y / iResolution.x;
    float zfar = 10.0f;
    float znear = 1.0f;
    float q = zfar/(zfar-znear);

    mat4 ProjMat;
    ProjMat[0] = vec4 ( 1.0/hfov, 0.0f, 0.0f, 0.5f );
    ProjMat[1] = vec4 ( 0.0f, 1.0/vfov, 0.0f, 0.5f );
    ProjMat[2] = vec4 ( 0.0f, 0.0f, q, 1.0f );
    ProjMat[3] = vec4 ( 0.0f, 0.0f, -q*znear, 0.0f );


    mat4 TotalMat;
    mat4 ObjMat1;
    mat4 ObjMat2;

    float a1 = iMouse.x * 0.01;
    float a2 = iMouse.y * 0.01;
    float zdist = 8.0;
    ObjMat1[0] = vec4 ( cos(a1),  sin(a1), 0.0, 0.0 );
    ObjMat1[1] = vec4 ( sin(a1), -cos(a1), 0.0, 0.0 );
    ObjMat1[2] = vec4 ( 0.0,          0.0, 1.0, 0.0 );
    ObjMat1[3] = vec4 ( 0.0,          0.0, 0.0, 1.0 );
    ObjMat2[0] = vec4 ( 1.0, 0.0,          0.0, 0.0 );
    ObjMat2[1] = vec4 ( 0.0, cos(a2),  sin(a2), 0.0 );
    ObjMat2[2] = vec4 ( 0.0, sin(a2), -cos(a2), zdist );
    ObjMat2[3] = vec4 ( 0.0, 0.0,          0.0, 1.0 );

    TotalMat = ObjMat1 * ObjMat2;
    TotalMat = TotalMat * ProjMat;

    // Background colour
    vec3 col;
    col.xy = uv.xy;
    col.z = 0.0;

    Vertex ScreenVert[NumVerts];
    for ( int VertNum = 0; VertNum < NumVerts; VertNum++ )
    {
        vec4 Pos = Verts[VertNum].Pos;

        vec4 ScrPos = Pos * TotalMat;
        float rw = 1.0/ScrPos.w;
        ScrPos.x *= rw;
        ScrPos.y *= rw;
        ScrPos.z *= rw;

        ScreenVert[VertNum].Pos = ScrPos;
        ScreenVert[VertNum].Uv = Verts[VertNum].Uv;
    }

    float NearestZ = 10000.0f;
    for ( int TriNum = 0; TriNum < NumTris; TriNum++ )
    {
        Vertex v1 = ScreenVert[Indices0[TriNum]];
        Vertex v2 = ScreenVert[Indices1[TriNum]];
        Vertex v3 = ScreenVert[Indices2[TriNum]];
        vec3 bary;
        bary.xy = BaryTri3D ( uv, v1, v2, v3 );
        bary.z = 1.0 - bary.x - bary.y;

        // You need to do the UV coord generation outside all the conditionals,
        // otherwise you don't get good gradients at the edges.
        // HOWEVER - what actually happens is the compiler spots you only *use* the UV
        // values inside the conditionals and drags the calculation inside,
        // which then breaks the gradients! So to stop this, I added a totally
        // spurious test of the UV coords to the barycentric test.

        if ((bary.z * v1.Uv  + bary.x * v2.Uv + bary.y * v3.Uv).x + 
             (bary.z * v1.Uv  + bary.x * v2.Uv + bary.y * v3.Uv).y < -10.0
            // && false
           ) {
            col.xy = bary.xy;
            col.z = sqrt(-1.0);
        }


        if ( ( bary.x >= 0.0 ) &&
             ( bary.y >= 0.0 ) &&
             ( bary.z >= 0.0 ) )
        {
            vec2 uv = bary.z * v1.Uv + bary.x * v2.Uv + bary.y * v3.Uv;
            // Interpolate Z
            // Note this is linear Z, not the strange Z that most rasteriser use
            // In this case, that's fine.
            float Z = bary.z * v1.Pos.z + bary.x * v2.Pos.z + bary.y * v3.Pos.z;
            if ( NearestZ > Z )
            {
                NearestZ = Z;

                if ( TEXTURED )
                {
                    vec4 tex = texture ( iChannel0, uv );
                    col.xyz = tex.xyz;
                }
                else
                {
                    col.xy = bary.xy;
                    col.z = 1.0;
                    if ( fract ( bary.x * 4.0 ) < 0.05 ) { col.z = 0.0; }
                    if ( fract ( bary.y * 4.0 ) < 0.05 ) { col.z = 0.0; }
                }
            }
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define time iTime
#define mod01 floor(mod(time, 4.0))
#define mod02 floor(mod(time * 2.0, 2.0))
#define mod03 floor(mod(time  * 8.0, 16.0))


float gg = 0.0;

struct matter
{
  float mat;
  int type;
};
mat2 rot(float a)
{
  float ca = cos(a);
  float sa = sin(a);

  return mat2(ca, -sa, sa, ca);
}

float sphere(vec3 p, float r)
{
  return length(p) - r;
}

float box(vec3 p, vec3 r)
{
  p = abs(p) - r;
  return max(p.x, max(p.y, p.z));
}

float Infbox(vec3 p, vec2 r)
{
  p.xz = abs(p.xy) - r;
  return max(p.x, p.y);
}

float terrain(vec3 p)
{
    return p.y + 0.8;
}

float smin(float a, float b, float k)
{
    float h = clamp(0.5 - 0.5 * ( a - b )/k, 0.0, 1.0);
    return mix(b , a, h) - k*h*(1.0*h);
}

vec3 opRepLimit(vec3 p, float c, vec3 l)
{
 	return p - c * clamp(round(p/c), -l, l);   
}

vec2 opRepID(vec2 p, float repeat)
{
  return (floor(abs(p) / repeat - 0.5) - 0.5) * repeat;
}

vec2 opRep(vec2 p, float repeat)
{
  return (fract(abs(p) / repeat - 0.5) - 0.5) * repeat; 
}

float opRep(float p, float repeat)
{
  return (fract(abs(p) / repeat - 0.5) - 0.5) * repeat;
}

matter map(vec3 p)
{
  matter m;

  float mat01, mat02;
  vec3 p02 = p;
  vec3 p03 = p;
  vec3 p04 = p;

  p02.z = opRep(p.z, 1.0);
  vec2 gID = opRepID(p.xz, 2.0);

  vec3 scale = vec3(0.5, 0.05 + mod01 * 0.05 + mod02 * 0.1 + mod03 * 0.1, 100000000000.0);

  p.xy *= rot(sin(p.z * 0.1 + time) * ((mod01 * 0.5) + 2.0));  
  p02.xy *= rot(sin(p.z * 0.02 + time) * ((mod01 * 0.1) + 2.0));  
  p02.xz *= rot(sin(p.z * 0.01 + time) * 0.5);  

  p03.xy *= rot(sin(p.z * 0.02 + time) * ((mod01 * 0.1) + 5.0));  
  //p02.xz *= rot(sin(p.z * 0.01 + time) * 0.5);  
  p04.xy *= rot(sin(p.z * 0.01 + time) * ((mod03 * 0.5) + .0));  
  mat01 = box(p + vec3(1.0, 4.0, 0.0), scale * 0.5);

  //mat01 = Infbox(p, vec2(1.5));
  mat02 = box(p02 + vec3(0.2, 6.0, 0.0), scale * 0.5);
  mat02 = min(mat02, box(p02 + vec3(10, 6.0, 0.0), scale * 0.2));
  mat02 = min(mat02, box(p02 + vec3(2.0, 1.0, 0.0), scale * 0.2));
  mat02 = min(mat02, box(p02 + vec3(2.0, 1.0, 0.0), scale * 0.2));
  mat02 = min(mat02, box(p03 + vec3(10.0, 5.0, 0.0), scale * 0.2));

  gg += 0.1/(0.9+mat01*mat01);

  m.mat = min(mat01, mat02);
  m.mat = min(m.mat, -box(p04, vec3(7.0 + abs(sin(time * 0.1)) * 5.0, 7.0 + abs(sin(time * 0.1)) * 5.0, 100000000000.0)));

  if(m.mat < 0.001)
  {
    m.type = 1;
  }

  return m;
}

vec3 normals(vec3 p)
{
  vec2 uv = vec2(0.01, 0.0);

  return vec3(map(p).mat - vec3(map(p - uv.xyy).mat, map(p - uv.yxy).mat, map(p - uv.yyx).mat));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 vec3 l = normalize(vec3(0.0, 10.0, -5.0));

  vec2 uv = vec2(fragCoord.x / iResolution.x, fragCoord.y / iResolution.y);
  uv -= 0.5;
  uv /= vec2(iResolution.y / iResolution.x, 1);

  vec3 o = vec3(0.0, -1.0, 1.0 + (time * 0.3)), t = vec3(0.0, -1.0, (time * 0.3));
  vec3 fr = normalize( t - o );
  vec3 ri = normalize( cross(vec3(0.0, 1.0, 0.0), fr) );
  vec3 up = normalize( cross(fr, ri) );
  vec3 dir = normalize( fr + uv.x * ri + uv.y * up );
  vec3 p = o + dir * 0.5;

  vec3 col = vec3(0.0);
  matter m;
  float shad = 0.0;
  float d = 0.0;

  for(int i = 0; i < 100; ++i)
  {
    /*
    p.xy *= rot(sin(time * 0.0001) * 0.1);
    p.xz *= rot(sin(time * 0.0001) * 0.05);
    p.yz *= rot(sin(time * 0.0001) * 0.01);
    */

    m = map(p);

    if(m.mat < 0.001)
    {
      if(m.type == 1)
      {
        m.mat = 0.5;

        vec3 n = normals(p);
        dir = reflect(dir, n);
      } else {

         break;
      }

      shad = clamp(1.0 - pow((float(i)/500.0), 2.0),0.0,1.0);
    }

    p += dir * m.mat;
    d += m.mat;

    col += mix(vec3(1.0, 0.5, 1.0), vec3( 1.0, 0.5, 0.0), sin(time + (p.z * 0.01))) * gg * 0.004;
  }

  vec3 n = normals(p);

  float fog = clamp(1.0 - (d/100.0), 0.0, 1.0);
  vec3 sky = mix(vec3(0.5, 0.3, 0.3), vec3(0.8, 0.4, 0.1), uv.y - 0.5); 

  col += shad * vec3(1.0)  * clamp(dot(n, l), 0.0, 1.0);
  col += sky * 1.0;

  fragColor = vec4(col, 1.0);
}

